  [1288] = {
    ["D"] = "Ihr habt gute Arbeit geleistet, $N, die Beste, die ich je von einem Amateur sah.$B$BLady Prachtmeer bat mich, sie über diese delikate Situation auf dem Laufenden zu halten, aber ich habe eine Weile nicht mit ihr gesprochen.$B$BIch wollte ihr meinen Bericht später bringen, aber es scheint mir passender, wenn Ihr mit ihr darüber sprecht. Ihr könnt ihre Fragen wahrscheinlich besser beantworten als ich.",
    ["O"] = "Bringt Mumms Bericht zu Lady Jaina Prachtmeer in Theramore.",
    ["T"] = "Mumms Bericht",
  },
  [1289] = {
    ["D"] = "Well. judging from Captain Vimes\'s report. you\'ve been a huge help to his investigation. and for that I thank you.$B$BI can put my mind more at ease knowing that the matter is in such capable hands. If you would. I would request that you return to Captain Vimes and help him in getting to the bottom of this mystery.",
    ["O"] = "Kehrt zu Hauptmann Garran Mumm in Theramore zurück.",
    ["T"] = "&lt;nyi&gt; Mumms Bericht",
  },
