  [5762] = {
    ["D"] = "Ich habe hier eine Sendung für einen alten Kunden von mir, Hemet Nesingwary Jr., ein Zwerg. Die Sendung war wochenlang unterwegs und Hemet ist schon längst wieder weg. Er sagte, dass er ins Schlingendorntal gehen wolle, um die Bestien dort zu jagen, aber er ließ mir etwas Geld da und wollte, dass ich ihm seine Sendung schicke, sobald es mir möglich sei. $B$BJunior ist ein reicher Zwerg und man sollte sich mit den Reichen ruhig gut stellen, findet Ihr nicht auch? Also... könntet Ihr die Sendung für mich zustellen?$B$BWie ich hörte, hat er im Schlingendorntal nördlich von Grom\'gol ein Lager aufgeschlagen.",
    ["O"] = "Take Kravel\'s Crate to Hemet Nesingwary Jr. in Stranglethorn.",
  },
  [6131] = {
    ["D"] = "Die Furbolgs der Holzschlundfeste sind die einzigen Furbolgs, die von der Verderbnis verschont blieben. Aber viele andere Völker töten die Furbolgs jetzt blindwütig und scheren sich nicht darum, ob sie Freund oder Feind sind. Deswegen trauen die Einwohner der Holzschlundfeste auch kaum jemandem.$B$BSeid Ihr daran interessiert, Euch zu beweisen? Treibt den verderbten Stamm der Totenwaldfelle zurück, dann betrachten wir Euch eines Tages vielleicht als Verbündeten. Ihr findet die Krieger, Pfadfinder und Gärtner der Totenwaldfelle im Süden.",
    ["O"] = "Grazle möchte, dass Ihr Euch beweist, indem Ihr 5 Krieger der Totenwaldfelle, 5 Pfadfinder der Totenwaldfelle und 5 Gärtner der Totenwaldfelle besiegt.",
    ["T"] = "Verbündeter der Holzschlundfeste",
  },
  [6221] = {
    ["D"] = "Direkt südwestlich von hier liegt ein Furbolglager der Totenwaldfelle. Ich bin nicht sicher, was sie vorhaben, aber ich würde dem gern einen Riegel vorschieben, ehe es zum Problem wird.$B$BDer Stamm der Totenwaldfelle ist böse, aber die Verderbnis ist es, die ihre Hirne verseucht. Ihre Feindseligkeit ist keine angeborene Eigenschaft, doch da kann man nichts machen. Aus diesem Grund stimmt es mich zutiefst traurig, dass ich gegen meine Geschwister kämpfen muss.$B$BBitte, $N, tut, was getan werden muss, aber erspart mir die Einzelheiten. Sie beunruhigen mich zu sehr.",
    ["O"] = "Nafien möchte, dass Ihr 5 Höhlenbehüter der Totenwaldfelle, 5 Rächer der Totenwaldfelle und 5 Schamanen der Totenwaldfelle besiegt.",
    ["T"] = "Totenwaldfelle des Nordens",
  },
  [6241] = {
    ["D"] = "Die Furbolgs der Holzschlundfeste bleiben meist unter sich, $N. Wir gewähren allen, die ihre Vertrauenswürdigkeit unter Beweis gestellt haben, freies Geleit, meiden aber wenn möglich andere Furbolgstämme.$B$BIn letzter Zeit ist mir aufgefallen, dass der Winterfellstamm uns gegenüber immer größere Feindseligkeit zeigt. Sie scheinen in Rage zu sein, und ganz von ihrem Hass auf alles beseelt, das sie nicht verstehen.$B$BWenn Ihr ihre Anzahl reduzieren könntet, würde uns das sehr helfen, $N.",
    ["O"] = "Salfa möchte, dass Ihr 6 Totemiker der Winterfelle, 6 Höhlenbehüter der Winterfelle und 6 Pfadfinder der Winterfelle erledigt.",
    ["T"] = "Aktivität der Winterfelle",
  },
  [6843] = {
