  [10074] = {
    ["D"] = "Dieser Stützpunkt war die erste Forschungseinrichtung für die Untersuchung von Naarukristallen.$B$BWir haben hier eine wahre Goldgrube an Forschungsunterlagen über die Kristalle des \"diamantenen\" Bergs, Oshu\'gun, gefunden. Wie es scheint, ist überall in diesem Gebiet etwas von der übrig gebliebenen Macht der Kristalle von Oshu\'gun enthalten. Wenn Ihr während Euren Abenteuern in diesem Gebiet Kristallpulver finden solltet, bringt es zu mir, und ich werde Euch ein Zeichen meiner Wertschätzung geben, dass Ihr bei den Rüstmeistern eintauschen könnt.",
    ["O"] = "Chief Researcher Amereldine at Halaa wants 20 Oshu\'gun Crystal Powder Samples.",
    ["T"] = "Kristallpulver von Oshu\'gun",
  },
  [10075] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Kristallpulver von Oshu\'gun",
  },
  [10076] = {
    ["D"] = "Dieser Stützpunkt war die erste Forschungseinrichtung für die Untersuchung von Naarukristallen.$B$BWir haben hier eine wahre Goldgrube an Forschungsunterlagen über die Kristalle des \"diamantenen\" Bergs, Oshu\'gun, gefunden. Wie es scheint, ist überall in diesem Gebiet etwas von der übrig gebliebenen Macht der Kristalle von Oshu\'gun enthalten. Wenn Ihr während Euren Abenteuern in diesem Gebiet Kristallpulver finden solltet, bringt es zu mir, und ich werde Euch ein Zeichen meiner Wertschätzung geben, dass Ihr bei den Rüstmeistern eintauschen könnt.",
    ["O"] = "Chief Researcher Kartos at Halaa wants 20 Oshu\'gun Crystal Powder Samples.",
    ["T"] = "Kristallpulver von Oshu\'gun",
  },
  [10077] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Kristallpulver von Oshu\'gun",
  },
  [10078] = {
    ["D"] = "Jetzt, da unsere Bergungsaktion ins Rollen gekommen ist, ist es an der Zeit, die zweite Phase unseres Plans einzuläuten.$B$BAm Pfad des Ruhms hat die Horde immer noch ihre alten Klingenwerfer postiert. Lasst Euch von Ihrem Alter nicht täuschen, $N, sie sind immer noch sehr gefährlich. So sehr wir auch versuchen, alles zurückzuerobern, was wir können, können wir nicht erwarten, dass die Horde ihre Waffen einfach so abbaut.$B$BNehmt diese Fackel und zündet ihre übriggebliebene Belagerungsausrüstung an. Ich werde die Sicherheit der Ehrenfeste nicht dem Zufall überlassen.",
    ["O"] = "Zündet die Klingenwerfer der Horde mit der flammenden Fackel am Pfad des Ruhms an. Kehrt dann mit der flammenden Fackel zu Dumphry in der Ehrenfeste zurück.",
    ["T"] = "Die Unerwünschten zerstören",
  },
  [10079] = {
    ["D"] = "Die Mine wurde von diesem Gan\'argpack überrannt!$B$BSie haben nicht nur meine Arbeiter aus den Tunneln getrieben, sondern hämmern auch noch Tag und Nacht darin herum! Und jetzt sitzt mir auch noch Danath im Rücken! Sagt, er würde mir meine Bierration streichen, wenn ich mich nicht bald darum kümmere!$B$BKeiner der Minenarbeiter will dort hineingehen, und die Wächter sind zu sehr damit beschäftigt, sich vollaufen zu lassen.$B$BIch flehe Euch an, $N, helft mir! Danath mag mir meinen Stolz nehmen, aber niemals mein Bier!",
    ["O"] = "Tötet 12 Pioniere der Gan\'arg und kehrt dann zu Großknecht Biggums zurück.",
    ["T"] = "Bringt diese Mine zum Beben!",
  },
  [10081] = {
    ["D"] = "Erschreckt Euch nicht über das, was Ihr sehen werdet, wenn die Wirkung eintritt. Diese Geister sind überall um uns herum - immer. Der Trank erlaubt Euch nur, sie zu verstehen und zu sehen. Ihr werdet seine Wirkung brauchen, wenn Ihr mit Mutter Kashur sprecht.$B$BIhr findet sie ein gutes Stück im Südwesten beim Ahnengrund.",
    ["O"] = "Großmutter Geyah möchte, dass Ihr mit Mutter Kashur sprecht.",
    ["T"] = "Mutter Kashur treffen",
  },
  [10082] = {
    ["D"] = "Seht Ihr, wie sie unter uns wandeln? Der Ahnengrund ist normalerweise sehr ruhig und friedlich.$B$BIn letzter Zeit haben sich die Geister der Ahnen jedoch erhoben und sind sehr aufgebracht. Etwas muss sie sehr stören. Wir müssen herausfinden, was das sein könnte, aber zuerst müssen wir die direkte Bedrohung beseitigen.$B$BDie Geister, die Ihr hier seht, sind nicht wirklich unsere Ahnen, sondern ein Abbild ihrer gegenwärtigen Lage. Ihr müsst die aufgebrachten Orcgeister zerstören. Tut dies und kehrt dann zu mir zurück.",
    ["O"] = "Mother Kashur has asked that you put to rest 15 Agitated Orc Spirits.",
    ["T"] = "Die aufgebrachten Ahnen",
  },
  [10084] = {
    ["D"] = "Mit den Fähigkeiten der Ingenieure der Mo\'arg und der endlosen Menge an Gan\'argdienern werden sie sicher schneller Teufelshäscher produzieren, als wir in Nethergarde Bescheid geben können.$B$BWir haben keine andere Wahl als sie so schnell wie möglich anzugreifen. Wir können keine Männer von der Ehrenfeste für diesen Angriff abziehen, das wäre purer Selbstmord! Die Höllenorcs und die Horde würden uns bei dem kleinsten Anzeichen von Schwäche überrennen.$B$BDie Ehrenfeste steht nun seit 20 Jahren, und wir haben nicht vor, sie jetzt zu verlieren.",
    ["O"] = "Tötet Nachtlord Malphas im Konstruktionslager: Mageddon und meldet Euch dann bei Truppenkommandant Danath Trollbann in der Ehrenfeste.",
    ["T"] = "Angriff auf Mageddon",
  },
  [10085] = {
    ["D"] = "Ich brauche Euch, um zu den Dörfern unserer Ahnen zu reisen, $N. Geht zu jedem einzelnen und schaut, ob unsere Vorfahren noch da sind.$B$BGeht zum Sonnenwindposten im Norden, zu den Ruinen des Lachenden Schädels ganz im Norden bei den Bergen, besucht Garadar im Nordosten und zuletzt die Ruinen des Blutenden Auges an der Grenze zwischen Nagrand und den Wäldern von Terokkar im Osten.$B$BBerichtet mir, sobald Ihr die Ahnen in ihrem Zuhause beobachtet habt.",
    ["O"] = "Mutter Kashur im Ahnengrund in Nagrand möchte, dass Ihr den Sonnenwindposten, die Ruinen des Lachenden Schädels, Garadar und die Ruinen des Blutenden Auges besucht.",
    ["T"] = "Ein Besuch bei den Ahnen",
  },
  [10086] = {
    ["D"] = "Könnt Ihr das fassen? Diese Orcs haben mich angeheuert, um ihre beschädigten Befestigungsanlagen abzustützen und Sprengstoff herzustellen. Ist das nicht ein Traumjob? Nur zu schade, dass uns die Materialien ausgehen, aber ein gewitzter Goblin wie ich weiß sich immer zu helfen!$B$BKönntet Ihr mich dabei unterstützen? Ich benötige ein wenig gebrauchtes Holz und Metall von den zerstörten Belagerungsmaschinen entlang des Pfads des Ruhmes. Geht dorthin und sammelt, aber gebt auf diese rothäutigen Orcs Acht!$B$BWenn Ihr fertig seid, kommt zurück und holt Euch eine fette Belohnung ab!",
    ["O"] = "Bringt 8 Stück gebrauchtes Metall und 8 Stück gebrauchtes Holz zu Megzeg Knaxknöchel in Thrallmar.",
    ["T"] = "Ich arbeite... für die Horde!",
  },
  [10087] = {
    ["D"] = "Ich bin ein glücklicher, glücklicher Goblin! Ich darf hier Sprengstoff und Killermaschinen herstellen in der Gewissheit, dass sie sofort bei wichtigen Missionen eingesetzt werden!$B$BWo wir gerade von Missionen sprechen, ich habe einen Befehl aus Thrallmar erhalten: Verbrennt die Kanonen der Allianz entlang des Pfads des Ruhms... die Kanonen, die gefährlich auf die Befestigungsanlagen der Horde gerichtet sind. Nehmt diese Fackel und begebt Euch zu jeder Kanone. Zündet die Fackel an und bestaunt den Funkenflug!",
    ["O"] = "Zündet die östliche und die westliche Kanone der Allianz mit der flammenden Fackel an und kehrt danach zu Megzeg Knaxknöchel in Thrallmar zurück.",
    ["T"] = "Zündet es an... für die Horde!",
  },
  [10088] = {
    ["D"] = "Die Mine wurde von diesem Gan\'argpack überrannt, und zu allem Übel sind die Peons der Meinung, dass jetzt die richtige Zeit für ein Schläfchen ist! Ich weiß nicht, wie sie bei all dem Lärm aus der Mine schlafen können, aber das ist mein geringstes Problem.$B$BWenn wir dieses Gan\'arggesindel nicht bald loswerden, wird Thrallmars Nachschub an Erzen und anderen Materialien abgeschnitten. Ich habe schon Wachkommandant Krunk um Hilfe gebeten, aber er sagt, dass er keine Männer schicken kann, solange die Stadt angegriffen wird. Könnt Ihr mir helfen?",
    ["O"] = "Tötet 12 Pioniere der Gan\'arg und kehrt dann zu Großknecht Razelcraz zurück.",
    ["T"] = "Bringt diese Mine zum Beben!",
  },
  [10089] = {
    ["D"] = "Thrallmar steht seit seiner Gründung unter Beschuss der Lager der Legion. Bis jetzt haben wir standhalten können, aber die Situation wird von Tag zu Tag schlimmer.$B$BSchattenjäger Ty\'jin hat mehrere Versuche unternommen, mehr über die Lager herauszufinden, aber keiner seiner Späher ist je zurückgekehrt.$B$BDie Zeit für Zurückhaltung ist vorbei, $N. Begebt Euch ins Konstruktionslager: Zorn und findet heraus, was die Legion vorhat.",
    ["O"] = "Tötet 10 Diener der Gan\'arg, 5 Legionäre des Konstruktionslagers und 5 Schwestern des Leids. Durchsucht danach des Konstruktionslager: Zorn nach einem Hinweis auf die Absichten der Legion.",
    ["T"] = "Konstruktionslager der Legion",
  },
  [10090] = {
    ["D"] = "Ihr schreibt die leuchtenden Glyphen, die in den Boden geritzt wurden, ab, aber ohne die zweite Hälfte ergibt das Diagramm keinen Sinn.$B$BDie Glyphen verweisen darauf, dass ein weiteres Konstruktionslager im Westen, das Konstruktionslager: Bosheit, an dem Projekt beteiligt ist. Vielleicht bewahrt die Legion dort die zweite Hälfte der Pläne auf?$B$BWenn die Legion etwas wirklich Gewaltiges herstellt, macht es Sinn, dass die Teile in verschiedenen Lagern zusammengebaut werden.",
    ["O"] = "Besorgt die Pläne des Konstruktionslagers: Bosheit und bringt sie zu Oberanführer Hun Stümmelfaust im Häschersturz.",
    ["T"] = "Die Pläne der Legion",
  },
  [10091] = {
    ["D"] = "Pah! Sie haben mich gefoltert und versucht, etwas über die Verteidigungsanlagen von Shattrath aus mir herauszuquetschen.$B$BIch habe mich geweigert - da haben sie die anderen Spione an Höllenschlund verfüttert, und ich sollte der Nächste sein. Aber ich habe ihnen nichts verraten!$B$BSie sind wahnsinnig! Sie sind versessen darauf, dämonische Mächte zu beschwören, die sie auf gar keinen Fall kontrollieren können!$B$BSie benutzen besondere Seeleninstrumente, um ihre Seelensplitter herzustellen und ihre Beschwörungen noch mächtiger zu machen. Ihr müsst ihnen diese Instrumente stehlen!$B$BIch würde Euch ja begleiten, aber ich muss nach Shattrath zurück um der Spionagemeisterin des Unteren Viertels zu berichten.",
    ["O"] = "Stehlt 5 Seeleninstrumente und bringt sie zu Spionagemeisterin Mehlisah Hochkrone auf der Terrasse des Lichts in Shattrath.",
    ["T"] = "Seeleninstrumente",
  },
  [10092] = {
    ["D"] = "Mit den Fähigkeiten der Ingenieure der Mo\'arg und der endlosen Menge an Gan\'argdienern werden sie sicher schneller Teufelshäscher produzieren, als wir in Orgrimmar Bescheid geben können.$B$BWir haben keine andere Wahl als sie so schnell wie möglich anzugreifen. Wir können keine Männer von Thrallmar für diesen Angriff abziehen, dann wären wir zu leicht angreifbar.$B$BEs gibt zu viele, die sich, ungeachtet der Allianz, nur zu gerne auf unseren ungeschützten Außenposten stürzen würden. Versagt nicht, $N. Es hängt zu viel von Euch ab.",
    ["O"] = "Tötet Nachtlord Malphas im Konstruktionslager: Mageddon und meldet Euch dann bei Oberanführer Hun Stümmelfaust im Häschersturz.",
    ["T"] = "Angriff auf Mageddon",
  },
  [10093] = {
    ["D"] = "Zum Glück ist der Pfad des Ruhms nicht das einzige Zeichen meines Volks in diesen Ländereien. Im Westen befinden sich immer noch ein paar unserer geheiligten Stätten, auch wenn einige von ihnen überrannt wurden.$B$BWährend Velen sich auf seine Reise nach Azeroth vorbereitete, haben diejenigen von uns, die hier bleiben sollten, versucht, sich wieder in unseren heiligen Orten niederzulassen.$B$BIm westlichen Teil der Höllenfeuerhalbinsel könnt Ihr beim Tempel von Telhamat eine kleine Gruppe von Draenei finden, die von Amaan dem Weisen angeführt werden. Ich bin mir sicher, dass er Euch gerne kennenlernen würde.",
    ["O"] = "Sprecht mit Amaan dem Weisen beim Tempel von Telhamat im Westen der Höllenfeuerhalbinsel.",
    ["T"] = "Der Tempel von Telhamat",
  },
  [10094] = {
    ["D"] = "Es ist nun zwei Jahre her, dass die Beschwörung des Schattenrats dieses einst großartige Mausoleum explodieren und die Knochenwüste entstehen lies.$B$BJetzt befinden sie sich im Krieg mit einer unglaublich mächtigen Kraft im Inneren.$B$B$N, Ihr müsst in das Schattenlabyrinth eindringen und herausfinden, wogegen sie kämpfen und ob es eine Gefahr für uns sein könnte. Unserer Spione haben berichtet, dass sie ein verbotenes Buch mit unheiligen Beschwörungen verwenden, bekannt als der Kodex des Blutes.$B$BSucht in ihm nach Antworten.",
    ["O"] = "Lest den Kodex des Blutes im Schattenlabyrinth von Auchindoun.",
    ["T"] = "Der Kodex des Blutes",
  },
  [10095] = {
    ["D"] = "Der Schattenrat hat die Kontrolle über das kosmische Wesen, das sie vor fast zwei Jahren beschworen haben, verloren! Es hat die Explosion von Auchindoun verursacht und die Knochenwüste erschaffen.$B$BJetzt nur noch als Murmur bekannt, droht es, den Schattenrat zu überrennen und zu entkommen!$B$BNun, da der Großmeister und der Rest der Führung tot sind, seid Ihr die einzige Hoffnung der Scherbenwelt im Kampf gegen dieses Monster!$B$B$N, tief in Eurem Herzen wisst Ihr, dass Ihr und Eure Freunde nicht versagen dürfen!",
    ["O"] = "Vernichtet Murmur und informiert danach Spionagemeisterin Mehlisah Hochkrone auf der Terrasse des Lichts in Shattrath über die Geschehnisse im Schattenlabyrinth.",
    ["T"] = "In das Herz des Labyrinths",
  },
  [10096] = {
    ["D"] = "Ikeyen hat mir erzählt, was ihm in diesen Höhlen widerfahren ist, und ich finde es einfach nur schrecklich!$B$BWisst Ihr, vielleicht sollten wir diesen armen Sporloks helfen. Ikeyen sagt, dass sie dort in der Funggorhöhle leben, aber die Marschenbestien sind kürzlich dort eingefallen.$B$BWarum geht Ihr nicht dort hin und schaut, dass Ihr ein paar von diesen Marschenbestien erlegt? Die Höhle ist ganz nah im Südwesten.",
    ["O"] = "Tötet 10 Marschengräber und 10 Marschenlauerer und kehrt dann zu Lauranna Thar\'well in der Zuflucht des Cenarius in den Zangarmarschen zurück.",
    ["T"] = "Rettung der Sporloks",
  },
  [10097] = {
    ["D"] = "Die Sethekk haben Skettis mit Fanfaren verlassen, als Auchindoun explodiert ist. Das war sicher ein Vorbote für die Ankunft unseres Meisters.$B$BMein Bruder, Syth, war einer ihrer Anführer und hat uns gesagt, dass es unsere Pflicht wäre, in die Tempelruinen zu gehen und unserem Gott zu begegnen.$B$BNachdem wir unser Lager in den Ruinen aufgeschlagen und sie die Sethekkhallen getauft hatten, kamen mir Zweifel an den Fähigkeiten meines Bruders und seines Verbündeten Ikiss.$B$BNach einiger Zeit hat mich Syth aus den Hallen geworfen und mir verboten, meine Schwester Lakka mitzunehmen. Helft Ihr mir, sie zu retten?",
    ["O"] = "Tötet Dunkelwirker Syth in den Sethekkhallen und befreit Lakka. Kehrt zu Isfar außerhalb der Sethekkhallen zurück, wenn Ihr die Aufgabe abgeschlossen habt.",
    ["T"] = "Bruder gegen Bruder",
  },
  [10098] = {
    ["D"] = "Mein Volk verehrt einen uralten Helden namens Terokk. Arakkoaküken erfreuen sich an den Geschichten über seine Taten und den Beschreibungen seines prächtigen Federkleids.$B$BEr lebte für hunderte von Jahren unter meinem Volk, doch eines Tages verschwand er urplötzlich, und ließ nur seine Maske, seinen Speer und seine Schriften zurück.$B$BDiese Relikte werden von den Arakkoa hoch verehrt, und es brach ihnen das Herz, als die Sethekk die Gegenstände bei ihrem Aufbruch aus Skettis verschleppten.$B$BIch habe alles, was ich über den Verbleib der Schriftrollen in den Hallen weiß, niedergeschrieben.",
    ["O"] = "Bringt Terokks Maske, Terokks Federkiel und die Sage von Terokk zu Isfar außerhalb der Sethekkhallen.",
    ["T"] = "Terokks Erbe",
  },
  [10099] = {
    ["D"] = "Diese verfluchten Gan\'arg wuseln immer noch in die Mine! Für jeden, den wir umbringen, scheinen zwei oder drei weitere aufzutauchen!$B$BIrgendwas in den Tiefen der Mine muss diese Menge steuern. Ihr scheint das letzte Mal ganz gut klargekommen zu sein, und das Licht weiß, dass Ihr $Gder:die; Einzige seid, $Gder:die; mutig genug ist, wieder in die Mine zu gehen. Findet heraus, wer für dieses Ärgernis verantwortlich ist und bestraft ihn dafür!",
    ["O"] = "Sucht und tötet Z\'kral in den Minen unter der Ehrenfeste und meldet Euch dann bei Großknecht Biggums zurück.",
    ["T"] = "Das Genie",
  },
  [10100] = {
    ["D"] = "Versteht mich nicht falsch, $N, ich bin sehr froh, dass Ihr Euch bereit erklärt habt, uns mit den Minen zu helfen, aber für jeden Pionier, den Ihr getötet habt, sind zwei neue dazugekommen.$B$BIch habe Wachkommandant Krunk schon die Ohren vollgeheult, aber er weigert sich immer noch, mir Hilfe zu schicken. Er murmelt immer irgendwas von Höllenebern. Wenn ich mir nicht so sicher wäre, dass er mich verprügeln würde, würde ich ihm sagen, dass jetzt nicht die Zeit ist, um an Rippchen zu denken!$B$BWie auch immer, es sieht so aus, als ob irgendjemand tief in den Minen die ganze Sache steuert. Findet ihn und tötet ihn!",
    ["O"] = "Sucht und tötet Urga\'zz in den Minen unter Thrallmar und kehrt dann zu Großknecht Razelcraz zurück.",
    ["T"] = "Das Genie",
  },
  [10101] = {
    ["D"] = "Oshu\'gun, der diamantene Berg, war einst ein heiliger Ort meines Volkes. Dort versammelten sich die Klans jedes Jahr, um das Frühlingsfest zu feiern. Dort kommunizierten unsere Schamanen mit unseren altehrwürdigen Vorfahren. Er bildete das Zentrum unserer Kultur - bis die Schatten uns übernahmen. Als die Klans zu einer Horde wurden, verstummten die Geister von Oshu\'gun. Unsere Vorfahren haben sich von uns abgewendet.$B$BDoch nun werden sie in ihrer Ruhe gestört. Reist nach Oshu\'gun und findet heraus, was die Geister beunruhigt.",
    ["O"] = "Mutter Kashur im Ahnengrund in Nagrand bittet Euch, Eurer Vision auf den Grund zu gehen. Findet K\'ure in Oshu\'gun.",
    ["T"] = "Wenn Geister sprechen",
  },
  [10102] = {
    ["D"] = "Als meine Energien sich über die Jahrhunderte nach und nach verflüchtigten, nahm eine Leere meinen Platz ein und verschlang die Seelen in der Nähe. Hilflos musste ich zusehen, wie ganze Generationen von Orcseelen in den Wirbel hineingezogen wurden. Vor Kurzem bemächtigten meine Feinde sich des Wirbels und benutzten ihn, um zahllose Leerdiener in ihre Brennende Legion zu rufen.$B$BNun liegt der Schlüssel zur Beendigung dieses Kreislaufs und der Befreiung der Orcgeister bei einem anderen Naaru, A\'dal. Ich konnte seine Essenz in der uralten Stadt Shattrath in den Wäldern von Terokkar fühlen.",
    ["O"] = "Sprecht mit A\'dal in Shattrath.",
    ["T"] = "Ein gelüftetes Geheimnis",
  },
  [10103] = {
    ["D"] = "Mit der Ankunft von Feldkommandeur Romus und seinen Streitmächten sind die Allianz und ihre Draeneifreunde mutiger geworden. Sie wandeln alle alten Draeneiruinen von hier bis Shattrath in Heerlager um.$B$BAls Antwort darauf haben die Trolle einen neuen Außenposten im östlichen Teil der Zangarmarschen errichtet, südlich des Todesmoors. Sie nennen ihn Sumpfrattenposten. Ich bin froh, dass sie sich in einer solchen Umgebung ihren Humor bewahrt haben. Ich wollte jedenfalls nicht mit ihnen tauschen.$B$BDer Kommandeur des Posten hat mich gebeten, ihm alle Mann zu schicken, die ich entbehren kann.",
    ["O"] = "Meldet Euch bei Zurai im Sumpfrattenposten in den Zangarmarschen.",
    ["T"] = "Bericht an Zurai",
  },
  [10104] = {
    ["D"] = "Die Gerüchte über Tuurem sind beunruhigend. Manche sagen, die Ruinen der Stadt laufen vor verrückten Zerschlagenen und Verirrten über, während gierige Artefaktjäger Draeneirelikte stehlen.$B$BEs ist schwer zu sagen, welche wahr und welche falsch sind, aber es liegt in unserer Verantwortung, die Ruinen so gut es geht zu schützen. Sucht Andarl in der Feste in den Wäldern von Terokkar. Er ist eine Art Forscher für magische Relikte. Er weiß wahrscheinlich besser als alle anderen Bescheid, was dort vor sich geht.",
    ["O"] = "Sprecht mit Andarl in der Feste in den Wäldern von Terokkar.",
    ["T"] = "Sorgen um Tuurem",
  },
  [10105] = {
    ["D"] = "Now that Swamprat Post is complete, our control of the roads in and out of the marsh is much less tenuous. Our presence in Terokkar Forest is much less secure. Even so, at Stonebreaker Hold, Rakoria awaits word of our progress.$B$BShe\'s sharp and capable, $N, but be careful around her. She\'s got a lot of a goods she\'s eager to move along the roads, but don\'t assume she\'s got our best interests at heart. Rakoria goes where she believes there\'s profit to be found.",
    ["O"] = "Sprecht mit Rakoria in der Steinbrecherfeste in den Wäldern von Terokkar.",
    ["T"] = "Nachrichten für Rakoria",
  },
  [10106] = {
    ["D"] = "Die Höllenfeuerzitadelle steht im ständigen Kampf mit unseren Streitmächten. Wir halten uns wacker, aber wir können keinen richtigen Angriff gegen sie starten. Um dies zu tun, müssen wir weiter vorrücken.$B$BUnsere Späher haben drei Befestigungsanlagen in der Nähe der Höllenfeuerzitadelle entdeckt. Wir möchten, dass Ihr uns helft, diese drei Anlagen einzunehmen. Meldet Euch bei mir, sobald Ihr eine in Beschlag nehmen konntet.$B$BMöge das Glück in den vor Euch liegenden Kämpfen bei Euch sein, $N, denn Ihr werdet ohne Zweifel auf Streitkräfte der Horde treffen, die dasselbe im Schilde führen.",
    ["O"] = "Nehmt den Rundblick, das Stadion und die Trümmerbastion ein, und kehrt dann zu Stabsfeldwebel Tracy Stolzbrunn in der Ehrenfeste auf der Höllenfeuerhalbinsel zurück.",
    ["T"] = "Höllenfeuerbefestigungen",
  },
  [10107] = {
    ["D"] = "Kilrath hat Neuigkeiten über den Anführer der Felsfäuste geschickt: Es ist ein Orc! Ein uralter Orc, der in der Zeit unserer Väter das Dunkle Portal durchschritten hat und in sein Heimatland zurückgekehrt ist. Ich möchte, dass Ihr diesen Orc ausfindig macht und ihn davon überzeugt, die Felsfäuste zurückzuziehen. Er lebt auf einer der höheren Ebenen bei den Ruinen der Brennenden Klinge im Südosten, nahe der Grenze zu Terokkar. Viel Erfolg, $N!$B$BSeid auf der Hut, er ist ein Klingenmeister...",
    ["O"] = "Jorin Totauge in Garadar in Nagrand möchte, dass Ihr Lantresor von der Klinge in den Ruinen der Brennenden Klinge findet und mit ihm sprecht.",
    ["T"] = "Diplomatische Maßnahmen",
  },
  [10108] = {
    ["D"] = "Die Agenten der Jägerin haben Neuigkeiten über den Anführer der Felsfäuste geschickt: Es ist ein Orc! Ein uralter Orc, der in der Zeit unserer Väter das Dunkle Portal durchschritten hat und in sein Heimatland zurückgekehrt ist. Ich möchte, dass Ihr diesen Orc ausfindig macht und ihn davon überzeugt, die Felsfäuste zurückzuziehen. Er lebt auf einer der höheren Ebenen bei den Ruinen der Brennenden Klinge im Osten. Viel Erfolg, $N!$B$BSeid auf der Hut, er ist ein Klingenmeister...",
    ["O"] = "Mo\'mor der Brecher in Telaar in Nagrand möchte, dass Ihr Lantresor von der Klinge in den Ruinen der Brennenden Klinge findet und mit ihm sprecht.",
    ["T"] = "Diplomatische Maßnahmen",
  },
  [10109] = {
    ["D"] = "Könnt Ihr sie sehen?? Bei Gazlows wundervollem Mundgeruch, ich muss diese Eier haben! Ihr seht aus wie jemand, den man gebrauchen kann.$B$BIch sag Euch was, $R, wenn Ihr ein paar Dinge für mich sammelt, damit ich meinen Spring-o-Mat 4000 zum Laufen bekomme, werde ich Euch einen Schlüssel geben, mit dem auch Ihr die Maschine bedienen könnt! Was man mit einem Spring-o-Mat 4000 machen kann? Wohl eher was man damit nicht machen kann...$B$BIch brauche Luftelementargas von den Luftelementaren in Nagrand. Sie sind überall, fragt mich also nicht, wo Ihr sie finden könnt!",
    ["O"] = "Wazdaz gegenüber der Klanwacht im Süden von Nagrand möchte, dass Ihr ihm 3 Einheiten Luftelementargas bringt.",
    ["T"] = "Ich muss sie haben!",
  },
  [10110] = {
    ["D"] = "Die Höllenorcs der Höllenfeuerzitadelle müssen von unserem Heimatland entfernt werden! Um sie anzugreifen, müssen wir strategisch wichtige Punkte in der Nähe der Zitadelle einnehmen.$B$BUnsere Kundschafter haben drei Befestigungsanlagen ausgemacht, die für unsere Zwecke geeignet sind, aber die Allianz hatte dieselbe Idee und schickt schon Streitkräfte, um sie einzunehmen!$B$BWir möchten, dass Ihr die Allianz aufhaltet und die drei Forts einnehmt. Helft bei der Einnahme jeder Anlage und kehrt dann zu mir zurück.$B$BFür die Horde!",
    ["O"] = "Nehmt den Rundblick, das Stadion und die Trümmerbastion ein, und kehrt dann zu Kriegsherold Blauauge in Thrallmar zurück.",
    ["T"] = "Höllenfeuerbefestigungen",
  },
  [10111] = {
    ["D"] = "Jetzt, da Ihr initiiert wurdet, möchte ich, dass Ihr mir dieses Ei bringt! Benutzt den Spring-o-Mat und versucht, das Nest genau in der Mitte zu treffen. Aber seid vorsichtig! Schlechte Sprünge können in körperlichen Schäden oder dauerhaften schweren Wunden resultieren.",
    ["O"] = "Wazdaz in Nagrand möchte, das Ihr den Spring-o-Mat 4000 benutzt, um in das Nest in dem nahe gelegenen Baum zu springen und das geheimnisvolle Ei zu stehlen.",
    ["T"] = "Bringt mir das Ei!",
  },
  [10112] = {
    ["D"] = "Hmmm? Eine Kiste sagt Ihr? Oder waren es mehrere? Wisst Ihr aus welchem Material sie war?$B$BUnd wie heißt der Händler? Empoor? Und Ihr seid Euch sicher, dass er ein Zerschlagener ist? Ich mache nicht viele Geschäfte mit ihresgleichen.$B$BVielleicht hilft es meinem Gedächtnis ein wenig auf die Sprünge, wenn Ihr mir einen Gefallen tut.$B$BDie Arakkoa der Skith - diese primitiven Zauberer - haben neulich eine große Schiffladung meiner magischen Waren gestohlen. Ich will alles zurückhaben!$B$BIhr könnt das Skithversteck südlich von Shattrath finden.",
    ["O"] = "Holt 5 von Lathrais gestohlenen Waren zurück und bringt sie zu Windhändler Lathrai bei der Taverne Weltenend im Unteren Viertel in Shattrath.",
    ["T"] = "Ein persönlicher Gefallen",
  },
  [10113] = {
    ["D"] = "\"Geht und sucht ein paar Vorräte\", hat er gesagt. Ich bin fast fünfzehn Mal getötet worden, bevor ich diesen Ort hier überhaupt gefunden habe! Diese Zerschlagenen sind vielleicht nicht das gastfreundlichste Volk, das ich kenne, aber immerhin haben sie nicht versucht, mir die Haut von den Knochen zu ziehen!$B$BWie auch immer, ich gehe nicht zurück. Meine Tage als Großwildjäger sind vorbei. Wenn ihr zufällig nach Nordosten Richtung Zangarmarschen geht und bei Nesingwarys Safari vorbeikommt, sagt dort Bescheid, dass \"die Flinte\" gekündigt hat.$B$BHey, vielleicht könnt Ihr ja meinen Platz einnehmen! Viel Glück, $GFremder:Fremde;.",
    ["O"] = "\"Die Flinte\" Jones möchte, dass Ihr mit Hemet Nesingwary in Nesingwarys Safari am nordöstlichen Rand von Nagrand sprecht.",
    ["T"] = "Nesingwarys Safari",
  },
  [10114] = {
    ["D"] = "Diese Orcs haben noch nie zuvor einen Tauren gesehen, könnt Ihr das glauben? Wenn Hauptmann Kroghan nicht wäre, würden sie mich wahrscheinlich in diesem Moment zum Abendessen verschlingen!$B$BAber wo sind meine Manieren? Ich bin Ohlorn Fernreiser, herausragender Jäger! Ich gehöre zu Nesingwarys Expedition, die sich von Azeroth aus auf die Jagd nach Großwild hierher begeben hat. Wenn Ihr Euch für die Jagd interessiert, solltet Ihr meine Truppe in Nesingwarys Safari östlich von hier an den Grenzen zu den Zangarmarschen besuchen.",
    ["O"] = "Ohlorn Fernreiser möchte, dass Ihr mit Shado \'Fitz\' Fernreiser in Nesingwarys Safari in Nagrand sprecht.",
    ["T"] = "Nesingwarys Safari",
  },
  [10115] = {
    ["D"] = "Die Kurenai und die Draenei sind nicht immer der gleichen Meinung, besonders nicht, wenn es um die Verirrten geht. Wir helfen den Verirrten, die sich an uns wenden, gerne, aber wir werden sie niemals verfolgen. Manche Draenei sind andererseits überzeugt davon, dass sie alle \'erlöst\' werden müssen.$B$BEs gibt jedoch Zeiten, in denen sogar die Verirrten zu weit gehen, wie zum Beispiel der Dolchfennstamm. In ihrem Dorf im Westen hantieren sie mit Giften herum, lügen und betrügen, was für uns sehr unnatürlich ist.$B$BWir müssen dem ein Ende bereiten, bevor der bösartige Dolchfennstamm seinen Einfluss noch weiter ausbreiten kann.",
    ["O"] = "Tötet 3 Auftragsmörder von Dolchfenn und 15 Matschwirbler von Dolchfenn.",
    ["T"] = "Dolchfennkrise",
  },
  [10116] = {
    ["D"] = "Gesucht: Häuptling Mummaki - Anführer des verdorbenen und degenerierten Dolchfennstamms der Verirrten, wegen seiner brutalen Angriffe auf die Oreborzuflucht.$B$BDer Häuptling und seine Truppe von Auftragsmördern haben mehrere Kurenai und Draenei auf dem Gewissen. Nur erfahrene Kämpfer sollten sich an sie heranwagen.$B$BReisenden wird nahe gelegt, die Gegend um Dolchfenn westlich der Oreborzuflucht zu meiden.$B$BDas Kopfgeld kann bei Vorlage eines Beweises für das Ableben des Häuptlings bei Ikuti eingefordert werden.",
    ["O"] = "Bringt Häuptling Mummakis Totem zu Ikuti in der Oreborzuflucht.",
    ["T"] = "Gesucht: Häuptling Mummaki",
  },
  [10117] = {
    ["D"] = "Gesucht: Häuptling Mummaki - Anführer des verdorbenen und degenerierten Dolchfennstamm der Verirrten, wegen seiner brutalen Angriffe auf Zabra\'jin und seine Einwohner.$B$BDer Häuptling und seine Truppe von Auftragsmördern haben mehrere Späher und Boten auf dem Gewissen. Nur erfahrene Kämpfer sollten sich an sie heranwagen.$B$BReisenden wird nahe gelegt, die Gegend um Dolchfenn nordwestlich von Zabra\'jin zu meiden.$B$BDas Kopfgeld kann bei Vorlage eines Beweises für das Ableben des Häuptlings bei Schattenjäger Denjai eingefordert werden.",
    ["O"] = "Bringt Häuptling Mummakis Totem zu Schattenjäger Denjai.",
    ["T"] = "Gesucht: Häuptling Mummaki",
  },
  [10118] = {
    ["D"] = "Von allen Stämmen der Verirrten in den Zangarmarschen ist der Dolchfennstamm der gerissenste und aggressivste. Er ist sehr gut organisiert und macht keinen Hehl daraus, dass er gerne die Kontrolle über alle Verirrten in den Marschen hätte.$B$BIhren Angriffen sind schon viele meiner Späher und Boten zum Opfer gefallen. Ich kann es mir nicht leisten, noch mehr Männer zu verlieren.$B$BWenn wir nicht bald handeln, werden sie die komplette Kontrolle über alle Straßen erlangen und Zabra\'jin einkesseln.",
    ["O"] = "Tötet 3 Auftragsmörder von Dolchfenn und 15 Matschwirbler von Dolchfenn und kehrt dann zu Schattenjäger Denjai in Zabra\'jin zurück.",
    ["T"] = "Nachricht für Dolchfenn",
  },
  [10119] = {
    ["D"] = "Zwanzig Jahre lang war es ruhig um das Dunkle Portal – es wurde zu einem Symbol längst vergangener Zeiten.$B$B<Reldorn sieht sich um.>$B$BIch habe nicht geglaubt, dass ich den heutigen Tag erleben würde, aber hier stehen wir nun und schauen tief in den Abgrund.$B$BAuf der anderen Seite des Portals sind wir in einem grausamen Krieg gefangen – ein Krieg, den wir nicht ohne Hilfe gewinnen können. Ihr müsst durch das Dunkle Portal reisen und unseren Truppen helfen, $C.$B$BMeldet Euch umgehend bei Kommandant Duron. Er erwartet Euch in der Scherbenwelt...",
    ["O"] = "Reist durch das Dunkle Portal und sucht Kommandant Duron. Er wartet auf der anderen Seite auf Eure Ankunft - in der Scherbenwelt.",
    ["T"] = "Durch das Dunkle Portal",
  },
  [10120] = {
    ["D"] = "Mit der Unterstützung der Allianz können meine Leute die Verteidigung hier aufrechterhalten, aber Ihr müsst dringend meinen Bericht zu General Krakork in Thrallmar bringen. Mit seiner Hilfe können wir diese Dämonen zurück in den Abgrund drängen, aus dem sie gekommen sind! Sprecht mit Vlagga Freyfeder auf der Anhöhe hinter uns. Sie wird Euch schnell auf den Weg dorthin bringen.",
    ["O"] = "Generalleutnant Orion beim Dunklen Portal auf der Höllenfeuerhalbinsel möchte, dass Ihr Orions Bericht zu Vlagga Freyfeder bringt.",
    ["T"] = "Ankunft in der Scherbenwelt",
  },
  [10121] = {
    ["D"] = "Auch wenn dieses Land voller neuer, unbekannter Feinde ist, ist die Brennende Legion immer noch die größte Bedrohung für unsere Expedition. Einer meiner vertrauenswürdigsten Krieger, Unteroffizier Schädelbrecher, hat die Taktiken der Legion untersucht und versucht, das wahre Ziel der Legion in diesem Land herauszufinden. Ihr findet ihn bei der Versorgungskarawane außerhalb des südlichen Walls.$B$BUnd $N, hütet Euch vor der Legion. Ihre Bösartigkeit darf nicht unterschätzt werden.",
    ["O"] = "Sprecht mit Unteroffizier Schädelbrecher bei der Versorgungskarawane außerhalb von Thrallmar im Osten.",
    ["T"] = "Die Brennende Legion ausrotten",
  },
  [10122] = {
    ["D"] = "Jeden Tag öffnen sich die Tore der Höllenfeuerzitadelle und noch mehr Höllenorcs strömen heraus und in die Lager, die sie entlang des Bollwerks südlich der Zitadelle errichtet haben.$B$BIch habe einen meiner besten Späher, Gefreiter Imarion, losgeschickt, um ihre Vorgehensweise zu erkunden, aber er ist noch nicht mit seinem Bericht zurückgekehrt.$B$BImarion sollte sich auf das nördlichste Lager entlang des Südlichen Bollwerks, nahe bei seiner Verbindung zur Zitadelle, konzentrieren. Findet ihn, $N. Ohne seinen Bericht sitzen wir hier hilflos herum.",
    ["O"] = "Durchsucht das nördlichste Lager entlang des Südlichen Bollwerks nach einer Spur von Gefreiter Imarion.",
    ["T"] = "Die Erstreckung der Zitadelle",
  },
  [10123] = {
    ["D"] = "Verdammnislord Kazzak hat das Kommando über die Brennende Legion in der Scherbenwelt. Da wir Kazzak nicht direkt angreifen können - noch nicht - müssen wir zunächst seine Leutnants, die die Truppen vom Boden aus kommandieren, auslöschen.$B$BSie haben Schreckenslords in der Teufelsfunkenklamm östlich von hier postiert. Sie sind für diese höllischen Stürme, die auf uns herabprasseln, verantwortlich. Sie und ihre Diener zu vernichten wäre ein schwerer Schlag für die Legion.",
    ["O"] = "Unteroffizier Schädelbrecher bei der Versorgungskarawane östlich von Thrallmar auf der Höllenfeuerhalbinsel möchte, dass Ihr 4 Flammenschürerwichtel, 6 höllische Kriegshetzer und 1 Schreckensrufer tötet.",
    ["T"] = "Teufelsfunkenklamm",
  },
  [10124] = {
    ["D"] = "Eure Fähigkeiten in der Schlacht sind dem obersten Kommando aufgefallen, $C. Sie wollen Euch an die Front zu Stümmelfausts Truppe schicken. Sie sind sehr radikal und haben sich der vollständigen Auslöschung der Legion verschrieben, also passt Ihr wahrscheinlich genau dazu...$B$BGeht etwa 200 Schritte nach Osten und biegt bei der hervorragenden Landmasse nach Süden ab. Ihr findet den Häschersturz dort, wo einst ein alter Bauernhof stand. Wenn Ihr angekommen seid, meldet euch bei Vorpostenkommandant To\'arch.$B$BUnd jetzt weggetreten!",
    ["O"] = "Meldet Euch bei Vorpostenkommandant To\'arch im Häschersturz.",
    ["T"] = "Vorposten: Häschersturz",
  },
  [10125] = {
    ["D"] = "Direkt im Osten liegt die Front der Legion. Die Front dient der Brennenden Legion als mobile Kommandobasis. Ihr könnt die Sender der Legion aus der Erde herausragen sehen. Mit ihrer Hilfe kommuniziert die Legion mit ihren Konstruktionslagern in der ganzen Scherbenwelt. Sie dienen ebenfalls als Schutzeinrichtung zur Verschlüsselung sämtlicher Übertragungen, sodass eine magische Überwachung nicht mehr möglich ist.$B$BIch möchte, dass Ihr diesen Sprengstoff nehmt und die Sender damit zerstört. Zerstört sie alle, $N! Sie sind von Süden nach Norden an der Grenze der Front der Legion aufgereiht.",
    ["O"] = "Vorpostenkommandant To\'arch im Häschersturz auf der Höllenfeuerhalbinsel möchte, dass Ihr To\'archs Sprengstoff benutzt, um den Sender der Legion: Bosheit, Sender der Legion: Zorn, Sender der Legion: Hass und Sender der Legion: Furcht zu zerstören.",
    ["T"] = "Mission: Die Kommunikation unterbrechen",
  },
  [10126] = {
    ["D"] = "Die Anweisungen sind mit Kriegsboss Nekrogg unterzeichnet. Sie befehlen den Höllenorcs in diesem Lager, ihren menschlichen Gefangenen zum Verhör nach Süden zu bringen.$B$BLeutnant Amadi hat erwähnt, dass das Südliche Bollwerk über mehrere Höllenorclager verfügt. Vielleicht haben sie Gefreiter Imarion zum nächsten größeren Abschnitt des Walls Richtung Süden gebracht.$B$BDa dieser Befehl von Verhör spricht, scheint es den Höllenorcs wichtig zu sein, dass Gefreiter Imarion am Leben bleibt. Nehmt schnell ihre Spur auf, bevor sie es sich anders überlegen!",
    ["O"] = "Reist entlang der westlichen Seite des Südlichen Bollwerks nach Süden und durchsucht das nächste Lager nach einer Spur von Gefreiter Imarion.",
    ["T"] = "Kriegsboss Nekroggs Befehle",
  },
  [10127] = {
    ["D"] = "Es wäre Vernachlässigung meiner Pflicht, wenn ich Euch nicht befehlen würde, die Dämonen an der Front der Legion, östlich von dieser Basis, zu vernichten.$B$BBeide Seiten der Front sind nach dem selben Muster aufgebaut: Teufelstreiber wandeln zwischen den Lagern der Legion hin und her, die von Zornwächtern bewacht werden. Die ganze Operation wird von einer Unterwerferin der Shivan beaufsichtigt. Tötet sie alle und überlasst Kazzak den Rest.$B$BUnd passt auf die patrouillierenden Teufelshäscher auf. Ich weiß, dass Ihr stark seid, aber diese Biester machen mit Euch kurzen Prozess.",
    ["O"] = "Vorpostenkommandant To\'arch am Häschersturz auf de Höllenfeuerhalbinsel möchte, dass Ihr 4 Teufelstreiber, 8 Zornwächter und Unterwerferin Shi\'aziv tötet.",
    ["T"] = "Mission: Das Band zerreißen",
  },
  [10128] = {
    ["D"] = "Die Spuren führen von Imarions Ausrüstung aus nach Süden, anscheinend entlang des Südlichen Bollwerks. Aus den Furchen lässt sich schließen, dass die Entführer es eilig hatten, ihn an den nächsten Ort zu bringen, aber warum?$B$BHat ihn dort jemand erwartet? Vielleicht Kriegsboss Nekrogg, der Anführer, der in den Befehlen, die Ihr weiter nördlich gefunden habt, erwähnt wurde?$B$BDas Schild und die Spuren werfen mehr Fragen auf, als sie beantworten, aber es scheint sicher, dass Ihr dort, wo Ihr Gefreiter Imarion findet, auch Kriegsboss Nekrogg finden werdet.",
    ["O"] = "Setzt Euren Weg entlang des Südlichen Bollwerks fort und sucht nach dem Ort, an dem Kriegsboss Nekrogg Gefreiter Imarion gefangen hält.",
    ["T"] = "Der Soldat Imarion",
  },
  [10129] = {
    ["D"] = "Wir haben die beiden Haupttransporter, mit denen die Brennende Legion ihre Verstärkung herbeiholt, ausgemacht: Tor Murketh und Tor Shaadraz.$B$BWenn Ihr den Pfad des Ruhmes nach Osten hinaufschaut, könnt Ihr sie an beiden Seiten der Straße erkennen. Ich möchte, dass Ihr sie beide in Stücke sprengt!$B$BHier sind die Bomben, die Ihr dafür benötigt. Sie wurden speziell von einem Goblin in Area 52 entworfen. Sprecht mit Schwadronskommandant Brack - er wird Euch einen Wyvern zur Verfügung stellen.",
    ["O"] = "Sprecht mit Schwadronskommandant Brack, bombardiert Tor Murketh und Tor Shaadrez und kehrt dann zu Vorpostenkommandant To\'arch im Häschersturz zurück.",
    ["T"] = "Mission: Tore Murketh und Shaadraz",
  },
  [10130] = {
    ["D"] = "Zusätzlich zu den anderen Anweisungen werden die Orc in den Befehlen angewiesen, ihre Ausrüstung und Verpflegung für einen Angriff vorzubereiten.$B$BNach den Plänen sollen Kriegsboss Nekroggs Mannen sich zu einer westlichen Flanke formieren, um die Ehrenfeste in die Zange zu nehmen.$B$BDa die Garnison der Feste sich auf die Verteidigung der östlichen Seite konzentriert, wäre ein Angriff von Westen fatal. Die Ehrenfeste kann nur gehalten werden, wenn der Angriff von Westen niemals stattfindet.",
    ["O"] = "Tötet 10 Wachen der Zerschmetterten Hand, 10 Berserker der Zerschmetterten Hand und 5 Akolythen der Zerschmetterten Hand und meldet Euch dann bei Leutnant Amadi im westlichen Wachturm der Ehrenfeste.",
    ["T"] = "Die westliche Flanke",
  },
  [10131] = {
    ["D"] = "Wie ich schon gesagt habe, hier rauszukommen wird nicht leicht. Kriegsboss Nekrogg mag vielleicht wie ein übergroßer Schläger aussehen, aber er ist kein Dummkopf.$B$BEr wird den Käfigschlüssel nicht aus den Augen lassen und kommt nicht aus seinem Turm auf dieser massiven Mauer heraus, bis er bereit ist, mich oder diese Orcfrau, die er gefangen hat, zu verhören.$B$BWir brauchen schon etwas Besonderes, um ihn aus diesen Mauern zu locken, aber ich habe einen Plan. Sucht einen der Grenadiere, die am Südlichen Bollwerk entlang patrouillieren, und bringt mir etwas von seinem Sprengstoff.",
    ["O"] = "Tötet einen Grenadier des Lachenden Schädels und bringt seine primitive Sprengladung zu Gefreiter Imarion im Außenposten von Gor\'gaz.",
    ["T"] = "Fluchtplan",
  },
  [10132] = {
    ["D"] = "Seht Ihr diesen riesigen roten Kristall im Norden? Er ist vor Kurzem vom Himmel gefallen und hat dabei ein großes Erdbeben ausgelöst.$B$BAls wir hierher kamen, um den Grund für den Aufruhr zu erforschen, haben wir all die Kolosse hier gesehen, die zuvor noch nicht da waren.$B$BEs ist eindeutig etwas faul an dem Kristall und den Energien, die er ausstrahlt, aber ich habe keine Zeit dafür. Ich muss mich zuerst um diese Bergriesen kümmern.$B$BIch möchte, dass Ihr zum Himmelssturzgrat geht und Euch um sie kümmert.",
    ["O"] = "Tötet 5 tobende Kolosse und kehrt dann zu Tola\'thion im Posten des Cenarius auf der Höllenfeuerhalbinsel zurück.",
    ["T"] = "Eine kolossale Bedrohung",
  },
  [10133] = {
    ["D"] = "Euer Geschick bereitet der Legion große Schwierigkeiten, $N. Meine Späher haben mich informiert, dass die Legion Kuriere benutzt, um untereinander zu kommunizieren. Wir haben es geschafft, ihre Truppen vom Hauptlager abzuschneiden!$B$BWorum ich Euch nun bitte ist mehr, als ich von einem normalen Soldaten verlangen würde.$B$BDer Kurier der Legion reist zwischen den Teichen von Aggonar und dem Konstruktionsbasis: Mageddon, nördlich von hier, hin und her. Ihr müsst den Kurier bei Mageddon abfangen, ihn erledigen und mit dem Schriftwechsel der Legion zu mir zurückkehren.",
    ["O"] = "Vorpostenkommandant To\'arch im Häschersturz auf der Höllenfeuerhalbinsel möchte, dass Ihr Arix\'maladish findet und tötet und den Schriftwechsel der Legion besorgt.",
    ["T"] = "Mission: Tötet den Boten",
  },
  [10134] = {
    ["D"] = "Als der Koloss Eurem Angriff zum Opfer fällt, bemerkt Ihr, dass er ein rotes Kristallfragment in der Hand hält.$B$BIn der Hand des Riesen schien es sehr klein und unauffällig, aber für Euch ist das Stück sehr schwer zu tragen. Es sieht sehr verdächtig nach einem Stück des riesigen roten Kristalls in der Mitte des Grats aus.$B$BDies ist ganz sicher ein Hinweis auf die merkwürdigen Dinge, die hier geschehen. Ihr seid Euch sicher, dass die Druiden beim Posten des Cenarius im Süden den Splitter sehen wollen.",
    ["O"] = "Bringt den Purpurkristallsplitter zu Tola\'thion im Posten des Cenarius auf der Höllenfeuerhalbinsel.",
    ["T"] = "Hinweise des purpurnen Kristalls",
  },
  [10135] = {
    ["D"] = "Ich bin nur ein Feldkommandeur, $C. Die Befugnis, auf solche Informationen hin zu handeln, fällt nur unserem Anführer, Nazgrel, zu. Ihr müsst den Schriftwechsel nach Thrallmar zu ihm bringen. Reitet schnell wie der Wind, Soldat. Lasst Euch durch nichts aufhalten!",
    ["O"] = "Bringt den Schriftwechsel der Legion zu Nazgrel in Thrallmar auf der Höllenfeuerhalbinsel.",
    ["T"] = "Mission: Seid der Bote",
  },
  [10136] = {
    ["D"] = "Magister Blutfalke hat endlich den Schriftwechsel der Legion, den Ihr gefunden habt, entziffert. Wie es aussieht, handelt es sich dabei um Angriffspläne, die von einem Leutnant des Hochlords Kazzak geschickt wurden - Arazzius der Grausame. Offensichtlich hat Arazzius den Befehl über die Bodentruppen auf der Höllenfeuerhalbinsel. Es ist an der Zeit, dass wir seinen Befehl beenden, $N. Vernichtet ihn mit größter Vorsicht.$B$BBegebt Euch zu den Teichen von Aggonar und setzt dem so genannten \'Legionslord\' ein Ende. Heute fordert die Horde die Höllenfeuerhalbinsel zurück. Die Legion möge verdammt sein.",
    ["O"] = "Tötet Arazzius den Grausamen und kehrt danach zu Nazgrel in Thrallmar zurück.",
    ["T"] = "Grausame Pläne",
  },
  [10137] = {
    ["D"] = "Der Erfolg des Plans hängt von der Qualität des Sprengstoffs ab...$B$BSo ungern ich mich auch auf etwas verlasse, das von einem Orc hergestellt wurde, ist dies meine einzige Möglichkeit hier rauszukommen. Nehmt dies und werft es gegen das Banner, das vom Turm da oben weht.$B$BWenn es explodiert wird Kriegsboss Nekrogg es sich nicht nehmen lassen, ein Exempel an dem Störenfried zu statuieren. Tötet ihn und nehmt den Schlüssel, um meinen Käfig zu öffnen. Viel Erfolg, $N.",
    ["O"] = "Benutzt die primitiven Sprengladungen um einen Tumult zu veranstalten und Kriegsboss Nekrogg aus seinem Turm zu locken. Tötet ihn, besorgt Nekroggs Schlüssel und öffnet damit Gefreiter Imarions Käfig. Wenn Ihr Gefreiter Imarion befreit habt, meldet Euch bei Leutnant Amadi.",
    ["T"] = "Den Kriegsboss provozieren",
  },
  [10138] = {
    ["D"] = "Eines ist klar, $N: wir können nicht so weitermachen. Die Höllenorcs haben uns eingekreist, und sobald wir sie erledigen, nehmen gleich mehrere ihren Platz ein. Wir können nicht hoffen, dass wir diesen Krieg gewinnen können, indem wir uns hinter den zerschlagenen Mauern der Ehrenfeste verstecken und die Tage bis zu unserem Tod durch die Hand der Höllenorcs zählen.$B$BIhr habt beeindruckende Arbeit geleistet und uns vor den schlimmsten Angriffen der Höllenorcs bewahrt, aber jetzt müsst Ihr Euch den Weg entlang des Pfads des Ruhmes freikämpfen und herausfinden, wer diese Angriffe steuert.",
    ["O"] = "Tötet 20 Grunzer der Zerschmetterten Hand, 10 Hexenmeister der Zerschmetterten Hand und besorgt Euch Drillmeister Zurok\'s Anweisungen. Kehrt dann zu Leutnant Amadi im westlichen Wachturm der Ehrenfeste zurück.",
    ["T"] = "Auf wessen Befehl hin?",
  },
  [10139] = {
    ["D"] = "So wie ich das sehe, war Drillmeister Zurok die rechte Hand des Anführers der Höllenorcs auf der Höllenfeuerhalbinsel, Truppenkommandant Gorax.$B$BDieser Gorax ist anders als die gewöhnlichen Schläger der Höllenorcs. Ich bewundere fast, wie er uns schon so lange in der Defensive halten konnte.$B$BZuroks Befehle deuten an, dass Gorax\' Kommandozentrale am Ende des Pfads des Ruhms liegt, dort, wo die Mauern der Zitadelle auf das Nördliche Bollwerk treffen. Ich habe keine Ahnung, was Euch dort erwartet, $N, aber Ihr solltet besser mit ein paar Gefährten reisen.",
    ["O"] = "Tötet Truppenkommandant Gorax und meldet Euch dann bei Leutnant Amadi im westlichen Turm der Ehrenfeste zurück.",
    ["T"] = "Die Beseitigung des Kommandanten",
  },
  [10140] = {
    ["D"] = "Wenn Ihr bereit seid, werde ich Euch einen Flug direkt zur Ehrenfeste besorgen.$B$BSprecht dort mit Marschall Isildor und gebt ihm Durons Bericht. Er wird sich freuen, einen weiteren Rekrut aus der Heimat zu sehen, und ich bin mir sicher, dass die Informationen in diesem Bericht sehr wichtig sind!",
    ["O"] = "Fliegt zur Ehrenfeste und übergebt Marschall Isildor Durons Bericht.",
    ["T"] = "Reise zur Ehrenfeste",
  },
  [10141] = {
    ["D"] = "Als die Brennende Legion in Azeroth eingefallen ist, war sie hier in der Scherbenwelt nicht untätig. Wir haben ihre Bewegungen über die Jahre verfolgt, aber ihre letzte Tat, $C, ist schlimmer als alles, was wir je gesehen haben.$B$BSprecht mit Unteroffizier Altumus in der Nähe der Östlichen Versorgungskarawane. Er hat die Eindringlinge beobachtet und weiß vielleicht mehr über ihre Pläne.$B$BSeid schnell und passt auf, $N - die Brennende Legion darf auf keinen Fall unterschätzt werden.",
    ["O"] = "Sprecht mit Unteroffizier Altumus bei der Östlichen Versorgungskarawane außerhalb der Ehrenfeste im Osten.",
    ["T"] = "Wiedergeburt der Brennenden Legion",
  },
  [10142] = {
    ["D"] = "Verdammnislord Kazzak hat das Kommando über die Brennende Legion in der Scherbenwelt. Da wir Kazzak nicht direkt angreifen können - noch nicht - müssen wir zunächst seine Leutnants, die die Truppen vom Boden aus kommandieren, auslöschen.$B$BSie haben Schreckenslords am Pfad der Pein östlich von hier postiert. Sie sind für diese teuflischen Stürme, die auf uns herabprasseln, verantwortlich. Sie und ihre Diener zu vernichten wäre ein schwerer Schlag für die Legion.",
    ["O"] = "Unteroffizier Altumus bei der Östlichen Versorgungskarawane östlich der Ehrenfeste auf der Höllenfeuerhalbinsel möchte, dass Ihr 4 Flammenschürerwichtel, 6 höllische Kriegshetzer und 1 Schreckensrufer tötet.",
    ["T"] = "Der Pfad der Pein",
  },
  [10143] = {
    ["D"] = "Eure Fähigkeiten in der Schlacht sind dem obersten Kommando aufgefallen, $C. Sie wollen Euch an die Front zu Rohamus\' Truppe schicken. Sie sind sehr radikal und haben sich der vollständigen Auslöschung der Legion verschrieben, also passt Ihr wahrscheinlich genau dazu...$B$BGeht etwa 400 Schritte nach Osten, durch das Tal der Knochen, bis Ihr auf einen zerstörten Teufelshäscher stoßt. Ihr findet den Expeditionsposten dort, wo einst ein Lager der Legion war. Wenn Ihr angekommen seid, meldet Euch bei Vorpostenkommandant Königstein.$B$BUnd jetzt weggetreten!",
    ["O"] = "Meldet euch bei Vorpostenkommandant Königstein im Expeditionsposten.",
    ["T"] = "Expeditionsposten",
  },
  [10144] = {
    ["D"] = "Direkt im Nordosten liegt die Front der Legion. Entlang der Front befinden sich die infernalen Portale, mit deren Hilfe Verstärkung auf die Höllenfeuerhalbinsel und in die ganze Scherbenwelt gebracht wird. Wir müssen diese Portale zerstören, $N!$B$BNehmt diesen Zünder. Er wurde in Area 52 hergestellt und benutzt die eigene Macht der Dämonen als Sprengstoff! Besiegt die Legion beim Portal Grimh und Portal Kaalez und haltet nach dämonischen Runensteinen Ausschau. Wenn Ihr genug Steine beisammen habt, benutzt den Zünder, um die Steine in den Portalen zu sprengen... und dann genießt das Feuerwerk!$B$BGeht, $N, und kehrt zu mir zurück, wenn Ihr Eure Aufgabe erfüllt habt.",
    ["O"] = "Besiegt die Feinde um Portal Grimh und Portal Kaalez herum, um dämonische Runensteine zu sammeln.$B$BBenutzt Königsteins Zünder in Portal Grimh und Portal Kaalez.$B$BKehrt danach mit dem Zünder zu Vorpostenkommandant Königstein zurück.",
    ["T"] = "Die Verstärkung abschneiden",
  },
  [10145] = {
    ["D"] = "Es wäre Vernachlässigung meiner Pflicht, wenn ich Euch nicht befehlen würde, die Dämonen an der Front der Legion, nördlich von dieser Basis, zu vernichten.$B$BBeide Seiten der Front sind nach dem selben Muster aufgebaut: Teufelstreiber wandeln zwischen den Lagern der Legion hin und her, die von Zornwächtern bewacht werden. Die ganze Operation wird von einer Unterwerferin der Shivan beaufsichtigt. Tötet sie alle und überlasst Kazzak den Rest.$B$BUnd passt auf die patrouillierenden Teufelshäscher auf. Ich weiß, dass Ihr stark seid, aber diese Biester machen mit Euch kurzen Prozess.",
    ["O"] = "Vorpostenkommandant Königstein beim Expeditionsposten auf de Höllenfeuerhalbinsel möchte, dass Ihr 4 Teufelstreiber, 8 Zornwächter und Unterwerferin Shi\'aziv tötet.",
    ["T"] = "Mission: Das Band zerreißen",
  },
  [10146] = {
    ["D"] = "Westlich des Dunklen Portals befinden sich zwei riesige Transporter: Tor Murketh und Tor Shaadraz.$B$BWenn Ihr den Pfad des Ruhmes nach Osten hinaufschaut, könnt Ihr sie an beiden Seiten der Straße erkennen. Ich möchte, dass Ihr sie in Stücke sprengt!$B$BHier sind die Bomben, die Ihr benutzen sollt. Sie wurden speziell von einem Goblin in Area 52 entworfen. Sprecht nun mit Schwadronskommandant Dabir\'ee - er wird Euch einen Greifen zur Verfügung stellen und Euch losschicken.",
    ["O"] = "Sprecht mit Schwadronskommandant Dabir\'ee, bombardiert Tor Murketh und Tor Shaadraz und kehrt dann zu Vorpostenkommandant Königstein im Häschersturz zurück.",
    ["T"] = "Mission: Tor Murketh und Tor Shaadraz",
  },
  [10147] = {
    ["D"] = "Euer Geschick bereitet der Legion große Schwierigkeiten, $N. Meine Späher haben mich informiert, dass die Legion Kuriere benutzt, um untereinander zu kommunizieren. Wir haben es geschafft, ihre Truppen vom Hauptlager abzuschneiden!$B$BWorum ich Euch nun bitte ist mehr, als ich von einem normalen Soldaten verlangen würde.$B$BDer Kurier der Legion reist zwischen den Teichen von Aggonar und dem Konstruktionsbasis: Mageddon, nördlich von hier, hin und her. Ihr müsst den Kurier bei Mageddon abfangen, ihn erledigen und mit dem Schriftwechsel der Legion zu mir zurückkehren.",
    ["O"] = "Vorpostenkommandant Königstein im Expeditionsposten auf der Höllenfeuerhalbinsel möchte, dass Ihr Arix\'maladish findet und tötet und den Schriftwechsel der Legion besorgt.",
    ["T"] = "Mission: Tötet den Boten",
  },
  [10148] = {
    ["D"] = "Ich bin nur ein Feldkommandeur, $C. Die Befugnis, auf solche Informationen hin zu handeln, fällt nur unserem Anführer, Truppenkommandant Danath, zu. Ihr müsst den Schriftwechsel zur Ehrenfeste zu ihm bringen. Reitet schnell wie der Wind, Soldat. Lasst Euch durch nichts aufhalten!",
    ["O"] = "Bringt den Schriftwechsel der Legion zu Truppenkommandant Danath Trollbann in der Ehrenfeste auf der Höllenfeuerhalbinsel.",
    ["T"] = "Mission: Seid der Bote",
  },
  [10149] = {
    ["D"] = "Wisst Ihr, was passiert wäre, wenn Ihr den Boten nicht abgefangen hättet, $N?$B$B<Danath nickt.>$B$BDie gesamte Armee der Legion des Höllenfeuers hätte vor unserer Tür gestanden. Einen solchen Angriff hätten wie niemals überlebt.$B$BEs gibt nur eine Möglichkeit zu verhindern, dass diese Befehle jemals die Front erreichen. Ihr müsst zu den Teichen von Aggonar, westlich von hier, reisen und diesen Arazzius töten.$B$BWenn Ihr das tut, werde ich Euch eine Auszeichnung zukommen lassen.",
    ["O"] = "Truppenkommandant Danath Trollbann in der Ehrenfeste auf der Höllenfeuerhalbinsel möchte, dass Ihr Arazzius den Grausamen tötet.",
    ["T"] = "Mission: Beendet alles, seid alles",
  },
  [10150] = {
    ["D"] = "Jeden Tag öffnen sich die Tore der Höllenfeuerzitadelle und noch mehr Höllenorcs strömen heraus und in die Lager, die sie entlang des Bollwerks südlich der Zitadelle errichtet haben.$B$BIch habe einen meiner besten Spurenleser, Späherin Makha, losgeschickt, um ihre Vorgehensweise zu erkunden, aber sie ist noch nicht mit ihrem Bericht zurückgekehrt.$B$BMakha sollte sich auf das nördlichste Lager entlang des Nördlichen Bollwerks, nahe bei seiner Verbindung zur Zitadelle, konzentrieren. Findet sie, $N. Ohne ihren Bericht sitzen wir hier hilflos herum.",
    ["O"] = "Durchsucht das nördlichste Lager entlang des Nördlichen Bollwerks nach einer Spur von Späherin Makha.",
    ["T"] = "Die Erstreckung der Zitadelle",
  },
  [10151] = {
    ["D"] = "Die Anweisungen sind mit Kriegsboss Nekrogg unterzeichnet. Sie befehlen den Höllenorcs in diesem Lager, ihren menschlichen Gefangenen zum Verhör nach Süden zu bringen.$B$BSchattenjäger Ty\'jin hat erwähnt, dass das Nördliche Bollwerk über mehrere Höllenorclager verfügt. Vielleicht haben sie Späherin Makha zum nächsten größeren Abschnitt des Walls Richtung Süden gebracht.$B$BDa dieser Befehl von Verhör spricht, scheint es den Höllenorcs wichtig zu sein, dass sie am Leben bleibt. Nehmt schnell ihre Spur auf, bevor sie es sich anders überlegen!",
    ["O"] = "Reist entlang der westlichen Seite des Nördlichen Bollwerks nach Süden und durchsucht das nächste Lager nach einer Spur von Späherin Makha.",
    ["T"] = "Kriegsboss Nekroggs Befehle",
  },
  [10152] = {
    ["D"] = "Zusätzlich zu den anderen Anweisungen werden die Orc in den Befehlen angewiesen, ihre Ausrüstung und Verpflegung für einen Angriff vorzubereiten.$B$BNach den Plänen sollen Kriegsboss Nekroggs Mannen sich zu einer westlichen Flanke formieren, um Thrallmar in die Zange zu nehmen.$B$BDa die Garnison der Feste sich auf die Verteidigung der östlichen Seite konzentriert, wäre ein Angriff von Westen fatal. Thrallmar kann nur gehalten werden, wenn der Angriff von Westen niemals stattfindet.",
    ["O"] = "Tötet 10 Wachen der Zerschmetterten Hand, 10 Berserker der Zerschmetterten Hand und 5 Akolythen der Zerschmetterten Hand und meldet Euch dann bei Steingardist Ambelan im Rückenbrecherposten in der Nähe von Zeth\'Gor.",
    ["T"] = "Die westliche Flanke",
  },
  [10153] = {
    ["D"] = "Die Spuren führen von Makhas Ausrüstung aus nach Süden, anscheinend über den Pfad des Ruhms. Aus den Furchen lässt sich schließen, dass die Entführer es eilig hatten, sie an den nächsten Ort zu bringen, aber warum?$B$BHat sie dort jemand erwartet? Vielleicht Kriegsboss Nekrogg, der Anführer, der in den Befehlen, die Ihr weiter nördlich gefunden habt, erwähnt wurde?$B$BDas Schild und die Spuren werfen mehr Fragen auf, als sie beantworten, aber es scheint sicher, dass Ihr dort, wo Ihr Späherin Makha findet, auch Kriegsboss Nekrogg finden werdet.",
    ["O"] = "Setzt Euren Weg über den Pfad des Ruhms fort und sucht nach dem Ort, an dem Kriegsboss Nekrogg Späherin Makha gefangen hält.",
    ["T"] = "Die Späherin Makha",
  },
  [10154] = {
    ["D"] = "Wie ich schon gesagt habe, hier rauszukommen wird nicht leicht. Kriegsboss Nekrogg mag vielleicht wie ein übergroßer Schläger aussehen, aber er ist kein Dummkopf.$B$BEr wird den Käfigschlüssel nicht aus den Augen lassen und kommt nicht aus seinem Turm auf dieser massiven Mauer heraus, bis er bereit ist, mich oder diese Orcfrau, die er gefangen hat, zu verhören.$B$BWir brauchen schon etwas Besonderes, um ihn aus diesen Mauern zu locken, aber ich habe einen Plan. Sucht einen der Grenadiere, die am Südlichen Bollwerk entlang patrouillieren, und bringt mir etwas von seinem Sprengstoff.",
    ["O"] = "Tötet einen Grenadier des Lachenden Schädels und bringt seine primitive Sprengladung zu Späherin Makha im Außenposten von Gor\'gaz.",
    ["T"] = "Fluchtplan",
  },
  [10155] = {
    ["D"] = "Der Erfolg des Plans hängt von der Qualität des Sprengstoffs ab...$B$BSo ungern ich mich auch auf etwas verlasse, das von einem Höllenorc hergestellt wurde, ist dies meine einzige Möglichkeit hier rauszukommen. Nehmt dies und werft es gegen das Banner, das vom Turm da oben weht.$B$BWenn es explodiert wird Kriegsboss Nekrogg es sich nicht nehmen lassen, ein Exempel an dem Störenfried zu statuieren. Tötet ihn und nehmt den Schlüssel, um meinen Käfig zu öffnen. Viel Erfolg, $N.",
    ["O"] = "Benutzt die primitive Sprengladung um einen Tumult zu veranstalten und Kriegsboss Nekrogg aus seinem Turm zu locken. Tötet ihn, besorgt Nekroggs Schlüssel und öffnet damit Späherin Makhas Käfig. Wenn Ihr Späherin Makha befreit habt, meldet Euch bei Steingardist Ambelan im Rückenbrecherposten in der Nähe von Zeth\'Gor.",
    ["T"] = "Den Kriegsboss provozieren",
  },
  [10156] = {
    ["D"] = "Eines ist klar, $N: wir können nicht so weitermachen. Die Höllenorcs haben uns eingekreist, und sobald wir sie erledigen, nehmen gleich mehrere ihren Platz ein. Wir können nicht hoffen, dass wir diesen Krieg gewinnen können, wenn unsere Streitkräfte zweigeteilt sind und sich im ständigen Kampf mit den Höllenorcs und den Dämonen der Legion befinden.$B$BIhr habt beeindruckende Arbeit geleistet und uns vor den schlimmsten Angriffen der Höllenorcs bewahrt, aber jetzt müsst Ihr Euch den Weg entlang des Pfads des Ruhmes freikämpfen und herausfinden, wer diese Angriffe steuert.",
    ["O"] = "Tötet 20 Grunzer der Zerschmetterten Hand, 10 Hexenmeister der Zerschmetterten Hand und besorgt Euch Drillmeister Zurok\'s Anweisungen. Kehrt dann zu Steingardist Ambelan im Rückenbrechergrat zurück.",
    ["T"] = "Auf wessen Befehl hin?",
  },
  [10157] = {
    ["D"] = "So wie ich das sehe, war Drillmeister Zurok die rechte Hand des Anführers der Höllenorcs auf der Höllenfeuerhalbinsel, Truppenkommandant Gorax.$B$BDieser Gorax ist anders als die gewöhnlichen Schläger der Höllenorcs. Ich bewundere fast, wie er uns schon so lange in der Defensive halten konnte.$B$BZuroks Befehle deuten an, dass Gorax\' Kommandozentrale am Ende des Pfads des Ruhms liegt, dort, wo die Mauern der Zitadelle auf das Nördliche Bollwerk treffen. Ich habe keine Ahnung, was Euch dort erwartet, $N, aber Ihr solltet besser mit ein paar Gefährten reisen.",
    ["O"] = "Tötet Truppenkommandant Gorax und meldet Euch dann bei Schattenjäger Ty\'jin in Thrallmar zurück.",
    ["T"] = "Die Beseitigung des Kommandanten",
  },
  [10158] = {
    ["D"] = "Die orcischen Ruinen von Zeth\'Gor im Südosten dienen als Basislager für die immer weiter steigende Flut von Höllenorcs, die aus der Höllenfeuerzitadelle strömt.$B$BSchattenjäger Ty\'jin hat mich geschickt, um den Außenposten und seine merkwürdigen Bewohner zu beobachten.$B$BEtwas hat diese Orcs auf eine Art und Weise verändert, die ich noch nie zuvor gesehen habe. Wir müssen alles in unserer Macht Stehende tun, um zu verhindern, dass sie noch stärker werden. Geht nach Zeth\'Gor und zerstört sämtliche Vorräte, die Ihr dort finden könnt.",
    ["O"] = "Zerstört 6 Stapel Vorräte des Blutenden Auges und meldet Euch dann bei Steingardist Ambelan im Rückenbrecherposten zurück.",
    ["T"] = "Vorräte des Blutenden Auges",
  },
  [10159] = {
    ["D"] = "Wir kämpfen schon die ganze Zeit, um den Pass durch den Dornnebelhügel von den Verheerern freizuhalten. Es wäre schlecht, wenn wir ganz von der Zuflucht in den Zangarmarschen abgeschnitten wären.$B$BWir möchten die Tiere nicht vertreiben, aber wir haben kaum einen andere Wahl, sie werden Reisenden in dieser Gegend gegenüber immer aggressiver.$B$BLöscht ein paar von den Felshetzern aus, wenn Ihr könnt.",
    ["O"] = "Tötet 8 Dornenfangfelshetzer und 8 Dornenfanggiftspucker und kehrt dann zu Mahuram Starkhuf im Posten des Cenarius auf der Höllenfeuerhalbinsel zurück.",
    ["T"] = "Haltet den Dornnebelhügel sauber!",
  },
  [10160] = {
    ["D"] = "Es gibt Gerüchte, dass eine neue Brut von Orcs aufkommt. Größer, bösartiger und listiger als die Rüpel, mit denen wir uns sonst herumschlagen müssen. Wenn diese Gerüchte wahr sind, müssen unsere Streitkräfte, die ohnehin schon überansprucht sind, sich bis über ihre Grenzen hinaus belasten.$B$BIch habe meinen Leutnant, Amadi, damit betraut, die Sache zu untersuchen. Wenn Ihr uns in dieser dunklen Stunde beistehen wollt, dann meldet Euch bei ihr. Sie befindet sich in dem zerbrochenen Turm im Westen der Feste.$B$BUnd $N, seid vorsichtig da draußen. Ihr stürzt Euch in eine neue, gefährliche Welt.",
    ["O"] = "Sprecht mit Leutnant Amadi im alten Wachturm im Westen der Ehrenfeste.",
    ["T"] = "Den Feind kennen",
  },
  [10161] = {
    ["D"] = "Bei einem Notfall ist es die Aufgabe des Piloten, einen kühlen Kopf zu bewahren, besonders, wenn die einzigen Befehle, die der Kopilot befolgt, die seines Magens sind.$B$BDie gute Nachricht ist, dass wir nicht tagelang darauf warten müssen, dass uns Ersatzteile geliefert werden. Die schlechte Nachricht ist, dass die Ersatzteile überall von hier bis zum Tal der Knochen im Osten verstreut sind.$B$BDie andere schlechte Nachricht? Die Ersatzteile sind nicht so leicht ersetzbar. Sie, äh, sind bei unserem schnellen Abgang abgefallen. Könntet Ihr mir vielleicht helfen, genügend davon wieder vom Boden aufzusammeln?",
    ["O"] = "Sammelt 30 Zeppelintrümmer und bringt sie zu \"Schreihals\" Quack Lufthans bei der Zeppelinabsturzstelle.",
    ["T"] = "Im Notfall...",
  },
  [10162] = {
    ["D"] = "Jetzt, da Ihr Eure Flügel habt, habt Ihr vielleicht Interesse an einem weiteren Bombardement?$B$BDieses Mal haben wir die Hauptproduktionsstätte der Legion isoliert, hinter den Konstruktionslagern im Norden. Schwingt Euch auf einen Wyvern und zerstört das verdammte Ding!$B$BHier sind die Bomben. Sprecht mit Schwadronskommandant Brack wenn Ihr zum Abflug bereit seid.",
    ["O"] = "Vorpostenkommandant To\'arch im Häschersturz auf der Höllenfeuerhalbinsel möchte, dass Ihr 20 Peons der Gan\'arg, 5 Vorarbeiter der Mo\'arg und 5 Teufelskanonen vernichtet.",
    ["T"] = "Mission: Die Abyssische Untiefe",
  },
  [10163] = {
    ["D"] = "Jetzt, da Ihr Eure Flügel habt, habt Ihr vielleicht Interesse an einem weiteren Bombardement?$B$BDieses Mal haben wir die Hauptproduktionsstätte der Legion isoliert, hinter den Konstruktionslagern im Norden. Schwingt Euch auf einen Greifen und zerstört das verdammte Ding!$B$BHier sind Eure Bomben. Sprecht mit Greifnerin Heulwind wenn Ihr zum Abflug bereit seid.",
    ["O"] = "Schwadronskommandant Greifgar im Trümmerposten auf der Höllenfeuerhalbinsel möchte, dass Ihr 20 Peons der Gan\'arg, 5 Vorarbeiter der Mo\'arg und 5 Teufelskanonen vernichtet.",
    ["T"] = "Mission: Die Abyssische Untiefe",
  },
  [10164] = {
    ["D"] = "Ihr habt ein ganz schönes Abenteuer hinter Euch, $N. Und das noch bevor Ihr überhaupt einen Fuß in den Auchenaikrypta gesetzt habt.$B$BIch bin einer der glücklichen Geister. Ich war nie an Levixus gebunden oder Teil eines der bösartigen Experimente in der Nekromantie von Exarch Maladaar. Während Levixus durch Eure Hand gestorben ist, lebt Maladaar immer noch weiter und quält die Geister in der Krypta.$B$BWorum ich Euch bitte ist ganz einfach. Geht in die Auchenaikrypta und tötet Exarch Maladaar, damit die gefangenen Geister endlich Frieden finden können.",
    ["O"] = "Großvater Aldrimus außerhalb der Auchenaikrypta von Auchindoun möchte, dass Ihr in die Auchenaikrypta geht und Exarch Maladaar vernichtet.",
    ["T"] = "Alles wird gut",
  },
  [10165] = {
    ["D"] = "Dieser Shaffar ist ganz schön gerissen, das muss man ihm lassen... Er hat es geschafft, das Konsortium fern zu halten, während er in aller Seelenruhe diese Gruft all ihrer Schätze beraubt. Ich glaube nicht, dass diese Dummköpfe es tatsächlich schaffen, durch seine Verteidigung zu brechen, deshalb mache ich Euch einen Vorschlag, $R.$B$BDringt in die Managruft ein und tötet den Nexusprinzen Shaffar. Bringt mir seine Körperbandagen als Beweis Eurer Tat, und ich werde Euch fürstlich belohnen.",
    ["O"] = "Nexusprinz Haramad außerhalb der Managruft möchte, dass Ihr Nexusprinz Shaffar tötet und ihm Shaffars Bandagen bringt.",
    ["T"] = "Die Konkurrenz untergraben",
  },
  [10166] = {
    ["D"] = "Dieser Anhänger bringt Erinnerungen an eine andere Zeit zurück. Eine bessere Zeit.$B$BLeider sind Erinnerungen das Letzte, was ich jetzt gebrauchen kann.$B$BIch möchte, dass Ihr dies beim Runenstein im Versengten Hain vergrabt. Dort habe ich den Treantältesten zum ersten Mal getroffen. Bettet die Erinnerung an meinen alten Freund in Frieden.",
    ["O"] = "Larianne Flusswind möchte, dass Ihr Weißborkes Anhänger beim Runenstein im Versengten Hain vergrabt.",
    ["T"] = "Weißborkes Erinnerung",
  },
  [10167] = {
    ["D"] = "Tief in der Auchenaikrypta befindet sich ein Grab: ein Grab, dass keinesgleichen in den Zerschlagenen Weiten findet. Aus diesem Grab, dem ersten Grab, wurde das riesige Mausoleum von Auchindoun geboren.$B$BIhr müsst zu der Auchenaikrypta reisen und dieses Grab finden. Tötet den Grabwächter und befreit den Geist, der darin gefangen ist. Der Geist ist der Schlüssel...",
    ["O"] = "Reist zur Auchenaikrypta in der Knochenwüste der Wälder von Terokkar und tötet Exarch Maladaar um den Geist von D\'ore zu befreien.",
    ["T"] = "Auchindoun...",
  },
  [10168] = {
    ["D"] = "Seit mehreren Jahrhunderten verbinden sich die Geister von Auchindoun nun mit meiner getrübten Essenz. Viele der Katastrophen, die diese Krypta heimgesucht haben, sind auf meinen geschwächten Zustand zurückzuführen.$B$BUnglücklicherweise kann nichts diesen Kreis aufhalten. Das gehört zur Natur der Naaru - ohne die Leere kann das Licht nicht existieren.$B$BEure einzige Möglichkeit besteht darin, die Geister von Nagrand zu vernichten, bevor sie sich in Wesen der Leere verwandeln. Benutzt die Seelenspiegel, die sich überall in der Krypta befinden, um sie zu befreien...",
    ["O"] = "Sucht einen Seelenspiegel in der Auchenaikrypta und benutzt ihn, um einen getrübten Geist eines uralten Orcahnen zu rufen. Zerstört 15 getrübte Geister, damit die Ahnen in Frieden ruhen können.$B$BKehrt zu Mutter Kashur im Ahnengrund zurück, wenn Ihr fertig seid.",
    ["T"] = "Was die Seele sieht",
  },
  [10169] = {
    ["D"] = "Die Aldor haben sich heute das Wohlwollen der Sha\'tar verdient. Als ob das etwas Überraschendes wäre!$B$BSchaut nur, wie sie nur ein paar Fuß von A\'dal entfernt mit ihrer tollen Beinarbeit angeben. Könnten sie sich wohl noch mehr bemühen, sich bei den Naaru einzuschmeicheln?$B$BHerold Haronems Schützen sind so engagiert, dass sie sogar ihren eigenen Kochtopf mitbringen, um keine langen Pausen zum Mittagessen machen müssen. Das kann nicht ohne Strafe vonstatten gehen, $N.$B$BIch möchte, dass Ihr diese Phiole in ihr Essen kippt. Die Folgen sollten... sehr amüsant sein.",
    ["O"] = "Magister Falris in Shattrath möchte, dass Ihr die Phiole mit Beruhigungsserum zur Terrasse des Lichts bringt und sie in den Kochtopf von Haronems Regiment schüttet.",
    ["T"] = "In Würde verlieren",
  },
  [10170] = {
    ["D"] = "Kehrt zu Großmutter Geyah in Garadar zurück und berichtet ihr von Eurem Sieg, $N. Das wird den Mag\'har sicher neuen Mut geben. Vielleicht erwacht sogar der junge Höllschrei aus seiner Melancholie.",
    ["O"] = "Kehrt nach Garadar zurück und sprecht mit Großmutter Geyah.",
    ["T"] = "Rückkehr zur Großmutter",
  },
  [10171] = {
    ["D"] = "Ihr habt so viel für die Mag\'har getan, $N. Alle wissen von Euren Taten, auch Garrosh. Geht nun zu ihm und erzählt ihm von eurem letzten Erfolg. Sagt ihm, was Ihr für ihn und sein Volk getan habt. Eure Worte werden ihn sicher trösten.",
    ["O"] = "Großmutter Geyah in Garadar möchte, dass Ihr mit Garrosh sprecht.",
    ["T"] = "Der untröstliche Häuptling",
  },
  [10172] = {
    ["D"] = "Ihr seid $Gein ehrenwerter:eine ehrenwerte:r; $R, $N. Ihr habt viel für die Mag\'har getan. Niemand könnte Eure Dienste für mein Volk je leugnen. Dennoch ist die Zeit der Mag\'har abgelaufen. Ihr habt mir mehr als alles andere gezeigt, dass ich nicht in der Lage bin, diese Leute anzuführen. Zu stark ist der Fluch meines Blutes. Ich werde nicht... ich kann nicht der nächste Höllschrei sein, der die Orcs in die Verdammnis führt.$B$BBitte, $N, kehrt zur Großmutter zurück und erklärt ihr, was ich Euch gesagt habe. Ich schäme mich zu sehr, um zu ihr zu gehen... ihr in die Augen zu schauen.",
    ["O"] = "Sprecht mit Großmutter Geyah und erzählt ihr, was Garrosh Euch gesagt hat.",
    ["T"] = "Es gibt keine Hoffnung",
  },
  [10173] = {
    ["D"] = "Erlaubt, dass ich mich vorstelle, $C.$B$BMein Name ist Ravandwyr, Lehrling des Erzmagiers Vargoth.$B$BMein Meister ist... nennen wir es indisponiert. Ich bin hier in einer Angelegenheit von höchster Wichtigkeit.$B$BDie Ruinen von Arklon südöstlich der Stadt sind eine Brutstätte für die Aktivitäten der Brennenden Legion. Was sie mit den Ruinen vorhaben interessiert weder mich noch meinen Meister. Aber ihre Anführerin, Ekkorash, hat den Stab meines Meisters.$B$BMit diesem Beschwörungspulver könnt Ihr die Dämonin aus ihrem Versteck locken. Streut es über die Kohlenpfanne in der Mitte der Ruinen.",
    ["O"] = "Besorgt Erzmagier Vargoths Stab von Ekkorash der Inquisitorin und bringt ihn zu Ravandwyr in Area 52. Um Ekkorash aus ihrem Versteck hervorzulocken, streut das Beschwörungspulver auf die Kohlenpfanne in der Mitte der Ruinen.",
    ["T"] = "Der Stab des Erzmagiers",
  },
  [10174] = {
    ["D"] = "Jetzt könnt Ihr mithilfe des Stabs Kontakt zu dem Erzmagier aufnehmen.$B$BEr lebt im Violetten Turm, auf einer Insel im Süden. Der verfluchte Turm ist sowohl eine Zuflucht als auch ein Gefängnis für ihn. Mitglieder und Verbündete der Kirin Tor können weder hinein noch hinaus, weswegen ich meinen Meister nicht sehen kann.$B$BDer Stab kann die Verteidigung des Turms durchbrechen, jedoch nur, wenn er von jemandem, der nicht zu den Kirin Tor gehört, aktiviert wird.$B$BNehmt den Stab und benutzt ihn, um ein Abbild des Erzmagiers zu erschaffen. Sagt ihm, was passiert ist und beweist ihm, dass ich ihn nicht ganz enttäuscht habe.",
    ["O"] = "Benutzt Erzmagier Vargoths Stab um Kontakt mit Erzmagier Vargoth aufzunehmen.$B$BFalls Ihr Erzmagier Vargoths Stab verlieren solltet, bittet Ravandwyr in Area 52 um Ersatz.",
    ["T"] = "Der Fluch des Violetten Turms",
  },
  [10175] = {
    ["D"] = "Kehrt in Eure Welt zurück, $N. Sucht meinen Enkelsohn und sagt ihm, dass sein Blut hier ist - in dieser zerschlagenen Welt.$B$BSagt ihm, dass das Erbe seines Vaters weiterlebt.$B$BSagt ihm alles...",
    ["O"] = "Reist nach Orgrimmar und überbringt Thrall Geyahs Nachricht.",
    ["T"] = "Thrall, Sohn Durotans",
  },
  [10176] = {
    ["D"] = "Jetzt, da Ihr alle Gegenstände, die ich benötige, um Kael\'thas Fluch zu brechen, gesammelt habt, gibt es nur noch Eines zu tun. Als Kael\'thas den Turm versiegelt hat, hat er einen arkanen Golem, der als Ar\'kelos bekannt ist, auf dem Grund des Turms platziert.$B$BIhr müsst Ar\'kelos besiegen, damit ich innerhalb des Turms wieder meine Magie wirken kann.$B$BWenn Ihr den Violetten Turm betretet, wird der Fluch Euch treffen.$B$BSobald Ihr Ar\'kelos besiegt habt, steigt den Turm hinauf und unterstützt mich bei den Vorbereitungen für das Ritual.",
    ["O"] = "Betretet den Violetten Turm in der Mitte von Kirin\'Var und besiegt Ar\'kelos. Sprecht danach mit Erzmagier Vargoth.",
    ["T"] = "Arkelos der Wächter",
  },
  [10177] = {
    ["D"] = "Meine Spione in Auchindoun haben uns erschreckende Berichte geschickt. Seit seiner Explosion vor ein paar Jahren hat dieser Ort nichts als Ärger gebracht.$B$BDort ist etwas - mal wieder - ganz schön schief gegangen, und der Schattenrat bereitet sich auf einen Krieg vor. Auch wenn die Berichte viele Details verschweigen, müssen sie sich auf einen Krieg im Inneren vorbereiten.$B$BIhr müsst Euch zum Eingang des Schattenlabyrinths begeben. Mein Feldkommandeur, Mahfuun, wird Euch genau erklären, was zu tun ist.",
    ["O"] = "Meldet Euch bei Feldkommandeur Mahfuun beim Eingang zum Schattenlabyrinth in Auchindoun in den Wäldern von Terokkar.",
    ["T"] = "Ärger in Auchindoun",
  },
  [10178] = {
    ["D"] = "To\'gun, dieser Narr! Wir können hier kaum die Angriffe zurückhalten und er geht wieder hinein um Informationen zu sammeln und die anderen Spione zu retten!$B$BIch weiß nicht, ob ich ihn umarmen oder töten soll!$B$BDer Schattenrat muss uns mithilfe von Magie ausfindig gemacht haben, denn all unsere Spionen mussten hastig von dort fliehen. Die meisten von uns haben es nicht geschafft.$B$BBitte, wenn Ihr mutig genug seid, um das Schattenlabyrinth zu betreten, könntet Ihr dann nach To\'gun Ausschau halten? Er ist schon eine ganze Weile fort, und ich befürchte das Schlimmste.",
    ["O"] = "Findet Spion To\'gun im Schattenlabyrinth von Auchindoun.",
    ["T"] = "Findet Spion To\'gun",
  },
  [10179] = {
    ["D"] = "Als klar wurde, dass unsere Expedition ohne Hoffnung auf Rückkehr nach Azeroth in der Scherbenwelt gefangen war, reisten die Kirin Tor zu den Ebenen von Farahlon und erbauten Kirin\'Var. Ich habe die Verwaltung des Dorfs Hüter Dierwert übertragen, aber als Kael\'thas und seine Streitkräfte mich hier im Violetten Turm einsperrten, ist mein Kontakt zu ihm abgebrochen.$B$BSucht ihn in Kirin\'Var und helft ihm, wo Ihr nur könnt, $N. Ich bin sicher, dass er seit dem Angriff alle Hände voll zu tun hat.",
    ["O"] = "Speak with Custodian Dierworth in Kirin\'Var Village.",
    ["T"] = "Der Hüter von Kirin\'Var",
  },
  [10180] = {
    ["D"] = "Ich habe versucht, Isfar davon zu überzeugen, hier in den sicheren Mauern von Shattrath zu bleiben, aber er wollte nicht auf mich hören. Stattdessen hat er sich entschlossen, wieder auf eigene Faust nach Auchindoun zu gehen.$B$BEr hatte zwar das Glück, den Sethekkhallen einmal zu entkommen, aber wenn sie ihn erwischen wird es kein zweites Mal geben.$B$BIch bewundere seine Entschlossenheit, seine Schwester zu befreien, aber ohne Hilfe wird er es nicht schaffen.$B$BFindet Isfar und sprecht mit ihm, $N. Wenn Ihr ihn nicht davon überzeugen könnt, zurückzukommen, könntet Ihr ihm dann vielleicht helfen Lakka zu retten?",
    ["O"] = "Sprecht mit Isfar in den Sethekkhallen in Auchindoun.",
    ["T"] = "Kann es einfach nicht lassen",
  },
  [10182] = {
    ["D"] = "Das Schwert ist von außergewöhnlicher Qualität, fein geschmiedet und perfekt für einen Kampfmagier ausbalanciert. Auf dem Knauf der Waffe sind das Siegel der Kirin Tor und der Name \"Dathric\" eingraviert.$B$BWährend Ihr die Waffe in den Händen hin und her dreht, bemerkt Ihr, dass etwas mit dem Schwert nicht stimmt. Es fühlt sich an, als wäre es zwischen zwei Welten hin- und hergerissen. Könnte dies der Gegenstand sein, der Kampfmagier Dathrics Geist an diese Welt bindet?$B$BVielleicht zerbricht das Band, wenn Ihr es an seinen angestammten Platz zurückbringt, und befreit Dathrics Geist endgültig.",
    ["O"] = "Sucht die Wohnung von Kampfmagier Dathric in der Zaubergasse und bringt die Klinge zurück in sein Waffenregal. Sprecht danach mit Hüter Dierwert in Kirin\'Var.",
    ["T"] = "Kampfmagier Dathric",
  },
  [10183] = {
    ["D"] = "Ich studiere hier draußen den Nether - die astralen Strömungen und so weiter. Dies ist ein idealer Platz, weit weg von den Lichtern und dem Getöse der Stadt. Wenn ich diesen Schrott doch nur am Laufen halten könnte!$B$BHey, wisst Ihr, wenn Ihr nach Arbeit sucht, solltet Ihr nach Area 52 gehen! Das liegt gleich den Pfad hoch und bei der Weggabelung rechts.$B$BWarum bringt Ihr nicht einfach meine neusten Forschungsnotizen zu Raketenchef Doppeldecker? Ich bin sicher, er hat genug Arbeit für Euch, wegen des Raketenschiffs X-52 und so.",
    ["O"] = "Bringt die Notizen des Netheorologen zu Raketenchef Doppeldecker in Area 52 im Nethersturm.",
    ["T"] = "Auf nach Area 52",
  },
  [10184] = {
    ["D"] = "Die meisten Geister im Dorf sind nicht mehr als blasse Überbleibsel der Magier der Kirin Tor, die sie einst waren. Sie erinnern sich nicht mehr an ihr früheres Ich und streunen blind vor Wut um die Ruinen herum.$B$BDas war jedoch nicht immer so. Direkt nach dem Angriff waren die Geister zwar rastlos, aber nicht bösartig. Mit der Zeit sind sie jedoch immer aggressiver geworden. Ich traue mich nicht, in den südlichen Teil des Dorfs zu gehen, denn sie machen nicht einmal vor mir Halt. Beruhigt sie und lasst uns hoffen, dass Ihr Ärger dann verfliegt.",
    ["O"] = "Hüter Dierwert in Kirin\'Var möchte, dass Ihr 20 abtrünnige Geister auf dem Ratsplatz tötet.",
    ["T"] = "Bösartige Überbleibsel",
  },
  [10185] = {
    ["D"] = "Nachdem die Blutelfen Kirin\'Var dem Erdboden gleichgemacht hatten, haben sie mit dem Bau eines großen Gebäudes im Nordwesten begonnen. Ohne die Kräfte des Erzmagiers können wir nicht genau sagen, was dort vor sich geht.$B$BEines ist jedoch klar. Die Anwesenheit der Blutelfen hat eine Flut von gefräßigen Manaelementaren entfesselt. Sie scheinen von den Ruinen der Zaubergasse angelockt zu werden und verursachen noch weitere Zerstörung... Wir können das nicht weiter zulassen.",
    ["O"] = "Tötet 10 Magiertöter und 10 Manasucher und kehrt zu Hüter Dierwert in Kirin\'Var zurück.",
    ["T"] = "Schlimmer als der Tod",
  },
  [10186] = {
    ["D"] = "Lasst mich der Erste sein, der Euch an Bord willkommen heißt, Juniortechniker dritten Grades $N!$B$BWir bauen hier draußen mein Meisterwerk - die X-52 Netherrakete!$B$BDoch zuerst müssen wir alle Teile und den Treibstoff für sie besorgen. Und da kommt Ihr ins Spiel.$B$BZuerst brauche ich Kristalle, VIELE Kristalle, für die astrallithische Antriebsmatrix. Die Draenei hatten so viele, dass sie ihnen schon zu den Ohren herauswuchsen!$B$BBegebt Euch also zu den Ruinen von Enkaat und sammelt ein paar für mich! Und kümmert Euch nicht um die Draeneigeister.",
    ["O"] = "Sammelt 10 Astrallithiummatrixkristalle und kehrt dann zu Raketenchef Doppeldecker in Area 52 im Nethersturm zurück.",
    ["T"] = "Ihr seid eingestellt!",
  },
  [10187] = {
    ["D"] = "Auch wenn es hier sehr ungemütlich ist, muntert es uns ein wenig auf, dass der Erzmagier die Misere des Dorfs nicht vergessen hat.$B$B<Der Hüter schaut zum Turm hinauf.>$B$BEs ist lächerlich, oder? Manchmal scheint es, als trenne uns nur eine Wand aus einfachem Stein. Wie leicht vergisst man die Wand aus Magie.$B$BWenn Ihr so nett wärt, erklimmt den Turm und sagt Erzmagier Vargoth, dass wir Fortschritte im Dorf machen und bald einen Weg finden werden, um die Manakreaturen fernzuhalten.",
    ["O"] = "Speak to Archmage Vargoth at the top of the Violet Tower.",
    ["T"] = "Eine Nachricht für den Erzmagier",
  },
  [10188] = {
    ["D"] = "Bevor wir uns vor so vielen Jahren von Azeroth aus aufmachten, haben die Kirin Tor unserer Expedition ein mächtiges Artefakt anvertraut, das Siegel von Krasus. Es hat uns im Kampf gegen die Orcs große Dienste erwiesen und könnte eine wichtige Rolle beim Brechen des Zaubers, der mich gefangen hält, spielen.$B$BAls Kael\'thas mich jedoch meiner Besitztümer beraubt hat, gab er das Siegel an Zauberbinderin Maryana, die mit der Garnison zurückblieb. Hätte er gewusst, was er in seinem Besitz hatte, hätte er es nicht so einfach weggegeben.",
    ["O"] = "Retrieve the Sigil of Krasus from Spellbinder Maryana at Manaforge Coruu, northwest of the Violet Tower. When you have gained the sigil, use Archmage Vargoth\'s Staff to project an Image of Archmage Vargoth and inform him of your progress.$B$BIf you lose Archmage Vargoth\'s Staff, speak to Ravandwyr at Area 52 to receive a replacement.",
    ["T"] = "Das Siegel von Krasus",
  },
  [10189] = {
    ["D"] = "Vielleicht sind Euch Kaels Manaschmieden auf dem Weg hierher aufgefallen. Das sind diese großen violetten Gebäude, die Energie aus dem Himmel saugen.$B$BWir wurden von den Sha\'tar hierher geschickt, um sie zu erkunden. Ihr könnt uns dabei helfen, indem Ihr eine Stammrolle von dort besorgt. Wenn wir eine Vorstellung davon hätten, wer dort als was arbeitet, wäre uns über alle Maßen geholfen.$B$BWir glauben, dass Hauptmann Arathyn, der eine Ballistentruppe direkt außerhalb der Manaschmiede B\'naar leitet, eine solche Rolle besitzen könnte. Holt sie für uns.",
    ["O"] = "Spionagemeister Thalodien in Area 52 möchte, dass Ihr zum Rand der Manaschmiede B\'naar geht und die Stammrolle von B\'naar von Hauptmann Arathyn besorgt.$B$BDas Abschließen von Aufgaben für die Seher führt zu einer Verringerung Eures Rufs bei den Aldor.",
    ["T"] = "Manaschmiede B\'naar",
  },
  [10190] = {
    ["D"] = "Ich habe die wichtige Aufgabe, die Maschinerie in diesen Ruinen zu sammeln, aber ich habe ein paar Probleme damit, alle meine Bots am Laufen zu halten.$B$BDie Phasenjäger - diese wurmähnlichen Dinger - stecken voller Energie! Man kann sie überall finden, wie sie aus dem Nether herausströmen.$B$BFüllt die Batterie mit diesem Ladeblaster, indem Ihr ihn bei einem Phasenjäger benutzt, wenn er sehr schwach ist, und ihm dann die restliche Energie entzieht.",
    ["O"] = "Benutzt den Batterieladeblaster bei genügend Phasenjägern, um den Batterieladestatus auf 10 zu bringen. Bringt die Batterie danach zu Bot-Spezialistin Alley in den Ruinen von Enkaat im Nethersturm zurück.",
    ["T"] = "Batterien aufladen",
  },
  [10191] = {
    ["D"] = "Grüße, $R. Ich bin aufgeladen und voll einsatzfähig!$B$BIn den Ruinen gibt es ein paar zerbrechliche Draeneimaschinen, die ich wieder beschaffen muss. Ich habe sehr edle Servos in den Stellgliedern meiner Handgelenke und bin mit den neusten verbrennungspneumatischen Beulensaugern ausgestattet. Die Maschinen mit der nötigen Sorgfalt zu behandeln ist also kein Problem für mich.$B$BLeider werden die Geister, die in den Ruinen spuken, mich sehr wahrscheinlich angreifen, daher brauche ich Euch und Eure Freunde als Beschützer.",
    ["O"] = "Begleitet Maxx E. Million Nr. V durch die Ruinen von Enkaat und wieder in Sicherheit. Sprecht dann mit Bot-Spezialistin Alley außerhalb der Ruinen von Enkaat im Nethersturm.",
    ["T"] = "Nummer V lebt!",
  },
  [10192] = {
    ["D"] = "Die Kirin Tor haben unserer Expedition einen weiteren Gegenstand aus Krasus\' Besitz mitgegeben. Es handelt sich dabei um sein Zauberbuch, das Krasus\' Kompendium genannt wird. Da es alle Zauber enthält, die Krasus beherrschte, war es uns eine unersetzliche Hilfe im Krieg.$B$BBevor Kael\'thas und seine Truppen Kirin\'Var besetzt haben, wurde mir das Buch von einem rebellischen Schüler, der begierig darauf war, die Nekromantie zu erlernen, gestohlen. Der Lehrling und seine Anhänger haben sich aufgeteilt und das Buch heimlich studiert, wobei sie sich in ihren Häusern im Süden der Stadt eingeschlossen haben.",
    ["O"] = "Reclaim Krasus\'s Compendium - Chapter 1, Krasus\'s Compendium - Chapter 2, and Krasus\'s Compendium - Chapter 3 from Kirin\'Var Village\'s Town Square. Use Archmage Vargoth\'s Staff to contact the archmage once you have the chapters.$B$BIf you lose Archmage Vargoth\'s Staff, speak to Ravandwyr at Area 52 to receive a replacement.",
    ["T"] = "Krasus\' Kompendium",
  },
  [10193] = {
    ["D"] = "Von den Informationen aus der Stammrolle wissen wir nun, dass die geistigen Führer der Manaschmieden die Warpmeister und Warpingenieure sind. Sie überwachen die Gewinnung der so genannten \"Warpenergie\" in den Manaschmieden und kümmern sich um ihre Lieferung an entfernte Orte über ein unterirdisches Rohrsystem.$B$BDie einzigen weiteren Spezialisten in dieser Gegend sind die Geologen. Alle anderen sind entweder Teil des Militärs oder Magister - beide sehr leicht zu ersetzen.$B$BGeht dort hinein und tötet Kaels Spezialisten. Das wird ihn am meisten treffen.",
    ["O"] = "Spionagemeister Thalodien in Area 52 möchte, dass Ihr in die Manaschmiede B\'naar geht und 2 Warpmeister des Sonnenzorns, 6 Warpingenieure des Sonnenzorns und 8 Geologen des Sonnenzorns tötet.$B$BDas Abschließen von Quests für die Seher führt zu einer Verringerung Eures Rufs bei den Aldor.",
    ["T"] = "Hochrangige Ziele",
  },
  [10194] = {
    ["D"] = "Während Ihr weg wart, haben meine Männer eine weitere Manaschmiede entdeckt. Sie befindet sich östlich von hier und ist als Manaschmiede Coruu bekannt.$B$BZwei meiner besten Spione haben einen geheimen Landeplatz außerhalb der Manaschmiede eingerichtet. Ihr sollt ihnen helfen, Kaels Truppen zu unterwandern und so viele Informationen wie möglich zu sammeln.$B$BSprecht mit Veronia außerhalb der Stadtmauer. Sie hat einen Windreiter vorbereitet, der Euch zur Manaschmiede Coruu bringen wird. Wenn Ihr dort seid, meldet Euch bei Caldis Morgenhell. Er wird Euch mit Eurer Mission vertraut machen.",
    ["O"] = "Sprecht mit Veronia außerhalb von Area 52 und fliegt zur Manaschmiede Coruu.$B$BDas Abschließen von Quests für die Seher führt zu einer Verringerung Eures Rufs bei den Aldor.",
    ["T"] = "Heimlicher Flug",
  },
  [10195] = {
    ["D"] = "Seit Ihr den ersten Stapel Feder gebracht und Bertelms Hut fertiggestellt habt, wollen alle anderen Söldner auch einen haben, darunter auch Thander. Ich habe ihnen gesagt, dass ich mich gleich darum kümmern werde, aber ich werde mehr Arakkoafedern dafür brauchen.$B$BWenn Ihr auf Euren Abenteuern in den Wäldern noch mehr davon finden solltet, nehme ich sie Euch gerne ab.",
    ["O"] = "Bringt 30 Arakkoafedern zu Ros\'eleth in Allerias Feste.",
    ["T"] = "Söldner sieht, Söldner handelt",
  },
  [10196] = {
    ["D"] = "Ich habe erste Erfolge darin, die Arakkoageister näher zu mir zu locken, aber sie sind immer noch sehr schüchtern. Vielleicht trauen sie sich näher, wenn ich meine Verkleidung verbessere.$B$BDazu benötige ich jedoch mehr Arakkoafedern wie die, die Ihr mir bereits gebracht habt. Wenn Ihr noch ein paar Federn bekommen könntet, würde ich sie Euch gerne abnehmen.",
    ["O"] = "Bringt 30 Arakkoafedern zu Malukaz in der Steinbrecherfeste.",
    ["T"] = "Mehr Arakkoafedern",
  },
  [10197] = {
    ["D"] = "Die Reihen von Kaels Armee zu unterwandern wird keine leichte Aufgabe. Mit meinen geringen Künsten der Illusion könnte ich einen von uns wie einen Anhänger Kaels aussehen lassen, aber es gibt einige Details in ihrer Uniform, die ich nicht nachbilden kann.$B$BBringt mir ein Set ihrer Roben, ein Paar ihrer Handschuhe und ein Gardistenmedaillon des Sonnenzorns. Das wird die Ergebnisse meiner Magie überzeugender machen. Sie tragen alle das gleiche, also solltet Ihr keine Probleme haben, diese Gegenstände zu finden.",
    ["O"] = "Bringt ein Set Roben des Sonnenzorns, ein Paar Handschuhe des Sonnenzorns und ein Gardistenmedaillon des Sonnenzorns zu Caledis Morgenhell bei der Manaschmiede Coruu im Nethersturm.$B$BDas Abschließen von Quests für die Seher führt zu einer Verringerung Eures Rufs bei den Aldor.",
    ["T"] = "Eine überzeugende Verkleidung",
  },
  [10198] = {
    ["D"] = "Ich habe aus den Teilen, die Ihr mir besorgt habt, eine Uniform hergestellt. Sie ist magisch verbessert, um die Illusion perfekt zu machen.$B$BWir haben beobachtet, wie Kommandant Morgenschmied vor ein paar Minuten hineingegangen ist, um Arkanist Ardonis zu treffen. Er hatte sein gesamtes Gefolge an Wachen bei sich, daher ist das, was sie zu besprechen haben, sicher von größter Wichtigkeit. Versucht, etwas darüber herauszufinden.$B$BAch, das hätte ich fast vergessen. Haltet Euch von den arkanen Golems fern, die in der Manaschmiede patrouillieren. Sie werden Eure Illusion mühelos durchschauen.",
    ["O"] = "Use the Sunfury Disguise, go into Manaforge Coruu and listen to the conversation between Commander Dawnforge and Arcanist Ardonis.  Report back to Caledis Brightdawn after completing this task.$B$BCompleting tasks for the Scryers will cause your Aldor reputation level to decrease.",
    ["T"] = "Informationen gesammelt",
  },
  [10199] = {
    ["D"] = "So, nun fehlt mir noch die eine Zutat, die meinem Gebräu den entscheidenden kleinen Kick geben sollte, den wir brauchen.$B$BDer Stich der Netherrochen, die sich in der Nähe der Schieferhautschinder herumtreiben, ist ganz schön heftig. Bringt mir ein paar ihrer Stachel, damit ich das Gift aus ihnen gewinnen kann.$B$BIch bin mir sicher, dass die Ergebnisse explosiv sind!",
    ["O"] = "Besorgt 5 Netherrochenstachel und bringt sie zu Stiefels in Area 52 im Nethersturm.",
    ["T"] = "Der kleine Extrakick",
  },
  [10200] = {
    ["D"] = "Es wäre besser, wenn diese Information so schnell wie möglich zu Thalodien gelangen würde. Ich bleibe hier und verwische unsere Spuren.$B$BEs wäre alles andere als gut, wenn Kael\'thas herausfinden würde, was wir hier getan haben. Geheime Informationen sind immer am besten, wenn der Feind nicht weiß, dass man sie hat.",
    ["O"] = "Kehrt zu Spionagemeister Thalodien in Area 52 im Nethersturm zurück.",
    ["T"] = "Rückkehr zu Thalodien",
  },
  [10201] = {
    ["D"] = "Das Öl ist endlich für einen ersten Test bereit. Es scheint dickflüssig genug zu sein, um als hochwertiges Waffenöl zu dienen.$B$BDer einzige Weg, dies herauszufinden ist, einen Profi zu fragen. Seht Ihr Grek da drüben bei den Fahndungsanschlägen? Ich weiß, er ist nicht gerade ein Poet, aber mit Waffen kennt sich der Orc aus!$B$BBringt ihm eine Probe des Öl und bittet ihn, es an seiner Waffe auszuprobieren. Wenn er das getan hat, berichtet mir von seiner Meinung.",
    ["O"] = "Geht zu Grek und bittet ihn, das Öl an seiner Waffe auszuprobieren. Bringt das Ergebnis zu Rakoria, wenn er fertig ist.",
    ["T"] = "Der Moment der Wahrheit",
  },
  [10202] = {
    ["D"] = "Wir wurden vor Kurzem von einem Magister des Sonnenzorns kontaktiert, der uns beitreten möchte und schwört, dass er uns Informationen geben kann, die wir dringend benötigen. Wir haben uns mit unseren Informanten in Verbindung gesetzt, um zu überprüfen, ob es sich bei ihm um einen Doppelagenten handeln könnte, und er scheint sauber zu sein.$B$BEr befindet sich in der Manaschmiede Ara, was die Kommunikation etwas erschwert. Geht zu ihm und hört Euch an, was er zu sagen hat. Wenn er nur halb so hilfreich ist, wie er behauptet, können wir ihn sicher gut einsetzen.",
    ["O"] = "Geht zur Manaschmiede Ara und sprecht mit Magister Theledorn.$B$BDas Abschließen von Quests für die Seher führt zu einer Verringerung Eures Rufs bei den Aldor.",
    ["T"] = "Ein Abtrünniger",
  },
  [10203] = {
    ["D"] = "Habt Ihr den Riss gesehen, der mitten durch Area 52 verläuft? Wir waren das! <Grinsen>$B$BIm Riss befinden sich allerlei Schätze für die X-52 Netherrakete! Das einzige Problem ist nur, dass wir nicht genug Ausrüstung haben.$B$BBis die Brennende Legion dort aufgetaucht ist, führten wir eine Operation in den Ruinen von Arklon aus. Ich möchte, dass Ihr diesen ultrastarken Dekonsolidierungsstrahler zu den Ruinen bringt. Benutzt ihn, um unsere Ausrüstung zum leitenden Ingenieur Zischeldruck im Riss zu beamen.$B$BJuniortechniker dritten Grades $N, dies ist Eure Chance auf eine Beförderung. Vergeigt sie nicht!",
    ["O"] = "Benutzt den ultrastarken Dekonsolidierungsstrahler, um den Hyperdreh-Grab-o-Matik, die servopneumatische Baggerklaue, den multispektralen Geländeabtaster und den großen Wagen voller Sprengstoff zurück nach Area 52 zu beamen. Meldet Euch dann beim leitenden Ingenieur Zischeldruck beim östlichen Ende des Risses in Area 52 im Nethersturm.",
    ["T"] = "Unschätzbare Ausrüstung beamen",
  },
  [10204] = {
    ["D"] = "Kaels Magister haben den Gebrauch von Manakristallen so weit perfektioniert, dass sie innerhalb von Sekunden große Mengen von Energie aufnehmen können.$B$BDas tun sie, indem sie kleinere Kristallsplitter von Blutjuwelen an größeren und vollen Kristallen aufladen.$B$BGeht zur Manaschmiede B\'naar und besorgt einen dieser Splitter von einem Magister des Sonnenzorns und benutzt ihn, um Energie von einem größeren Kristall aufzunehmen.$B$BKehrt zu mir zurück, damit ich die Wirkung der Aura untersuchen kann. Keine Sorge, Nebenwirkungen sollten nur temporär sein.",
    ["O"] = "Magistrix Larynna möchte, dass Ihr zur Manaschmiede B\'naar geht und einen Blutjuwelensplitter von einem Magister des Sonnenzorns besorgt. Benutzt den Splitter in der Nähe von größeren Blutjuwelenkristallen und kehrt zu ihr in Area 52 zurück.$B$BDas Abschließen von Quests für die Seher führt zu einer Verringerung Eures Rufs bei den Aldor.",
    ["T"] = "Blutjuwelenkristalle",
  },
  [10205] = {
    ["D"] = "Sphärenräuber Nesaad muss erledigt werden! Ich habe keine Ahnung, wie er meinen Angriff überleben konnte, aber er darf seine Taten dort auf keinen Fall fortführen.$B$BGeht zurück und vernichtet Nesaad. Er hat ein kleines Lager des Astraleums im südwestlichen Teil des Hügels.$B$BVersagt nicht, wie ich es tat, $N.",
    ["O"] = "Tötet Sphärenräuber Nesaad und kehrt dann zu Netherpirscher Khay\'ji in Area 52 im Nethersturm zurück.",
    ["T"] = "Sphärenräuber Nesaad",
  },
  [10206] = {
    ["D"] = "Diese astralen Hampelmänner sind genauso opportunistisch wie wir, das muss man ihnen lassen. Aber niemand, und ich meine NIEMAND, wagt sich auf das Gebiet von B.U.M.M.!$B$BDer Hügel im Süden gehört uns!$B$BWenn wir die X-52 Netherrakete fertigstellen wollen, brauchen wir alle neuen Technologien, die wir in die Finger bekommen können. Und da die Astralen von uns stehlen, ist es nur gerecht, wenn wir es ihnen mit gleicher Münze heimzahlen.$B$BWarum geht Ihr nicht zum Hügel und schaut, ob Ihr ihnen etwas von ihrer Technologie vor der Nase wegschnappen könnt.",
    ["O"] = "Sammelt 10 Stück Astraltechnologie und bringt sie zu Papa Mauschel in Area 52 im Nethersturm.",
    ["T"] = "Wählt Eure Rolle",
  },
  [10207] = {
    ["D"] = "Eure Fähigkeiten in der Schlacht sind dem obersten Kommando aufgefallen, $C. Sie wollen Euch an die Front zu Stümmelfausts Truppe schicken. Sie sind sehr radikal und haben sich der vollständigen Auslöschung der Legion verschrieben, also passt Ihr wahrscheinlich genau dazu...$B$BGeht etwa 200 Schritte nach Osten und biegt bei der hervorragenden Landmasse nach Süden ab. Ihr findet den Häschersturz dort, wo einst ein alter Bauernhof stand. Wenn Ihr angekommen seid, meldet euch bei Vorpostenkommandant To\'arch.$B$BUnd jetzt weggetreten!",
    ["O"] = "Meldet Euch bei Vorpostenkommandant To\'arch im Häschersturz.",
    ["T"] = "Vorposten: Häschersturz",
  },
  [10208] = {
    ["D"] = "Direkt im Osten liegt die Front der Legion. Entlang der Front befinden sich die höllischen Portale und Tore, mit deren Hilfe Verstärkung auf die Höllenfeuerhalbinsel und in die ganze Scherbenwelt gebracht wird. Wir müssen diese Portale zerstören!$B$BNehmt diese Zünder. Sie wurden in Area 52 hergestellt und benutzten die eigene Macht der Dämonen als Sprengstoff! Besiegt die Legion beim Portal Xilus und Portal Kruul und haltet nach dämonischen Runensteinen Ausschau. Benutzt den Zünder, um die vier Steine in den Portalen zu sprengen, sobald Ihr genug Steine beisammen habt... und dann genießt das Feuerwerk! Kehrt zu mir zurück, wenn Ihr Eure Aufgabe erfüllt habt.",
    ["O"] = "Besiegt die Feinde um Portal Xilus und Portal Kruul herum, um dämonische Runensteine zu sammeln.$B$BBenutzt To\'archs Zünder in Portal Xilus und Portal Kruul.$B$BKehrt danach mit To\'archs Zünder zu To\'arch im Häschersturz auf der Höllenfeuerhalbinsel zurück.",
    ["T"] = "Die Verstärkung abschneiden",
  },
  [10209] = {
    ["D"] = "Als nächstes müssen wir meinen Vertrauten, Gletscharius, aus den Händen des Blutelfenbeschwörers, der ihn gefangen hält, befreien. Beschwörer Kanthin war einst einer der erfahrensten Magister in der Streitmacht, die Kael\'thas losgeschickt hat, um Kirin\'Var auszulöschen. Es ist ganz normal, dass er meinen Vertrauten als Trophäe haben will.$B$BIch bin mir sicher, dass Kanthin, dumm wie er ist, versucht hat, Gletscharius zu seinem Diener zu machen. Ich habe herausgefunden, dass Kanthin sich jetzt in der Manaschmiede Duro aufhält, falls dies Euch etwas sagt.",
    ["O"] = "Defeat Summoner Kanthin at Manaforge Duro, on the island to the north of Sunfury Hold, and reclaim the Stone of Glacius from him. When you have obtained the stone, use Archmage Vargoth\'s Staff to contact the archmage.$B$BIf you lose Archmage Vargoth\'s Staff, speak to Ravandwyr at Area 52 to receive a replacement.",
    ["T"] = "Beschwörer Kanthins Preis",
  },
  [10210] = {
    ["D"] = "Willkommen in Shattrath, $C. Dies ist vielleicht der einzige Ort in der Scherbenwelt, in der Ihr so etwas wie Sicherheit empfinden könnt.$B$BEine Gruppe von Naaru, die als Sha\'tar bekannt sind, hat die Dämonen von hier fortgejagt.$B$BWenn Ihr noch nie einen Naaru gesehen habt, solltet Ihr A\'dal auf der Terrasse des Lichts besuchen. Der Anführer der Sha\'tar ist der ehrfurchtgebietendste Anblick, den ich je gesehen habe. Ganz anders, als alles andere in diesem öden Land.",
    ["O"] = "Sprecht mit A\'dal, Anführer der Sha\'tar in der Mitte der Terrasse des Lichts.",
    ["T"] = "A\'dal",
  },
  [10211] = {
    ["D"] = "Belästigt A\'dal nicht mit Euren unbedeutenden Fragen, $C. Er ist vielleicht das mächtigste Wesen, das Ihr je zu Gesicht bekommen werdet.$B$BIhr kommt mir nicht bekannt vor. Ich nehme an, Ihr wollt etwas über die Stadt erfahren?$B$BIhr müsst schon entschuldigen, dass ich selbst keine Führungen mache, aber ich muss einige Pläne mit A\'dal besprechen. Vielleicht wärt Ihr ja mit einem herbeigerufenen Diener an meiner statt zufrieden? Ausgezeichnet!",
    ["O"] = "Folgt Khadgars Diener und hört Euch an, was er zu erzählen hat. Kehrt nach dieser Aufgabe zu Khadgar zurück.",
    ["T"] = "Stadt des Lichts",
  },
  [10212] = {
    ["D"] = "Ich muss erst alles in Ordnung bringen, bevor ich gehe. Kehrt zur Großmutter zurück und sagt ihr, dass ihr Enkelsohn kommt.",
    ["O"] = "Kehrt zu Großmutter Geyah in Garadar in Nagrand zurück.",
    ["T"] = "Held der Mag\'har",
  },
  [10213] = {
    ["D"] = "Als ich verwundet in der Wüste lag, habe ich gesehen, wie ein goblinscher Zeppelin abgestürzt ist. Er kam im Nordwesten runter, hinter dem Expeditionsrüstlager.$B$BDer Zeppelin hat gebrannt. Schreiende, brennende Goblins fielen wie Bomben vom Himmel. Ich hatte natürlich sehr große Schmerzen und viel Blut verloren. Vielleicht habe ich mir das alles auch nur eingebildet.$B$BWenn Ihr irgendwelche Überlebenden finden solltet, bietet ihnen eine Zuflucht hier im Rückenbrecherposten an.",
    ["O"] = "Durchsucht die Absturzstelle nach Überlebenden.",
    ["T"] = "Untersuchung der Absturzstelle",
  },
  [10214] = {
    ["D"] = "Die Mine wurde von diesem Gan\'argpack überrannt, und zu allem Übel sind die Peons der Meinung, dass jetzt die richtige Zeit für ein Schläfchen ist! Ich weiß nicht, wie sie bei all dem Lärm aus der Mine schlafen können, aber das ist mein geringstes Problem.$B$BWenn wir dieses Gan\'arggesindel nicht bald loswerden, wird Thrallmars Nachschub an Kupfer und anderen Materialien abgeschnitten. Ich habe schon Wachkommandant Krunk um Hilfe gebeten, aber er sagt, dass er keine Männer schicken kann, solange die Stadt angegriffen wird. Könnt Ihr mir helfen?",
    ["O"] = "Tötet 15 Pioniere der Gan\'arg und kehrt dann zu Großknecht Razelcraz zurück.",
    ["T"] = "Bringt diese Mine zum Beben!",
  },
  [10216] = {
    ["D"] = "Shaffars Astrale haben uns ganz aus der Managruft ausgeschlossen. Zum Glück habe ich einen Plan. Auch wenn wir nicht durch die Eingangstür können, bin ich mir sicher, dass es innen viele Transporter gibt.$B$BIch möchte, dass Ihr zuerst den Weg für meine Gruppe freiräumt und dann einen der Transporter aktiviert, damit wir hineinteleportieren können.$B$BAktiviert den Transporter in der Kammer genau vor Shaffars Kammern, wenn Ihr den Weg freigemacht habt. Keine Sorge, ich werde Euch dafür entlohnen...",
    ["O"] = "Konstrukteur Morphalius möchte, dass Ihr 10 astrale Grufträuber, 5 astrale Zauberer, 5 Nexuswandler und 5 astrale Zauberbinder tötet.$B$BWenn Ihr damit fertig seid, sucht den astralen Transporter in der Kammer vor Shaffars Kammer und aktiviert ihn.",
    ["T"] = "Sicherheit geht vor",
  },
  [10218] = {
    ["D"] = "Wen habt Ihr erwartet?! Dieser Idiot Morphalius wurde entpriorisiert. Jetzt bin ich hier, um sein Chaos aufzuräumen. Alles, was ich von Euch will, ist, dass Ihr mir den Rücken freihaltet, während ich Shaffars Astralsammler durchgehe und ihnen alles entnehme, was sie gesammelt haben. Wenn ich meinen Auftrag erfolgreich abschließe, wird Haramad selbst Euch belohnen!$B$BWas sagt Ihr? Begleitet mich durch die Managruft und sammelt ein paar unglaubliche Schätze! Die meiste Arbeit habt Ihr ohnehin schon erledigt! Hinter uns ist nichts mehr am Leben, oder?",
    ["O"] = "Begleitet Kryoingenieur Sha\'heen sicher durch die Managruft, damit er die Astralmaterie aus Shaffars Astralsammlern entnehmen kann.$B$BMeldet Euch bei Nexusprinz Haramad außerhalb der Managruft beim Lager des Konsortiums, wenn Ihr erfolgreich wart.",
    ["T"] = "Jemandes Arbeit zahlt sich aus",
  },
  [10219] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Gassi gehen",
  },
  [10220] = {
    ["D"] = "Ich wurde hierher geschickt, Kontakt zu den unerschütterlichen Geistern herzustellen. Diese einst stolzen Soldaten der Allianz wurden vor langer Zeit besiegt, und nun spuken ihre Geister in den Ruinen im Westen.$B$BWenn sie sich uns anschließen würden, hätten wir einen mächtigen Verbündeten. Stellt Euch nur vor, eine Armee von untoten Kriegern, die sich unserem Feind entgegenstellt. Aber diese Untoten sind blind vor Hass und wollen sich meinen Vorschlag nicht anhören.$B$BVielleicht kann man sie zwingen, sich uns anzuschließen.",
    ["O"] = "Mordiba möchte, dass Ihr 12 unerschütterliche Fußsoldaten, 8 unerschütterliche Krieger und 6 unerschütterliche Zauberer beim Expeditionsrüstlager westlich des Rückenbrechergrats tötet.",
    ["T"] = "Bringt sie zum Zuhören",
  },
  [10221] = {
    ["D"] = "$N, mein früherer Schützling, Sparky Überschub, hat sich geweigert, während seiner Arbeit hier seinen Helm zu tragen und hat sich in den berüchtigten Dr. Bumm verwandelt!$B$BEr hat sich mit einem Großteil unserer Pionierausrüstung nach Nordosten aufgemacht! Seitdem sind er und seine Bummbots eine große Bedrohung für Area 52!$B$BIch möchte, dass Ihr zu seinem Lager außerhalb der Stadt geht und ihm ein Ende macht! Benutzt diese Bummbomben um ihn auszulöschen.$B$BUnd passt auf, dass Ihr ihm oder seinen Bummbots nicht zu nahe kommt!",
    ["O"] = "Tötet Dr. Bumm und kehrt dann mit den guten Nachrichten zu dem leitenden Ingenieur Zischeldruck in Area 52 im Nethersturm zurück.",
    ["T"] = "Dr. Bumm!",
  },
  [10222] = {
    ["D"] = "Nachdem Prinz Kael\'thas\' Streitkräfte das Dorf zerstört und den Erzmagier im Turm eingesperrt hatten, haben sie sich zum Sonnenzornposten im Nordosten zurückgezogen. Die gleichen Männer, die unser Dorf zerstört und unsere Freunde getötet haben, stoßen nun auf ihren \'unglaublichen Sieg\' an, während wir unsere Gefallenen nicht rächen können.$B$BWir dürfen diese Verbrechen nicht ungestraft lassen! Seid unser Instrument der Rache, $N. Geht zum Sonnenzornposten und fordert das Leben der Verantwortlichen für dieses Verbrechen ein!",
    ["O"] = "Hüter Dierwert in Kirin\'Var möchte, dass Ihr 10 Bogenschützen des Sonnenzorns tötet.",
    ["T"] = "Die Garnison des Sonnenzornpostens",
  },
  [10223] = {
    ["D"] = "Den Blutelf, der den Angriff geführt hat, werde ich nie vergessen. Er trug einen Wappenrock mit einem Ding, was ich noch nie zuvor gesehen habe, über einer roten Rüstung.$B$BEr war wild und erbarmungslos, und kämpfte an der Front, wo man kaum einen anderen Blutelfenoffizier je zu Gesicht bekommt. Er war an diesem Tag für den Tod vieler Kirin Tor verantwortlich, aber er blieb nicht lange im Sonnenzornposten. Kael\'thas hat ihn zur Manaschmiede Duro beordert, um bei ihrem Versuch, das Gebiet zu kontrollieren, zu helfen. Findet ihn, $N, und bringt seinen Bogen für immer zum Schweigen.",
    ["O"] = "Hüter Dierwert in Kirin\'Var möchte, dass Ihr Meister Daellis Morgenschlag tötet.",
    ["T"] = "Nieder mit Daellis",
  },
  [10224] = {
    ["D"] = "Als Chefingenieur der X-52 ist es meine Pflicht dafür zu sorgen, dass die Hauptmotoren der Netherrakete tiptop in Schuss sind.$B$BUnglücklicherweise habe ich sie noch nicht gebaut.$B$B$C, ich bin hier in einer Notlage! Ich habe fast alle Teile. Ich brauche nur noch einen Antriebskern.$B$BZum Glück bin ich der Meinung, dass wir die Essenz der Managespenster in der Nähe nutzen können, um die Motoren zu überbrücken.$B$BIhr findet die Managespenster überall bei diesen komischen leuchtenden Gräben im Norden der Insel. Außer hier in Area 52 natürlich.",
    ["O"] = "Sammelt 7 Essenzen eines Managespensts und bringt sie zu Chefingenieur Trep bei Area 52 im Nethersturm.",
    ["T"] = "Essenzen für die Motoren",
  },
  [10225] = {
    ["D"] = "$N! Ich möchte, dass Ihr all diese Astrallithiumkristalle zu Chefingenieur Trep, meinem, äh, Ingenieurschef bringt.$B$BEr sollte wissen, was mit ihnen zu tun ist. Und wenn er es nicht weiß, dann sorgt dafür, dass er es herausfindet.",
    ["O"] = "Bringt die Astrallithiummatrixkristalle zu Chefingenieur Trep in Area 52 im Nethersturm.",
    ["T"] = "Meldung beim Chefingenieur",
  },
  [10226] = {
    ["D"] = "Verdammt, das hat nicht geklappt.$B$BOk, ich habe eine andere Idee. Ich habe Xyrol einen Elementarenergieextraktor herstellen lassen. Ich weiß nicht genau, was man mit dieser Energie anfangen kann, aber ich werde schon einen Verwendungszweck für sie finden.$B$BDie zerrissenen Rumpler und Sphärenentartungen auf dem Felsvorsprung der Schwindenden Weite im Süden haben genau das, was wir für die Motoren brauchen!$B$BDer Extraktor wird ihre Energie in eine feste Form bringen, die Ihr aufsammeln könnt, nachdem Ihr sie besiegt habt.",
    ["O"] = "Extrahiert 5 Einheiten Elementarenergie und bringt sie zu Chefingenieur Trep in Area 52 im Nethersturm.",
    ["T"] = "Elementarenergieextraktor",
  },
  [10227] = {
    ["D"] = "Ich sehe tote Draenei. Sie sind überall.$B$BPah, niemand glaubt mir, weil ich ein Kind bin. Nichtmal meine Mama glaubt mir.$B$B<Ha\'lei zeigt auf Ramdor.>$B$BEr glaubt mir. Er sieht sie auch. Und er regt sich darüber auf!$B$BIrgendwie ist es lustig. Ich sehe Geister und die Leute lächeln mich nur an und tätscheln mir den Kopf, um mich zu beruhigen. Ramdor sieht Geister und sie halten ihn für verrückt. Ich hoffe echt, dass ich nie erwachsen werde!$B$BVielleicht solltet Ihr erst mit Ramdor reden, bevor Ihr da hinein geht. Er war einst ein Totenpriester.",
    ["O"] = "Sprecht mit Ramdor dem Wahnsinnigen auf dem Knochenhaufen außerhalb der Auchenaikrypta.",
    ["T"] = "Ich sehe tote Draenei",
  },
  [10228] = {
    ["D"] = "Ich bin der Meinung, dass die Toten, um die wir uns seit den letzten 500 Jahren gekümmert haben, in Ruhe gelassen werden sollten. Aber Maladaar hat andere Pläne. Er hat sich in die Krypta des Gedenkens zurückgezogen, um seine Experimente zu beginnen... Nekromantie.$B$BIch hoffe, Ihr hattet nicht vor, dorthin zu gehen... nicht bevor Ihr mir geholfen habt, diese verdammten Geister loszuwerden. Ich weiß, wo sie ihre Schätze aufbewahren, $R. Jetzt habe ich Eure Aufmerksamkeit, was?$B$BBringt mir das Buch der Toten, das sich im Besitz des Aldors Ezekiel von Shattrath befindet.",
    ["O"] = "Sprecht mit Ezekiel von Shattrath in Shattrath.",
    ["T"] = "Ezekiel",
  },
  [10229] = {
    ["D"] = "Dieser Foliant scheint eine Art Geschichtsbuch zu sein, das in einer Geheimschrift der Allianz geschrieben wurde. Althen der Historiker im Rückenbrecherposten hat die Unerschütterlichen eine Zeit lang studiert. Vielleicht kann er die Geheimschrift der Allianz entziffern und das Buch lesen.",
    ["O"] = "Bringt den geheimnisvollen Folianten zu Althen dem Historiker im Rückenbrecherposten.",
    ["T"] = "Den Folianten entziffern",
  },
  [10230] = {
    ["D"] = "Vielleicht haben wir uns ganz falsch an diese Unerschütterlichen herangewagt.$B$BDieser Foliant sagt, dass das Rüstlager der Allianz vor langer Zeit von den Orcs und den Todesrittern zerstört wurde. Die Unerschütterlichen wurden dort als rachedurstige Geister in einer ewigen Schlacht gegen die Klans der Höllenorcs gefangen.$B$BSie denken nur an Rache. Wir müssen ihnen die Chance geben, ihre meist verhassten Feinde zu besiegen. Kehrt noch einmal in die Ruinen des Rüstlagers zurück und sucht Feldkommandant Thalvos. Er trägt ein Kampfhorn, das die Unerschütterlichen rufen kann. Bringt es mir.",
    ["O"] = "Besorgt Euch das Kampfhorn der Unerschütterlichen von Feldkommandant Thalvos und bringt es zu Althen dem Historiker im Rückenbrecherposten.",
    ["T"] = "Das Kampfhorn",
  },
  [10231] = {
    ["D"] = "Ramdor? Hmm, mal überlegen... Ramdor... Oh ja, der Totenpriester. Was will er denn mit dem Buch? Nun, es ist gleichgültig. Ja, ich habe Euch angelogen und nein, ich habe das Buch nicht. Nicht mehr zumindest. Es wurde gestohlen. Wahrscheinlich von einem dieser widerlichen Flüchtlinge.$B$BIm Übrigen existiert Auchindoun nicht mehr. Wir haben keine Verwendung mehr für das Buch. Wenn Ihr es findet, könnt Ihr es behalten.$B$BWie schon gesagt, ich habe ein paar dieser Grobiane letzte Woche um die Terrasse herumschleichen sehen. Fangt mit Eurer Suche bei ihnen an. Wahrscheinlich spielen sie irgendwo im Armenviertel Würfelspiele.",
    ["O"] = "Ezekiel sagt, dass das Buch gestohlen wurde. Eure einzige Spur führt Euch zu den Grobianen. Schaut nach, ob sie irgendwelche Informationen haben.",
    ["T"] = "Welches Buch? Ich sehe kein Buch.",
  },
  [10232] = {
    ["D"] = "Die Brennende Legion ist auf unserem Grund im Trümmerfeld, den Pfad hinunter und über die Brücke nach Osten, einmarschiert!$B$BDas könnte ein großes Problem für uns sein! Die Legion in den Ruinen von Arklon und jetzt auch noch hier! Langsam sieht es für uns und das Raketenprojekt hier in Area 52 schlecht aus!$B$B$N, ich möchte, dass Ihr dorthin geht und mit den Dämonen fertigwerdet! Ich würde es ja tun, aber ich bin kein Kämpfer.$B$BJa, das riecht nach Ärger, ich fühle es.",
    ["O"] = "Tötet 5 Schicksalsschmiede der Mo\'arg und 15 Ingenieure der Gan\'arg. Kehrt danach zu Papa Mauschel in Area 52 im Nethersturm zurück.",
    ["T"] = "Legion in Trümmern",
  },
  [10233] = {
    ["D"] = "Die Garnison des Sonnenzornpostens auszulöschen ist nicht genug, um die Blutelfen davon abzuhalten, ihn erneut zu beziehen. Kael\'thas Anhänger sind eifrig und hartnäckig. Sobald im Sonnenzorn noch ein Stein auf dem anderen steht, ist dies praktisch eine Einladung für sie, zurückzukehren.$B$BNein, der Posten muss völlig zerstört werden. Sorgt dafür, dass ihre Zelte und Artillerie zu Asche zerfallen!$B$B<Der Geist ballt die Fäuste.>$B$BStehlt die Fackeln von den Flammenbewahrern des Sonnenzorns um Eure Aufgabe zu erfüllen, $N.",
    ["O"] = "Benutzt eine flammende Fackel, zerstört 4 Sonnenzornballisten und 4 Sonnenzornzelte und kehrt dann zu Zaubererleutnant Morran in Kirin\'Var zurück.",
    ["T"] = "Den Sonnenzornposten anzünden",
  },
  [10234] = {
    ["D"] = "Ihr seht aus, als wärt Ihr $Gein fähiger junger Mann:eine fähige junge Frau;! Ich habe mich gefragt, ob Ihr mir vielleicht zur Hand gehen könntet.$B$BIm Osten befindet sich das Trümmerfeld, auf dem die Brennende Legion Einzug gehalten hat. Es ist voller Teufelshäscherteile. Dort liegen auch ein paar kleinere Teile herum, um die sie sich nicht zu scheren scheinen.$B$BIch möchte, dass Ihr dorthin geht, und mir ein paar dieser kleineren Teile eines Teufelshäschers besorgt!",
    ["O"] = "Sammelt 10 Teile eines Teufelshäschers und bringt sie zu Mama Mauschel im Mittelreichposten in der Biokuppel Mittelreich im Nethersturm.",
    ["T"] = "Der Müll eines Dämons",
  },
  [10235] = {
    ["D"] = "Wisst Ihr, was sie entmutigen würde? Wenn wir ihren Anführer erlegen!$B$BWie ich gehört habe, klappt das immer. Und wenn sie sehen, dass so $Gein knallharter:eine knallharte:c; $C wie Ihr ihren Anführer erledigt, dann ziehen sie sicher den Schwanz ein und rennen weg!$B$BGeht dorthin zurück und erlegt den Großen, den sie Verdammnisklaue nennen. Ich habe gehört, dass er sich im östlichen Teil den Trümmerfelds unter dem Rohr aufhält.$B$BBringt mir seine Klaue! Vielleicht können wir sie brauchen.",
    ["O"] = "Besorgt Euch Verdammnisklaues Hand und bringt sie zu Mama Mauschel im Mittelreichposten in der Biokuppel Mittelreich im Nethersturm.",
    ["T"] = "Verdammnisklaue die Krallen stutzen",
  },
  [10236] = {
    ["D"] = "Ich kann es kaum fassen, dass ich Beutebucht verlassen habe, um in die Scherbenwelt zu kommen. Mein Vetter Quack Lufthans hat mir versprochen, dass ich hier ein Vermögen machen würde, aber stattdessen ist er in seinem Zeppelin auf und davon und hat mich hier zurückgelassen. Nun versuche ich, mir einen sehr bescheidenen Lebensunterhalt mit der Mine zu verdienen.$B$BAber wie soll ich denn überleben, wenn ich dauernd von Höllenebern und Dämonen und Höllenorcs angegriffen werde? Mein Schredder hier ist gestern kaputt gegangen und die Höllenorcs haben meine Ersatzteile gestohlen. Sie haben die Teile in die Große Kluft im Südwesten gebracht und vielleicht dort fallenlassen.",
    ["O"] = "Großknecht Razelcraz möchte, dass Ihr ihm 6 Schredderteile besorgt. Kehrt danach zu ihm bei der Mine nordwestlich von Thrallmar zurück.",
    ["T"] = "Die Scherbenwelt ist doof!",
  },
  [10237] = {
    ["D"] = "$N, Ihr müsst so schnell wie möglich nach Area 52 zurückkehren! Beeilt Euch!$B$BSagt dem Raketenchef, dass die Brennende Legion hinter uns her ist!",
    ["O"] = "Überbringt Raketenchef Doppeldecker in Area 52 die schlechten Nachrichten.",
    ["T"] = "Warnt Area 52!",
  },
  [10238] = {
    ["D"] = "Warum sollten die Höllenorcs denn meine Schredderteile stehlen wollen? Ich hasse sie so. Sie schleichen sich einfach in mein Lager hier und klauen immer meine Sachen. Vor ein paar Tagen haben sie drei meiner Peons gestohlen! Jetzt sind alle anderen Peons abgehauen, weil sie Angst hatten, von den Höllenorcs gefressen zu werden.$B$BIch kann meine Mine ohne Peons nicht betreiben. Wenn Ihr meine gestohlenen Peons retten könntet, kommen die anderen vielleicht auch wieder zurück. Die Höllenorcs haben ihre Lager entlang des Walls im Südwesten.",
    ["O"] = "Rettet Manni, Moh und Jakk aus den Lagern der Höllenorcs und bringt sie zu Großknecht Razelcraz bei der Mine nordwestlich von Thrallmar zurück.",
    ["T"] = "Wie man Goblins zubereitet",
  },
  [10239] = {
    ["D"] = "Wir sind nicht die Einzigen, die unter dem Zufluss von Manakreaturen leiden. Auch die Blutelfen sind darüber besorgt, ich weiß jedoch nicht, warum.$B$BWas immer sie da drüben tun mögen, sie scheinen nicht damit aufzuhören. Stattdessen erfinden sie immer neue Methoden, um sie zu töten. Die wirksamste davon beinhaltet eine magische Verteidigung, die von einer Art konzentrierter Energie gespeist wird.$B$BSammelt einige dieser Energiequellen und bringt sie mir. Scheut Euch nicht, die Blutelfen zu töten, wenn sich eine Gelegenheit ergeben sollte.",
    ["O"] = "Zaubererleutnant Morran in Kirin\'Var möchte, dass Ihr ihm 10 Energieisolationswürfel von den Blutelfen der Manaschmiede Coruu bringt.",
    ["T"] = "Mögliche Energiequelle",
  },
  [10240] = {
    ["D"] = "Wir sind dabei, eine Verteidigung um das Dorf herum zu errichten, die mit der Energiequelle der Blutelfen gespeist wird. Ich kann nicht garantieren, dass sie funktionieren wird, aber wir müssen es versuchen.$B$BWenn nicht werden die Manakreaturen weiterhin die Geister der gefallenen Kirin Tor verspeisen.$B$BBenutzt dieses Gerät bei den kleinen Runen um die Verteidigung des Dorfes herum, um die Energie dort freizusetzen. Wenn ich das Feld korrekt gewebt habe, sollten wir die Wirkung bemerken, sobald Ihr das Gerät bei jedem Punkt benutzt habt.",
    ["O"] = "Benutzt das Gerät zur Aktivierung von Runen bei der östlichen Rune, der nordöstlichen Rune und der westlichen Rune. Die Runen befinden sich entlang der Verteidigung der Zaubergasse und des Ratsplatz von Kirin\'Var. Kehrt zu Zaubererleutnant Morran zurück, wenn Ihr die Aufgabe erfüllt habt.",
    ["T"] = "Eine Verteidigung errichten",
  },
  [10241] = {
    ["D"] = "Wir wurden von Sha\'tar hierher geschickt, um die Manaschmieden, die Kael\'thas\' Armee hier betreibt, zu untersuchen. Aber die Seher drohen damit, ihre Spionagearbeiten nicht durchzuführen, wenn wir sie nicht militärisch unterstützen.$B$BWir können uns nicht erlauben, dass diese Mission wegen politischen Differenzen den Bach hinunter geht, daher haben wir uns einverstanden erklärt, die Verteidigung der Manaschmiede zu schwächen, damit die Seher ihre Arbeit verrichten können.$B$BBeweist Eure Loyalität gegenüber den Aldor und tötet die Magister und Blutwärter des Sonnenzorns, die die Manaschmiede B\'naar bewachen.",
    ["O"] = "Tötet 8 Magister des Sonnenzorns und 8 Blutwärter des Sonnenzorns und kehrt dann zu Exarch Orelis in Area 52 zurück.$B$BDas Abschließen von Quests für die Aldor führt zu einer Verringerung Eures Ruf bei den Sehern.",
    ["T"] = "Ablenkung in der Manaschmiede B\'naar",
  },
  [10242] = {
    ["D"] = "$N, meine Ausrüstung hier ist leider nicht für alle Tests, die ich durchführen möchte, geeignet. Mein Kollege Apotheker Albreck im Rückenbrecherposten verfügt jedoch über alle Hilfsmittel, die man dazu braucht.$B$BIch habe eine Blutprobe des Blutenden Auges, die mir ein Agent aus Thrallmar besorgt hat. Bringt diese Probe zu Albreck und erlaubt ihm, sie zu untersuchen.$B$BSprecht mit Schwadronskommandant Brack, um zum Rückenbrecherposten zu kommen. Er befindet sich in der Nähe der Wyvern im Süden.",
    ["O"] = "Apothekerin Zelana beim Häschersturz möchte, dass Ihr mit Schwadronskommandant Brack sprecht, um auf einem Wyvern zum Rückenbrecherposten zu fliegen. Bringt die Blutprobe des Blutenden Auges zu Apotheker Albreck im Rückenbrecherposten.",
    ["T"] = "Der Rückenbrechergrat",
  },
  [10243] = {
    ["D"] = "Die Seher haben eine Stammrolle der Manaschmiede B\'naar in die Hände bekommen und sind nun dabei, gezielt nach Anschlagsopfern zu suchen.$B$BSie haben jedoch eine kleine Information übersehen, die an den Rand der Schriftrolle gekritzelt wurde. Es scheint eine Art Passwort oder Code zu sein, wie sie für den Zugang zu von den Naaru erschaffenen Technologien wie der Exodar oder der Festung der Stürme benutzt werden.$B$BGeht in die Manaschmiede und haltet nach einer Steuerungskonsole Ausschau. Schreibt die Anzeige so gut es geht auf dieser Rolle ab. Wir müssen unseren Verdacht über die Herkunft von Kaels Technologie erhärten.",
    ["O"] = "Geht in die Manaschmiede B\'naar und sucht die Steuerungskonsole von B\'naar.$B$BDas Abschließen von Quests für die Aldor führt zu einer Verringerung Eures Rufs bei den Sehern.",
    ["T"] = "Die Technologie der Naaru",
  },
  [10244] = {
    ["D"] = "Ihr zerbrecht das Siegel des ziemlich großen Folianten und könnt nicht verstehen, was dort geschrieben steht. Es schein die Sprache der Dämonen zu sein.$B$BIhr könnt jedoch die Bilder erkennen, die ihre eigene Geschichte erzählen.$B$BEs handelt sich eindeutig um eine Bauanleitung für Teufelshäscher, und Ihr wisst ganz sicher, dass Mama Mauschel sich das gerne anschauen würde.",
    ["O"] = "Bringt die Bauanleitung für Teufelshäscher zu Mama Mauschel im Mittelreichposten in der Biokuppel Mittelreich im Nethersturm.",
    ["T"] = "Liesmich",
  },
  [10245] = {
    ["D"] = "Ihr versucht die Formen abzuschreiben, als sie an Euren Augen vorbeiziehen. Die Ergebnisse sind nicht gerade genau, aber sie sollten genügen, um den Verdacht der Aldor zu bestätigen.",
    ["O"] = "Bringt die Steuerungsabschrift von B\'naar zu Anachoret Karja in Area 52 im Nethersturm.$B$BDas Abschließen von Quests für die Aldor führt zu einer Verringerung Eures Rufs bei den Sehern.",
    ["T"] = "Steuerungsabschrift von B\'naar",
  },
  [10246] = {
    ["D"] = "Euer Erfolg in der Manaschmiede B\'naar war sehr bemerkenswert. Ich bin sicher, dass Anachoret Karja Euch bitten wird, Eure Leistung als Nächstes in der Manaschmiede Coruu zu wiederholen.$B$BWir haben erfahren, dass ein besonders wichtiges Regiment von Arkanisten und Forschern des Sonnenzorns dort stationiert wurde. Wir würden sie gerne angreifen. Wenn es Euch gelingt, wird das ein vernichtender Schlag für die Streitkräfte unseres Feindes sein.",
    ["O"] = "Reist zur Manaschmiede Coruu und tötet 8 Arkanisten des Sonnenzorns und 5 Forscher des Sonnenzorns. Kehrt zu Exarch Orelis zurück, wenn Ihr Eure Aufgabe erledigt habt.$B$BDas Ausführen von Quests für die Aldor führt zu einer Verringerung Eures Rufs bei den Sehern.",
    ["T"] = "Angriff auf Manaschmiede Coruu",
  },
  [10247] = {
    ["D"] = "Am Rand des Dorfes im Osten befinden sich die Versuchsgründe. Unser Spitzenobertüftler, Doktor Vomisa, hat hier ein wenig mit einer Kombination aus Teufelshäschertechnologie, Astraltechnologie und natürlich Goblintechnologie herumgespielt.$B$BDas verdanken wir nicht zuletzt Euren Sammelbemühungen, Juniortechniker dritten Grades $N!$B$BSucht ihn auf! Ich bin mir sicher, dass er einen Weg weiß, wie Area 52 gerettet werden kann.$B$BUnd weil er Euch sicher als Versuchskaninchen einsetzen möchte, würde ich an Eurer Stelle lieber ein paar Freunde mitbringen. Nur für den Fall.",
    ["O"] = "Sprecht mit Dr. h. c. tüft. Vomisa in den Versuchsgründen im Nethersturm.",
    ["T"] = "Dr. h. c. tüft. Vomisa",
  },
  [10248] = {
    ["D"] = "Dr. h. c. tüft. Vomisa zu Euren Diensten. Hier, nehmt diese Steuerung. Ich möchte, dass Ihr sie aktiviert und meinen neu erfundenen Schrotthäscher X6000 mal ausprobiert.$B$BBleibt in der Nähe! Die Steuerung ist an meine Konsole hier gebunden, wenn Ihr zu weit weggeht, wird der Roboter aufhören zu funktionieren.$B$BProbiert alles aus... Ich schaue zu und stelle dann die Servos gemäß Eures Testlaufs ein.",
    ["O"] = "Aktiviert die Steuerung für Schrotthäscher X6000 und testet seine Fähigkeiten aus. Gebt Dr. h. c. tüft. Vomisa bei den Versuchsgründen im Nethersturm danach Eure Rückmeldung.",
    ["T"] = "Du, der Roboter",
  },
  [10249] = {
    ["D"] = "Ich bin mir sicher, dass der Raketenchef ein oder zwei Takte mit Euch reden möchte!$B$BAn Eurer Stelle würde ich mich gleich nach Area 52 aufmachen und mit ihm sprechen!",
    ["O"] = "Meldet Euch sofort bei Raketenchef Doppeldecker in Area 52 im Nethersturm!",
    ["T"] = "Zurück zum Chef!",
  },
  [10250] = {
    ["D"] = "Im Osten befindet sich ein alter Belagerungsturm der Allianz. Jetzt suchen Plünderer dort nach alten Nägeln und Alteisen. Vor langer Zeit haben bei diesem Turm viele Krieger der Allianz ihr Leben gelassen. Ihr Banner wurde von einem widerlichen Orckommandanten namens Urtrak gestohlen.$B$BStoßt bei diesem Belagerungsturm in das Kampfhorn der Unerschütterlichen, um Urtraks Aufmerksamkeit zu erregen. Vielleicht ruft es auch Krieger herbei, die vor langer Zeit in der Schlacht gestorben sind.",
    ["O"] = "Stoßt am alten Belagerungsturm der Allianz in das Kampfhorn der Unerschütterlichen. Tötet Urtrak und kehrt danach zu Althen dem Historiker im Rückenbrechergrat zurück.",
    ["T"] = "Blutige Rache",
  },
  [10251] = {
    ["D"] = "Gestern Abend haben uns ein paar Draenei einen Besuch abgestattet. Sie haben irgendwie erfahren, dass wir das Buch in unserem Besitz haben und uns das großzügige Angebot gemacht, es uns abzunehmen.$B$BWir haben sie nicht gefragt, was sie mit dem Buch wollen, aber der Jüngere sprach die ganze Zeit vom \"großen Plan des Meisters\", was immer das zu bedeuten hat.$B$BIch stelle keine Fragen, wenn es ums Geld geht. Sie haben uns viel Gold geboten und wir haben ihnen das Buch gegeben.$B$BEr sagte, er lebe in Nagrand, im alten verlassenen Rüstlager nördlich von Telaar.",
    ["O"] = "Sucht Nitrin den Gelehrten im verlassenen Rüstlager in Nagrand.",
    ["T"] = "Der große Plan des Meisters?",
  },
  [10252] = {
    ["D"] = "Es hat schon begonnen. Levixus steht auf den Ruinen von Auchindoun und liest aus dem Buch der Toten. Er ruft die Toten beim Namen und unterwirft die gequälten Seelen durch ein dämonisches Ritual seinem Willen. Wenn er kann, wird er ganz Auchindoun erwecken!$B$BAber... Ihr müsst Euch erst vorbereiten. Ihr könnt nicht mit etwas kämpfen, dass Ihr nicht seht.$B$B<Nitrin reicht Euch ein Pergament.>$B$BBringt mir die Gegenstände, die ich für die Tinktur benötige. Sie wird Euren Geist erweitern und Euch ermöglichen, die Geisterwelt zu sehen.",
    ["O"] = "Nitrin im verlassenen Rüstlager in Nagrand möchte, dass Ihr ihm 1 Augapfel eines Berggronns, 1 fehlerloser Schnabel eines großen Windrocs und 1 Tran eines gealterten Grollhufs bringt.",
    ["T"] = "Vision der Toten",
  },
  [10253] = {
    ["D"] = "Geht jetzt, $N. Ihr könnt Levixus auf den höchsten Ruinen von Auchindoun finden. Nähert Euch vom Süden der Knochenwüste. Er befindet sich mitten in einem Sturm von Seelen...$B$BVernichtet ihn und nehmt ihm das Buch der Toten ab. Wenn Ihr das verfluchte Buch in Eurem Besitz habt, dann zerstört es! Zerstört es, damit eine solche Grausamkeit nie wieder geschehen kann.",
    ["O"] = "Vernichtet Levixus und nehmt Euch das Buch der Toten aus seinem Besitz.$B$BBringt das Buch der Toten zu Ramdor dem Wahnsinnigen außerhalb der Auchenaikrypta am Eingang zu Auchindoun.$B$BIhr hattet eine Vereinbarung, erinnert Ihr Euch?",
    ["T"] = "Levixus der Seelenrufer",
  },
  [10254] = {
    ["D"] = "<Marschall Isildor spuckt in den Staub.>$B$BNun, es ist an der Zeit, dass Ihr Euch Euren Sold verdient. Meldet Euch sofort bei Truppenkommandant Trollbann in der Feste. Er und seine Berater werden sich sicher freuen, Euch zu sehen.$B$B<Marschall Isildor salutiert teilnahmslos.>$B$BFür die Allianz!",
    ["O"] = "Marschall Isildor möchte, dass Ihr Euch bei Truppenkommandant Danath Trollbann in der Ehrenfeste meldet.",
    ["T"] = "Truppenkommandant Danath",
  },
  [10255] = {
    ["D"] = "Ich glaube, in dem Blut ist etwas, das wir als Grundlage für ein Gegengift verwenden können. Schaut mich nicht an, als hätte ich den Verstand verloren!$B$BDas ist in der Natur ganz normal. Oft kann das Heilmittel aus dem Gift selbst gewonnen werden. Und genau das habe ich vor.$B$BWenn wir die Eber von dem dämonischen Einfluss befreien können, verlieren sie hoffentlich ihre Aggressivität und können uns zeigen, wie wir auch den andern Arten helfen können.",
    ["O"] = "Probiert das cenarische Gegengift an einem bulligen Hölleneber aus und beobachtet die Folgen. Sobald Ihr die Ergebnisse des Experiments habt, kehrt zu Thiah Rotmähne im Posten des Cenarius auf der Höllenfeuerhalbinsel zurück.",
    ["T"] = "Das Gegengift testen",
  },
  [10256] = {
    ["D"] = "Wenn ich zurückblicke, hätte ich eigentlich wissen müssen, dass Kael\'thas\' Magie so widerstandsfähig ist. Er war schon immer ein talentierter Magier, aber nie zuvor habe ich etwas so Mächtiges gesehen, wie die Magie, die er beim Angriff auf den Turm gewirkt hat.$B$BDer Schutzschild verhindert, dass ich die Macht, die er aufrechterhält, ausspionieren kann, aber Ihr könnt das trotzdem.$B$BIhr werdet jedoch einen Fokus brauchen. Apex, ein Riese, der Kristalle mit sich herumträgt, streift auf den Weiten westlich der Manaschmiede Duro umher. Einer seiner Kristalle würde einen ausgezeichneten Fokus abgeben.",
    ["O"] = "Sucht Apex und besorgt Euch Apex\' Kristallfokus. Kehrt dann zum Violetten Turm zurück und benutzt Apex\' Kristallfokus in der Nähe von Erzmagier Vargoths Kugel um herauszufinden, wer den Schlüssel zum Fluch des Turms hat.",
    ["T"] = "Den Schlüsselmeister finden",
  },
  [10257] = {
    ["D"] = "Es überrascht mich nicht, dass Kael\'thas den Schlüssel zu diesem Schutzschild in die Hände von jemandem wie Kommandantin Sarannis gegeben hat. Prinz Kael\'thas mag vielleicht vieles sein, aber sicher kein Narr.$B$BUm diesen Fluch ein für alle Mal zu brechen, müsst Ihr in die Festung der Stürme gehen und den Schlüsselstein besorgen.$B$BWenn der Schlüsselstein zerstört ist, werde ich meine Freiheit wiedererlangen.",
    ["O"] = "Reist zur Botanika der Festung der Stürme und besorgt Euch den Schlüsselstein von Kommandantin Sarannis. Bringt ihn danach zu Erzmagier Vargoth im Violetten Turm.",
    ["T"] = "Den Schlüsselstein erobern",
  },
  [10258] = {
    ["D"] = "Hogarth, der Kommandant des Rüstlagers, befindet sich im östlichen Turm. Ihr müsst ihm von Eurem Sieg über Urtrak berichten. Vielleicht ist er dann freundlich? Natürlich könnte er auch versuchen, Eure Seele zu fressen. Bei Geistern wie diesen weiß man nie.$B$BDie Unerschütterlichen sind uns immer noch feindlich gesonnen und werden versuchen zu töten, wenn sie können. Seid vorsichtig, $N.",
    ["O"] = "Meldet Euch bei Hogarth, dem Kommandanten des Rüstlagers.",
    ["T"] = "Die Gefallenen ehren",
  },
  [10259] = {
    ["D"] = "Die Kämpfe bei dem Portal nehmen kein Ende! Die Argentumdämmerung kämpft in einer verzweifelten Schlacht - sollten wir verlieren, wird das Feuer der Dämonen niemals erlöschen.$B$BHelft uns, $N. Zieht in den Kampf und helft unseren Brüdern und Schwestern. Helft ihnen, sich gegen die Welle der Dunkelheit zu erheben! Die Argentumdämmerung - und die Welt - brauchen Euch!",
    ["O"] = "Tötet 6 einfallende Teufelswachen und kehrt dann zum Argentumabgesandten Stolzbrunn zurück.",
    ["T"] = "In die Bresche springen",
  },
  [10260] = {
    ["D"] = "Ihr müsst über die Hauptbrücke vom Schergrat in den Nethersturm gekommen sein. Habt Ihr dort Netheorologe Kupfernickel mit seiner verrückten Ausrüstung gesehen?$B$BMerkwürdig, ich erwarte einen Bericht von ihm, und er ist schon lange überfällig. Wahrscheinlich hat er es vergessen. Ihr kennt ja diese Wissenschaftler.$B$BGeht zurück zur Brücke und nagelt ihn fest. Lasst Euch nicht mit Entschuldigungen abspeisen, bringt mir einfach seinen Bericht.$B$BDanke!",
    ["O"] = "Sprecht mit Netheorologe Kupfernickel bei der Brücke, die vom Nethersturm zum Schergrat führt.",
    ["T"] = "Netheorologe Kupfernickel",
  },
  [10261] = {
    ["D"] = "GESUCHT: 1 Vernichterservo, um den kaputten in meinem langhalsigen Spektrumanalysator zu ersetzen.$B$BIhr und Eure Freunde sollten bei den Blutelfen in der Manaschmiede B\'naar im Westen nachschauen, ob sie ein paar Ersatzteile haben. Wie es scheint, haben sie dort ein paar Arkanvernichter.$B$BBitte bringt den Servo in unbeschadetem Zustand zu Papa Mauschel in Area 52.",
    ["O"] = "Besorgt einen Vernichterservo und bringt ihn zu Papa Mauschel in Area 52 im Nethersturm.",
    ["T"] = "Gesucht: Vernichterservo!",
  },
  [10262] = {
    ["D"] = "Es gibt noch etwas anders, bei dem ich Eure Hilfe brauche.$B$BEine Gruppe von Astralen, die als Zaxxis bekannt sind, haben ihre Verbindungen zum Konsortium abgebrochen um ihre eigenen Geschäfte zu machen. Sie machen sogar Geschäfte mit den Blutelfen! Das können wir nicht hinnehmen!$B$BIch habe ihre Gruppe bei dem Hügel im Süden unterwandert, aber als ich ihren Anführer ausgeschaltet hatte, musste ich zurück in die Sicherheit von Area 52 fliehen.$B$B$C, geht dorthin und bringt mir ihre Insignien. Natürlich tragen nicht alle Insignien, weil sie Feiglinge sind.",
    ["O"] = "Sammelt 10 Insignien der Zaxxis und kehrt dann zu Netherpirscher Khay\'ji in Area 52 im Nethersturm zurück.",
    ["T"] = "Ein Hügel voll Astraler",
  },
  [10263] = {
    ["D"] = "Die Naaru von Shattrath haben versucht, dem Konsortium beizubringen, die Dinge auf ihre Weise zu sehen.$B$BDas Glück wollte, dass die Geschäftsbeziehungen zwischen den Astralen und den Blutelfen im Nethersturm sich in letzter Zeit drastisch verschlechtert haben.$B$BIm Dorf gibt es einen Mitarbeiter des Konsortiums namens Khay\'ji, der vor Kurzem ein paar Probleme mit einer Truppe von abtrünnigen Astralen hatte. Als Mitglied der Aldor möchte ich Euch bitten, ihm zu helfen, wo Ihr nur könnt.",
    ["O"] = "Sprecht mit Netherpirscher Khay\'ji in Area 52 im Nethersturm.",
    ["T"] = "Dem Konsortium helfen",
  },
  [10264] = {
    ["D"] = "Die Naaru von Shattrath haben versucht, dem Konsortium beizubringen, die Dinge auf ihre Weise zu sehen.$B$BDas Glück wollte, dass die Geschäftsbeziehungen zwischen den Astralen und den Blutelfen im Nethersturm sich in letzter Zeit drastisch verschlechtert haben.$B$BIm Dorf gibt es einen Mitarbeiter des Konsortiums namens Khay\'ji, der vor Kurzem ein paar Probleme mit einer Truppe von abtrünnigen Astralen hatte. Als Mitglied der Seher möchte ich Euch bitten, ihm zu helfen, wo Ihr nur könnt.",
    ["O"] = "Sprecht mit Netherpirscher Khay\'ji in Area 52 im Nethersturm.",
    ["T"] = "Dem Konsortium helfen",
  },
  [10265] = {
    ["D"] = "Unser Anführer, Nexusprinz Haramad, hat mich gebeten, ein Kristallartefakt aus den Ruinen von Arklon im Südosten zu besorgen.$B$BEs sind alte Draeneiruinen. Zum Pech für mich und die Goblins hier hat die Brennende Legion beschlossen, selbst danach zu graben.$B$BWenn Ihr mir weiterhin helfen wollt, nehmt dem Schreckenslord namens Pentatharon das Kristallartefakt ab.",
    ["O"] = "Besorgt ein Kristallartefakt von Arklon und bringt es zu Netherpirscher Khay\'ji in Area 52 im Nethersturm.",
    ["T"] = "Kristallsammlung des Konsortiums",
  },
  [10266] = {
    ["D"] = "Während Ihr in den Ruinen von Arklon wart, habe ich eine Bitte um Unterstützung von einem unserer Mitarbeiter im Feld erhalten. Wie es scheint hat das Konsortium einige Probleme damit, Waren von den Blutelfen zurückzufordern, die sie nicht bezahlen wollen.$B$BBitte begebt Euch von Area 52 aus nach Osten und überquert die Brücke zur nächsten Insel. Ihr findet meinen Gefährten Gahruj im Mittelreichposten in der Biokuppel Mittelreich.",
    ["O"] = "Sucht Gahruj und bietet ihm Eure Hilfe an. Er befindet sich im Mittelreichposten in der Biokuppel Mittelreich im Nethersturm.",
    ["T"] = "Bitte um Unterstützung",
  },
  [10267] = {
    ["D"] = "Unsere Geschäftsbeziehungen mit den Blutelfen scheinen zu Ende zu sein. Vor ein paar Wochen wurde ihnen bei der Manaschmiede Duro eine große Ladung mit Vermessungsausrüstung geliefert. Sie haben sich geweigert, dafür zu bezahlen.$B$B$N, ich möchte, dass Ihr den Blutelfen in der Manaschmiede Duro im Osten einen Besuch abstattet. Sie scheinen ein paar Probleme mit angreifenden Manakreaturen zu haben, also nutzt aus, dass sie beschäftigt sind.$B$BMir ist egal, was mit ihnen passiert, aber ich hätte meinen rechtmäßigen Besitz gerne zurück.",
    ["O"] = "Sammelt 10 Kästen mit Vermessungsausrüstung und bringt sie zu Gahruj im Mittelreichposten in der Biokuppel Mittelreich im Nethersturm.",
    ["T"] = "Rechtmäßiger Besitz",
  },
  [10268] = {
    ["D"] = "Nexusprinz Haramad hat mir zu verstehen gegeben, dass er seinen Besitz unverzüglich und persönlich zurückerhalten möchte. Ich wäre Euch sehr dankbar, wenn Ihr ihm die Waren an meiner statt zurückbringen könntet.$B$BEine Audienz beim Nexusprinzen ist eine große Ehre, und ich bin mir sicher, dass er sehr gerne etwas über Eure Abenteuer im Nethersturm erfahren würde.$B$BSucht ihn in seiner Kammer oben in der Sturmsäule im Norden auf. Er ist oft nur als holografisches Bild anwesend, dass Ihr ansprechen könnt.",
    ["O"] = "Bringt die Vermessungsausrüstung zum Bild von Nexusprinz Haramad in der Sturmsäule im Nethersturm.",
    ["T"] = "Eine Audienz beim Prinzen",
  },
  [10269] = {
    ["D"] = "Da ist noch ein anderer Grund, warum ich die Vermessungsausrüstung zurückhaben wollte. Es gibt da einen Kristall aus einer alten Draeneilegende, der mir sehr wichtig ist.$B$BMan sagt, dass der Kristall seinem Träger gottgleiche Macht verleiht! Aber es besteht die Gefahr, dass jemand anderes ihn ausgräbt, nämlich die Brennende Legion.$B$B$N, nehmt die Ausrüstung und benutzt sie, um den ersten Triangulationspunkt zu finden. Wir wissen, dass er sich irgendwo östlich der Manaschmiede Ultris befinden muss.$B$BMeldet Euch danach bei meinem Agenten, Händler Hazzin, auf der Insel der Manaschmiede Ultris.",
    ["O"] = "Benutzt das Triangulationsgerät, um den Weg zum ersten Triangulationspunkt zu bestimmen. Wenn Ihr ihn gefunden habt, meldet die Position an Händler Hazzin beim Wachposten des Protektorats auf der Insel der Manaschmiede Ultris im Nethersturm.",
    ["T"] = "Triangulationspunkt Eins",
  },
  [10270] = {
    ["D"] = "Aus Eurem Gesicht kann ich lesen, dass Ihr $Gein:eine:r; $R mit Sinn fürs Geschäft seid.$B$BIch habe mir einen Ruf als Händler für exotische und teure Waren erarbeitet, und bin gerade dabei, ein neues Unternehmen zu starten.$B$BIch könnte einen fähigen Unterhändler brauchen, um die Waren vom Sternensturz weiterzuleiten.$B$BZuerst müsst Ihr eine Teleporterplattform wieder aufladen. Im Zugangsschacht Zeon auf der südlichen Seite der Manaschmiede Ultris findet Ihr eine Ersatzenergiezelle. Das Protektorat befindet sich zurzeit dort, deshalb könnt Ihr einfach ihren Signalgebern zu Eurem Ziel folgen.",
    ["O"] = "Besorgt Euch die Teleporterenergiezelle und bringt sie zur Astralteleportplattform im Sternensturz östlich der Manaschmiede Ultris. Aktiviert die Plattform und bringt die Teleporterenergiezelle zu Windhändler Marid, damit er sie einbauen kann.",
    ["T"] = "Ein nicht-so-bescheidenes Angebot",
  },
  [10271] = {
    ["D"] = "Wir Ihr sicher gesehen habt, wimmelt es in dieser Gegend von Netherdrachen aller Altersstufen. Alle Netherdrachen, die jungen wie die alten, verfügen über eine Essenz, die bei K... - ähh, meinem Kunden sehr begehrt ist.$B$BDie Meisten sind nicht bereit, dafür ihr Leben aufs Spiel zu setzen, deswegen haben wir die Gegend hier ganz für uns alleine. Ihr könnt damit anfangen, die Essenz von allen Drachen hier zu sammeln, aber es ist von größter Notwendigkeit, dass wir sofort einen steten Nachschub aufbauen. Kehrt zu mir zurück, wenn Ihr eine erste Ladung zusammen habt.",
    ["O"] = "Ladet den Essenzsammler auf, indem Ihr ihn bei Netherdrachen und Nethergroßdrachen auf dem Sternensturz anwendet, und kehrt dann zu der Gegend um die Astralteleportplattform zurück.",
    ["T"] = "Zur Sache",
  },
  [10272] = {
    ["D"] = "Wir sind hierhergekommen, um den Gerüchten über eine merkwürdige Drachenbrut, die vielleicht die Nachkommen von Todesschwinge selbst sein könnten, nachzugehen. Ganz offensichtlich handelt es sich nicht um gewöhnliche schwarze Drachen.$B$BWenn wir eine Gelegenheit hätten, ihre Eier zu untersuchen, könnten wir sicher mehr über sie herausfinden. Ihr Hass auf meinesgleichen sitzt so tief, dass ich nicht nahe genug herangehen kann, um die Eier selbst zu sammeln. Werdet Ihr mir helfen?",
    ["O"] = "Sammelt 15 Netherdrachkineier und kehrt dann zu Tyri und Jorad im Sternensturz zurück.",
    ["T"] = "Ein viel versprechender Anfang",
  },
  [10273] = {
    ["D"] = "Your former employer, Wind Trader Marid, must\'ve gotten wind that you\'re helping us now. Through my projection, I\'ve seen his nether-stalkers entering the area, hoping to track us down.$B$BWe\'ve been able evade them so far, but Marid must be dealt with before the problem grows more severe.$B$BIf your impressions of Marid are correct, he\'ll do just about anything to secure the supply of his product. Return to him and tell him you\'re prepared to offer a deal. Lure him away and slay him.",
    ["O"] = "Sprecht mit Windhändler Marid und lockt ihn von seinem Handelsposten fort. Vernichtet ihn und kehrt dann zu Tyri und Jorad im Sternensturz zurück.",
    ["T"] = "Störende Ablenkung",
  },
  [10274] = {
    ["D"] = "Die Netherdrachen verfügen über dasselbe wilde Temperament wie ihre Vorfahren, die schwarzen Drachen. Etwas an ihnen ist jedoch vollkommen anders. Sie schlüpfen schnell und werden schnell reif, indem sie die fremdartigen Energien, die ihre Art zur Zerstörung Draenors getrieben hat, nutzen.$B$BEs gibt so Vieles, was ich noch über sie in Erfahrung bringen muss, aber ich glaube, dass die Mächte für meinen eigenen Schwarm von Vorteil sein könnten. Wenn wir den Anführer dieser Drachen, Veraku, als Zeichen unserer Dominanz vernichten könnten, könnten wir den Rest vielleicht so einschüchtern, dass sie sich uns unterwerfen.",
    ["O"] = "Benutzt die Herausforderung des blauen Drachenschwarms um Veraku am südlichen Ende des Sternensturzes zu provozieren. Besiegt ihn und kehrt dann zu Tyri und Jorad im Sternensturz zurück.",
    ["T"] = "Den Sternensturz sichern",
  },
  [10275] = {
    ["D"] = "Ich habe das Triangulationsgerät mit den Informationen des ersten Punkts neu eingestellt. Ihr könnt es genauso wie beim ersten Mal benutzen, um den zweiten Punkt zu bestimmen.$B$BAlles, was ich Euch sagen kann, ist, dass der zweite Punkt sich weit im Westen befindet.$B$BWenn Ihr ihn gefunden habt, sprecht mit Windhändler Tuluman, ein weiterer Agent des Nexusprinzen. Er steht als Waffenhändler getarnt in einem kleinen Handelsposten neben der Brücke, die zur Insel, auf der sich die Manaschmiede Ara befindet, führt.",
    ["O"] = "Benutzt das Triangulationsgerät, um den Weg zum zweiten Triangulationspunkt zu bestimmen. Wenn Ihr ihn gefunden habt, meldet die Position an Windhändler Tuluman auf Tulumans Landeplatz auf der anderen Seite der Brücke zu der Insel der Manaschmiede Ara im Nethersturm.",
    ["T"] = "Triangulationspunkt Zwei",
  },
  [10276] = {
    ["D"] = "Mit den Daten vom ersten und zweiten Punkt kann ich sicher sagen, dass der Kristall sich auf den Ruinen von Farahlon im Nordosten befindet.$B$BUnglücklicherweise sagen die Daten, dass der Kristall sich bewegt, was bedeutet, dass er bereits gefunden wurde. Wahrscheinlich von der Brennenden Legion!$B$BFalls das tatsächlich so ist, empfehle ich Euch, ein paar Freunde mitzunehmen.$B$BTötet den Dämon, der den Kristall hat, und bringt ihn zu Nexusprinz Haramad.",
    ["O"] = "Besorgt Euch den Kristall von Ata\'mal und bringt ihn zu Nexusprinz Haramad in der Sturmsäule im Nethersturm.",
    ["T"] = "Das volle Dreieck",
  },
  [10277] = {
    ["D"] = "Grüße, $N. Bitte folgt der Bewahrerin. Sie wird ein wenig... Licht in die Angelegenheit bringen.",
    ["O"] = "Andormu in den Höhlen der Zeit möchte, dass Ihr der Bewahrerin der Zeit durch die Höhle folgt.",
    ["T"] = "Die Höhlen der Zeit",
  },
  [10278] = {
    ["D"] = "Ich bin lange auf der Suche nach der Wahrheit der Leere durch die Scherbenwelt gereist. Jetzt bin ich alt und schwach. Ich sehe merkwürdige Dinge und bin nicht mehr ich selbst. Vielleicht wollt Ihr mir helfen?$B$BIch muss die Geheimnisse der Leerwandler aufdecken. Westlich des Expeditionsrüstlagers befindet sich ein Ort namens Sphärenfelder. Zeit und Raum sind dort sehr schwach, da die Felder nahe beim Abgrund liegen. Geht dorthin und benutzt den instabilen Sphärenrissgenerator. Er wird einen Sphärenriss erzeugen. Ihr müsst den Sphärennether von den Kreaturen, die durch ihn herauskommen, sammeln.",
    ["O"] = "Benutzt den instabilen Sphärenrissgenerator in den Sphärenfeldern. Sammelt 3 Einheiten Sphärennether von instabilen Leerwandlern und kehrt dann zu Ogath dem Verrückten im Rückenbrecherposten zurück.",
    ["T"] = "Sphärenrisse",
  },
  [10279] = {
    ["D"] = "Ah, $N. Wir haben Euch erwartet. Bitte erlaubt, dass einer meiner Brüder Euch zum Meister fliegt. Er wartet unten auf Euch...",
    ["O"] = "Sprecht mit Andormu in den Höhlen der Zeit.",
    ["T"] = "Zum Hort des Meisters",
  },
  [10280] = {
    ["D"] = "Ich bin ein Astraler, der sein Wort hält, $N. Ihr habt mich wirklich inspiriert.$B$BIch werde Euch den Kristall von Ata\'mal geben, damit Ihr ihn den Naaru überreicht. Da Euer Heldenmut dies möglich gemacht hat, soll Euch die Ehre zuteilwerden, ihn zu übergeben.$B$BIhr könnt meinen persönlichen Teleporter benutzen, um direkt nach Shattrath zu gelangen. Tretet einfach hindurch und er wird Euch dorthin bringen.$B$BGebt den Kristall dem Naaru namens A\'dal auf der Terrasse des Lichts.",
    ["O"] = "Bringt den Kristall von Ata\'mal zu A\'dal auf der Terrasse des Lichts in Shattrath.",
    ["T"] = "Sonderlieferung nach Shattrath",
  },
  [10281] = {
    ["D"] = "Grüße, $N. Keine Angst, dies ist nur eine Projektion meiner gegenwärtigen Form. Es ist die einzige Möglichkeit, wie mein Gefährte und ich Wache halten können, ohne uns selbst zu offenbaren.$B$BDer Astrale, der Euch angesprochen hat, Marid, ist nur ein Schwindler, der sich als anständiger Händler ausgibt. Überlegt Euch daher, ob Ihr nicht lieber mit mir zusammenarbeiten wollt. Mein Ziel ist es, mehr über die ungewöhnlichen Drachkin des Sternensturzes herauszufinden. Trefft mich am nördlichen Ende des Sturzes, damit wir über die Details sprechen können.",
    ["O"] = "Sucht Tyri am nördlichen Ende des Sternensturzes und sprecht mit ihr.",
    ["T"] = "Formelle Vorstellung",
  },
  [10282] = {
    ["D"] = "Im Westen findet Ihr die Pfade der Zeit in das Hügelland der Vergangenheit - vor 7 Jahren um genau zu sein.$B$BDie Streitmächte kämpfen gegen den Kriegshäuptling der Horde, Thrall, um ihn davon abzuhalten, aus der Burg Durnholde zu fliehen.$B$BWenn es Thrall nicht gelingt zu fliehen, wird die Welt, wie Ihr sie jetzt kennt, aufhören zu existieren.$B$BRuft eine Gruppe von Abenteurern zusammen und reist auf dem Pfad der Zeit ins Alte Hügelland. Zwei unserer Agenten warten dort auf Euch.$B$BSprecht mit Erozion auf der anderen Seite.",
    ["O"] = "Andormu in den Höhlen der Zeit bittet Euch, ins Alte Hügelland zu reisen und mit Erozion zu sprechen.",
    ["T"] = "Das Alte Hügelland",
  },
  [10283] = {
    ["D"] = "Taretha spielte eine entscheidende Rolle bei Thralls Überleben und Erziehung und prägte Thralls Leben maßgeblich.$B$BEs war Taretha, die die Wachen von Durnholde ablenkte, damit Thrall entkommen konnte.$B$BJetzt haben die Ewigen Taretha gefangen und halten sie in Tarrens Mühle fest. Ohne unsere Einmischung wird das Ablenkungsmanöver niemals stattfinden. Ihr müsst den Brandsatz, den ich Euch gegeben habe, nehmen und die Internierungsbaracken in Durnholde anzünden. Wenn Ihr das erledigt habt, sprecht mit dem jungen Thrall im Kellergefängnis der Burg.",
    ["O"] = "Reist zur Burg Durnholde und platziert mithilfe des Bündels mit Brandbomben, das Ihr von Erozion erhalten habt, 5 Brandsätze auf den Fässern in jeder Internierungsbaracke.$B$BSprecht mit Thrall im Kellergefängnis der Burg Durnholde, wenn Ihr die Internierungsbaracken angezündet habt.",
    ["T"] = "Tarethas Ablenkungsmanöver",
  },
  [10284] = {
    ["D"] = "Ihr schaut mich an und seht nur einen Jungen, aber tief in meinem Herz und meiner Seele halte ich meine Träume versteckt. Ich habe Pläne für mein Volk, Fremder. Eines Tages werden sie ihre Unterdrücker überwältigen und in Einheit auferstehen. Sie werden ihre Fesseln abwerfen und aus diesem Aufstand soll die orcische Horde entstehen.$B$BIch verspreche Euch, Fremder, dass ich Euch suchen werde, wenn wir heil hier herauskommen und ich die Möglichkeiten habe, Eure selbstlose Tat zu belohnen... Ich werde Euch finden.$B$B<Thrall nickt.>$B$BWir fliehen, wenn Ihr bereit seid.",
    ["O"] = "Gebt Thrall Bescheid, wenn Ihr bereit seid. Folgt Thrall aus der Burg Durnholde und helft ihm, Taretha zu befreien und sein Schicksal zu erfüllen.$B$BSprecht mit Erozion im Alten Hügelland, wenn Ihr diese Aufgabe erfüllt habt.",
    ["T"] = "Flucht aus Durnholde",
  },
  [10285] = {
    ["D"] = "Diese Zeitachse wurde wiederhergestellt und der Angriff des ewigen Drachenschwarms auf das Hügelland vorerst abgewendet.$B$BKehrt sofort zu Andormu zurück. Er wartet im Hort des Meisters auf Eure Ankunft.",
    ["O"] = "Kehrt zu dem jungen Andormu in den Höhlen der Zeit in Tanaris zurück.",
    ["T"] = "Rückkehr zu Andormu",
  },
  [10286] = {
    ["D"] = "Arelions Tagebuch enthält überhaupt keine wissenschaftlichen Studien. Er beschreibt seine Eskapaden mit einer jungen Blutelfe! Wie konnte ich nur so dumm sein?$B$BUnglücklicherweise nennt das Tagebuch den Namen des Flittchens nicht. Aledis, ein Kollege von Arelion, war sein bester Freund und Vertrauter. Befragt ihn und findet heraus, mit wem er eine Affäre hatte.$B$BIch habe gesehen, wie Aledis sich vor Kurzem aus der Falkenwacht in Richtung Zangarmarschen aufgemacht hat. Folgt der Straße nach Norden aus dem Dorf heraus. Sie biegt nach einer Weile nach Westen in Richtung Zangarmarschen ab.",
    ["O"] = "Magistrix Carinda möchte, dass Ihr Magister Aledis befragt. Aledis wurde zuletzt auf einer Reise von der Falkenwacht zu den Zangarmarschen gesehen.",
    ["T"] = "Arelions Geheimnis",
  },
  [10287] = {
    ["D"] = "Ich bin Arelions Freund, aber ich werde für ihn nicht die Prügel meines Lebens einstecken. Seine Geliebte ist Viera Sonnenwisper.$B$BSagt das seinem Drachen von Ehefrau. Was will sie schon dagegen tun?",
    ["O"] = "Return to Magistrix Carinda at Falcon Watch.",
    ["T"] = "Die enttarnte Geliebte",
  },
  [10288] = {
    ["D"] = "Ich möchte, dass Ihr mit Amish Wildhammer sprecht. Ihr findet ihn etwa 30 Schritt südlich von hier. Nehmt den ersten Flug zur Ehrenfeste. Wenn Ihr dort angekommen seid, werdet Ihr von Marschall Isildor begrüßt werden. Gebt ihm diese Empfehlung, in der ich notiert habe, wo Ihr meiner Meinung nach am hilfreichsten sein könntet.$B$BIch habe Euch einen Befehl erteilt, Soldat, nun fort mit Euch! Und auch wenn Ihr darauf brennt, uns im Kampf gegen den Feind hier zu unterstützen, dürft Ihr keine Zeit verschwenden. Die Ehrenfeste braucht Euch sogar noch mehr als wir.",
    ["O"] = "Kommandant Duron beim Dunklen Portal auf der Höllenfeuerhalbinsel möchte, dass Ihr Durons Bericht zu Flugmeister Amish Wildhammer bringt.",
    ["T"] = "Ankunft in der Scherbenwelt",
  },
  [10289] = {
    ["D"] = "Wenn Ihr bereit seid, rufe ich einen Wyvern, der Euch nach Thrallmar bringt. Wenn Ihr angekommen seid, bringt Orions Bericht zu General Karkork.$B$BLok\'tar ogar, $N - Viel Glück.",
    ["O"] = "Nehmt Vlagga Freyfeders Windreiter nach Thrallmar. Bringt Orions Bericht zu General Krakork.",
    ["T"] = "Reise nach Thrallmar",
  },
  [10290] = {
    ["D"] = "Im Nordosten gibt es eine Region, die Netherstein genannt wird. Früher war sie unter einem fruchtbaren und grünen Land verborgen. Die neu gefundenen Kristallformationen haben uns ein Mineral gezeigt, das wir zur Ehre der früheren Identität des Landes Farahlit genannt haben.$B$BDie kleine Menge Farahlit, die wir bis jetzt abgebaut haben, erfreut sich jetzt schon großer Beliebtheit bei unseren Kunden. Das hochwertigste Mineral stammt jedoch nicht von den Ablagerungen, sondern von den Riesen, die um den Netherstein herum leben. Ich biete Euch einen Teil des Gewinns an, wenn Ihr mir helft, hochwertiges Farahlit zu besorgen.",
    ["O"] = "Sammelt 4 Stück Rohfarahlit und kehrt dann zu Zuben Elgenubi in der Sturmsäule zurück.",
    ["T"] = "Auf der Suche nach Farahlit",
  },
  [10291] = {
    ["D"] = "Auch wenn unsere Krieger keine ebenbürtigen Gegner haben, ist unsere Stellung in diesem Ödland immer noch angreifbar. Meldet Euch bei Nazgrel in der Kaserne. Er braucht gute Agenten... wie Euch. Wenn wir unsere Stellung hier nicht halten können, sind wir für niemanden eine Hilfe! Geht jetzt.",
    ["O"] = "General Krakork in Thrallmar möchte, dass Ihr mit Nazgrel sprecht.",
    ["T"] = "Bericht an Nazgrel",
  },
  [10292] = {
    ["D"] = "Ein wertvolles Gut in kleinen Häppchen zu sammeln hat das malerische Flair eines Familienunternehmens, aber auf dem heutigen Markt ist es einfach nicht konkurrenzfähig.$B$BNein, wir brauchen einen Weg, Farahlit in Massenproduktion zu beschaffen! Dann wäre der Hauptanteil des Marktes unser!$B$BIch habe eine Idee, aber sie erfordert eine mächtige, konzentrierte Energiequelle. Solch eine Energiequelle kann man nur an einem Ort finden. Geht zu den Lagern der Legion nördlich von hier und besorgt Euch ein paar ihrer Schusswaffen.",
    ["O"] = "Zuben Elgenubi möchte, dass Ihr 8 Schusswaffen der Legion besorgt und sie zur Sturmsäule bringt.",
    ["T"] = "Mehr Energie!",
  },
  [10293] = {
    ["D"] = "Jetzt, da wir ein solides Grundprodukt haben, benötigen wir etwas von höherer Qualität. Das Farahlit, das wir bisher gesammelt haben, ist zwar ganz nett, aber es gibt noch besseres da draußen.$B$BWir müssen eine Probe direkt von Felsnarbe, dem Patriarchen der Riesen von Farahlon, besorgen! Ihr solltet ihn bei den anderen Riesen in der Gegend um den Nethersteine herum finden können. Aus seinem Rücken ragen große Farahlitstücke heraus; genau was wir brauchen. Tötet den Riesen und sammelt einen Farahlitkern von ihm.",
    ["O"] = "Zuben Elgenubi in der Sturmsäule möchte, dass Ihr einen Farahlitkern besorgt.",
    ["T"] = "Die Kristallader anzapfen",
  },
  [10294] = {
    ["D"] = "Wir müssen meinen Sphärenrissgenerator verbessern. Ihr müsst ihn zum Leerengrat bringen. Geht von hier aus nach Osten, um die verdorbene Festung Zeth\'Gor herum. Im Osten hinter Zeth\'Gor befindet sich ein kleiner Pass durch die Berge. Hinter diesem Pass findet Ihr den Leerengrat, einen schrecklichen Ort direkt am Abgrund.$B$BIhr müsst die Wandler, die Ihr dort findet, töten und mir Splitter ihrer Seele bringen. Mit ihrer Macht werden wir den Sphärenrissgenerator verbessern und ein Portal in die jenseitige Dimension öffnen, das nicht mehr geschlossen werden kann.",
    ["O"] = "Geht zum Leerengrat und tötet die Kreaturen, die Ihr dort findet. Sammelt 40 Seelensplitter des Leerengrats und kehrt dann zu Ogath dem Verrückten im Rückenbrecherposten zurück.",
    ["T"] = "Der Leerengrat",
  },
  [10295] = {
    ["D"] = "Mein Sphärenrissgenerator ist fertig. Ihr könnt damit jetzt einen wahren Prinzen der Leere rufen, eine Kreatur, deren Seelensplitter uns deutlich mehr darüber verraten wird, was dahinter liegt.$B$BGeht nach Süden in den Leerengrat und benutzt meinen Sphärenrissgenerator in der Nähe der Steine der Leere. Haltet nach einer Gruppe von Kristallen am Ende des Abgrunds Ausschau. Ein riesiger Sphärenriss wird sich auftun, aus dem dann sicher der Baron der Leere Galaxis entsteigen wird.$B$BZerstört diese Kreatur und bringt mir seinen Seelensplitter.",
    ["O"] = "Tötet Galaxis und sammelt seinen Seelensplitter. Bringt den Splitter zu Ogath dem Verrückten im Rückenbrecherposten.",
    ["T"] = "Aus dem Abgrund",
  },
  [10296] = {
    ["D"] = "Der ewige Drachenschwarm versucht nun nicht einmal mehr, seine Pläne geheim zu halten. Sie haben vor, den Wächter, Medivh, zu töten und zu verhindern, dass das Dunkle Portal jemals geöffnet wird. Auf den ersten Blick scheint das ein Gefallen für die Streitkräfte der Allianz zu sein, aber tatsächlich wären die Folgen katastrophal und würden in der Zerstörung Eurer Welt enden.$B$BIhr müsst auf dem Pfad der Zeit zum Schwarzen Morast reisen. Geht zuerst nach Westen und dann nach Süden, vorbei an meinem zukünftigen Ich, bis Ihr zu dem Tunnel kommt, in dem die Sumpfwesen leben. Durchquert ihn und sprecht mit Sa\'at.",
    ["O"] = "Reist durch die Höhlen der Zeit in den Schwarzen Morast während der Öffnung des Dunklen Portals und sprecht mit Sa\'at.",
    ["T"] = "Der Schwarze Morast",
  },
  [10297] = {
    ["D"] = "Hinter diesem Pfad der Zeit, im Herzen des Schwarzen Morasts, arbeitet Medivh verbissen an der Öffnung des Dunklen Portals.$B$BÜberall im Morast werden sich Zeitlöcher öffnen, aus denen die Ewigen herausströmen werden mit dem Ziel, Medivhs Schilde und schließlich auch Medivh selbst zu vernichten.$B$BTötet die Lords des Zeitlochs und ihre Anführer, bis Medivh erfolgreich war.",
    ["O"] = "Sa\'at im Schwarzen Morast der Höhlen der Zeit möchte, dass Ihr Medivh verteidigt, bis er das Dunkle Portal geöffnet hat.$B$BKehrt zu Sa\'at zurück, wenn Ihr erfolgreich wart.",
    ["T"] = "Die Öffnung des Dunklen Portals",
  },
  [10298] = {
    ["D"] = "Kehrt zum Kind, Andormu, zurück, $N. Er erwartet Euch außerhalb dieser Zeitfalte im Hort des Meisters.$B$BIch höre Flügelschlagen. Die Wächter der Sande interessieren sich für Eure Fähigkeiten. Vielleicht möchten auch sie mit Euch sprechen.",
    ["O"] = "Kehrt zum Kind, Andormu, in den Höhlen der Zeit in Tanaris zurück.",
    ["T"] = "Held des Schwarms",
  },
  [10299] = {
    ["D"] = "Wir können die Manaschmieden nicht mit Gewalt abschalten, da wir sonst eine Explosion auslösen könnten, die die Scherbenwelt bis in den Kern erschüttern würde.$B$BLaut Eurer Abschrift gibt es einen Notschalter, der die Technologie dauerhaft abschaltet - ein Sicherheitsmechanismus, der bei der Technologie der Naaru Standard ist.$B$BFür diesen Vorgang braucht Ihr einen Kristall, der Euch die Kommunikation mit der Konsole ermöglicht. Dieser Zugangskristall befindet sich unseren Informationen zufolge im Besitz eines Vorarbeiters namens Theredis.",
    ["O"] = "Anachoret Karja möchte, dass Ihr in die Manaschmiede B\'naar geht und einen Zugangskristall von Vorarbeiter Theredis besorgt. Benutzt ihn bei der Steuerkonsole von B\'naar, um sie abzuschalten und meldet Euch danach bei Anachoret Karja zurück.$B$BDas Ausführen von Quests für die Aldor führt zu einer Verringerung Eures Rufs bei den Sehern.",
    ["T"] = "Manaschmiede B\'naar abschalten",
  },
  [10300] = {
    ["D"] = "<Ravandwyr untersucht den Stab und bemerkt, dass das Kopfstück fehlt.>$B$BDas ist einfach barbarisch! Ekkorash muss das kristallene Kopfstück abgenommen haben, um es mit einem anderen Schmuckstück zu verwenden, und hat gar nicht gemerkt, dass es zum Rest des Stabs dazugehört!$B$BSolange er nicht komplett ist, ist die Macht des Stabs stark verringert. Ich könnte ihn jedoch mit einem geeigneten Draeneikristall reparieren. Ich habe gehört, wie die Goblins von den Kristallen der Geister in den Ruinen von Enkaat im Norden gesprochen haben.",
    ["O"] = "Besorgt 4 fehlerlose Kristallsplitter von den Geistern in den Ruinen von Enkaat und bringt sie zu Ravandwyr in Area 52.",
    ["T"] = "Den Stab wiederherstellen",
  },
  [10301] = {
    ["D"] = "Auch wenn ich froh bin, dass ich das meiste von Krasus\' Kompendium zurück habe, ist es ohne das heliotrope Oculus nutzlos. Seht Ihr, Krasus war sich der unermesslichen Macht des Buchs bewusst und hat Vorkehrungen getroffen, damit es vor gierigen Blicken geschützt bleibt.$B$BNur ein Magier mit großem Wissen kann dem Kompendium mithilfe des Oculus\' seine Geheimnisse entlocken.$B$BWährend Kael\'thas\' Angriff hat einer seiner Leutnants, Zauberhäscherin Marathelle, das Octulus aus dem Turm gestohlen und es zum Sonnenzornposten gebracht. Sie hält es sicher für ein hübsches kleines Schmuckstück.",
    ["O"] = "Obtain the Heliotrope Oculus from Spellreaver Marathelle at Sunfury Hold. Use Archmage Vargoth\'s Staff to contact the archmage once you have achieved your goal.$B$BIf you lose Archmage Vargoth\'s Staff, speak to Ravandwyr at Area 52 to receive a replacement.",
    ["T"] = "Das Kompendium öffnen",
  },
  [10302] = {
    ["D"] = "Die verstrahlten Kraftkerne der Exodar haben der Umwelt hier verheerenden Schaden zugefügt. Die deutlichste Auswirkung ist das Auftauchen merkwürdiger Kreaturen, die anscheinend in ihrer Nähe wie Pilze aus dem Boden schießen.$B$BVielleicht gibt es etwas, was wir für sie tun können, aber im Augenblick sind sie einfach zu viele für uns.$B$BIch möchte, dass Ihr losgeht und ihre Zahl reduziert. Vielleicht fällt mir danach eine andere Lösung ein.",
    ["O"] = "Tötet 8 unberechenbare Mutanten und kehrt dann zu Botanikerin Taerix bei der Absturzstelle im Am\'mental zurück.",
    ["T"] = "Unberechenbare Mutanten",
  },
  [10303] = {
    ["D"] = "Ihr müsst... die Bedrohung durch die Blutelfen... auslöschen. Findet heraus... was sie... vorhaben!$B$BSie sind auf dem... Kamm hinter mir.",
    ["O"] = "Tötet 10 Blutelfenspäher und kehrt dann zu Tolaan im Schattenkamm im Am\'mental zurück.",
    ["T"] = "Die Blutelfen",
  },
  [10304] = {
    ["D"] = "Mit Eurer Hilfe konnten wir schneller, als wir gehofft hatten, eine Lösung für unser Problem finden.$B$BIch habe keine weitere Arbeit für Euch, meine Lehrlinge schaffen den Rest alleine.$B$BIhr solltet mit Verteidiger Aldar auf der anderen Seite des Schiffs sprechen.",
    ["O"] = "Sprecht mit Verteidiger Aldar bei der Absturzstelle im Am\'mental.",
    ["T"] = "Verteidiger Aldar",
  },
  [10305] = {
    ["D"] = "Dieser abgenutzte, dicke Foliant ist kein Zauberbuch, wie Ihr es vielleicht von einem Magier der Kirin Tor erwartet hättet. Die Seiten dieses Bandes sind voll von handgeschriebenen Geschichten mit bunten Zeichnungen.$B$BDer Foliant scheint viel älter zu sein, als seine Besitzerin und stammt vielleicht aus ihrem Familienerbe. Möglicherweise war das starke Band zu diesem Buch das, was Belmaras Geist an die Ruinen von Kirin\'Var gefesselt hat. Wenn Ihr es auf seinen rechten Platz im Bücherregal zurückbringt, kommt ihr Geist vielleicht zur Ruhe.",
    ["O"] = "Sucht die Wohnung von Bannzauberin Belmara in der Zaubergasse und bringt das Buch zurück auf seinen Platz im Bücherregal. Sprecht danach mit Hüter Dierwert in Kirin\'Var.",
    ["T"] = "Bannzauberin Belmara",
  },
  [10306] = {
    ["D"] = "Der Mantel, der ohne Zweifel von Hochelfen geschneidert wurde, ist sogar für die Verhältnisse der Kirin Tor reich verziert. Die Symbole und Zeichen ergeben für Euch keinen Sinn, aber sie scheinen die Kleidung eindeutig mit Magie zu verstärken.$B$BVielleicht bindet dieser Gegenstand seinen früheren Besitzer an diese Existenzebene. Wenn das so ist, würde die Verbindung des Geistes mit dieser Welt vielleicht zerbrechen, wenn Ihr den Mantel zurück in die Kommode in Beschwörer Luminraths Wohnung bringt.",
    ["O"] = "Sucht die Wohnung von Beschwörer Luminrath in der Zaubergasse und bringt den Mantel zurück in seine Kommode. Sprecht danach mit Hüter Dierwert in Kirin\'Var.",
    ["T"] = "Beschwörer Luminrath",
  },
  [10307] = {
    ["D"] = "Während Ihr die Kappe betrachtet, fragt Ihr Euch, ob ein Magier der Kirin Tor überhaupt so einen Hut besitzen würde. Der Zustand der Kappe lässt darauf schließen, dass der Gnom das Haus nie ohne sie verlassen hätte.$B$BIhr probiert den Hut kurz auf und bemerkt keine magischen Eigenschaften außer einem schwachen Zwang zu tanzen.$B$BVielleicht bindet dieser geschätzte Besitz den Geist des toten Magiers an das Dorf. Wenn Ihr die Kappe zurück in die Schließkiste in Cohliens Wohnung legen würdet, könntet Ihr den Geist des Magiers vielleicht befreien.",
    ["O"] = "Sucht die Wohnung von Cohlien Frostwirker in der Zaubergasse und bringt die Kappe zurück in seine Schließkiste. Sprecht danach mit Hüter Dierwert in Kirin\'Var.",
    ["T"] = "Cohlien Frostwirker",
  },
  [10308] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Noch ein Hügel voller Astraler",
  },
  [10309] = {
    ["D"] = "Seht Ihr den kaputten Teufelshäscher im Süden? Ist er nicht schön?$B$BIch möchte sein Herz!$B$BWas? Natürlich hat er ein Herz... wer hat denn keins?$B$B<N.D. sieht für einen kurzen Moment beleidigt aus.>$B$BGeht dort hinüber, öffnet ihn und bringt mir, was ich möchte, dann bekommt Ihr auch einen schönen Finderlohn.$B$BOh, und ich würde an Eurer Stelle ein oder zwei Freunde mitnehmen. Diese Astralen der Zaxxis sind eine ziemlich verschlagene Truppe!$B$BNehmt diesen Teufelsschocker, um seinem Herz einen Stromimpuls zu geben, wenn Ihr bereit seid, loszulegen.",
    ["O"] = "Besorgt das Herz des Teufelshäschers und bringt es zu N. D. Mieskamp beim Hügel im Nethersturm.",
    ["T"] = "Teufelshäscher mit Herz",
  },
  [10310] = {
    ["D"] = "Jetzt, da die Brennende Legion sich auf dem Trümmerfeld der Teufelshäscher in der Nähe befindet, müssen wir alles tun, um den Zuwachs der Dämonen in dieser Gegend aufzuhalten.$B$BSie haben ein Warptor im Süden in einer Gegend, die sie bezeichnenderweise Invasionspunkt: Zerstörer genannt haben.$B$BIch möchte, dass Ihr mit mir dorthin geht und mich beschützt, während ich das Tor sabotiere.$B$BGeht danach zum Mittelreichposten in der Biokuppel Mittelreich im Norden und meldet Gahruj unseren Erfolg.$B$BWir werden ein oder zwei Freunde von Euch für diesen Auftrag brauchen.",
    ["O"] = "Begleitet Drijya zum Warptor beim Invasionspunkt: Zerstörer und sorgt für seine Sicherheit, während er das Tor sabotiert. Sprecht danach mit Gahruj im Mittelreichposten in der Biokuppel Mittelreich im Nethersturm.",
    ["T"] = "Sabotage am Warptor!",
  },
  [10311] = {
    ["D"] = "$C, schön ein hilfreiches Gesicht hier zu sehen.$B$BDie Brennende Legion bei dem Teufelshäscherhügel hier in der Nähe wird immer mutiger, und ich mache mir Sorgen um unsere Sicherheit. Die Dämonen erhalten immer mehr Verstärkung aus dem Invasionspunkt: Zerstörer im Süden.$B$BMein Gefährte, Drijya, hat sich dorthin aufgemacht, um die Gegend zu erkunden und zu schauen, ob er etwas gegen die Warptore der Legion ausrichten kann.$B$BBitte findet ihn und helft ihm, wo Ihr könnt.",
    ["O"] = "Sprecht mit Drijya in der Nähe des Invasionspunkts: Zerstörer im Nethersturm.",
    ["T"] = "Drijya braucht Eure Hilfe",
  },
  [10312] = {
    ["D"] = "Die Geister zum Schweigen zu bringen hat leider nichts an Ihrer Aggressivität geändert. Die bösartigen Geister, die Ihr vernichtet habt, tauchen nun wieder in den Straßen des Dorfs auf.$B$BWir müssen herausfinden, was dahinter steckt. Die Zerstörung Kirin\'Vars war nur der Anfang, sonst hätten sich die Geister schon längst beruhigt.$B$BIm Rathaus im Süden von Kirin\'Var gibt es ein Stadtregister, in dem alle Einwohner eingetragen sind. Ich habe gehört, dass Kampfmagier Dathric es zuletzt hatte. Vielleicht trägt sein Geist es noch mit sich herum.",
    ["O"] = "Besorgt das Stadtregister von Kirin\'Var aus dem Rathaus von Kirin\'Var und bringt es zu Hüter Dierwert.",
    ["T"] = "Die Annalen von Kirin\'Var",
  },
  [10313] = {
    ["D"] = "Wenn uns unser Instinkt nicht täuscht und diese Manaschmieden die Technologie der Festung der Stürme nutzen, könnte die ganze Scherbenwelt in großer Gefahr schweben.$B$BDie Energieauflademechanismen der Festung der Stürme waren niemals dafür gedacht, sie so nahe an bewohntem Land zu benutzen. Die Warpenergien könnten dieses Land zerstören, sogar wenn sie durch Kaels\' Rohrleitungen fließen.$B$BBringt diese Kugel zur Manaschmiede B\'naar. Sie ist so eingestellt, dass sie die Warpenergien messen kann. Benutzt sie an der Stelle, an der die Rohrleitungen in die Erde führen.",
    ["O"] = "Exarch Orelis in Area 52 möchte, dass Ihr die Warpmesskugel zur Manaschmiede B\'naar bringt und sie in der Nähe der vier Rohrleitungen benutzt.$B$BDas Ausführen von Quests für die Aldor führt zu einer Verringerung Eurer Ehre bei den Sehern.",
    ["T"] = "Warpenergien messen",
  },
  [10314] = {
    ["D"] = "Das merkwürdige magische Gerät ähnelt nichts, was Ihr je zuvor gesehen habt. Es wurde mit Sicherheit nicht von Blutelfen hergestellt.$B$BAls Ihr bemerkt, dass die Geister sich näher an das Haus bewegen, stellt Ihr fest, dass die Kugel stärker zu leuchten beginnt, und wieder schwächer wird, als die Geister sich entfernen. Es steht außer Frage, dass sie mit der Beeinflussung der Geister in Verbindung steht.$B$BWie sie jedoch funktioniert, bleibt ein Geheimnis. Vielleicht kann Hüter Dierwert sich ja einen Reim darauf machen, wenn Ihr ihm die Überreste einiger Geister und Eure Beobachtungen zuteilwerden lasst.",
    ["O"] = "Sammelt 10 abscheuliche Überreste von den wahnsinnigen Geistern auf dem Kapellenhof in Kirin\'Var und bringt sie zu Hüter Dierwert.",
    ["T"] = "Ein hartnäckiger Verdacht",
  },
  [10315] = {
    ["D"] = "Die Blutelfen sind Dummköpfe! Ich bin froh, dass unsere Handelsbeziehungen mit ihnen auseinander gebrochen sind. Auf all meinen Reisen durch den Kosmos habe ich nie so selbstsüchtige Wesen gesehen!$B$BSie zerstören weiterhin unser Land mit ihren Manaschmieden. Wollen wir mal schauen, ob wir ihnen bei der Manaschmiede Ara jenseits der Brücke Einhalt gebieten können.$B$BDie Nethermanten kanalisieren die Energie der Blutjuwelen, um den Energiefluss zu stabilisieren. Geht dort hin und löscht sie aus!",
    ["O"] = "Tötet 15 Nethermanten des Sonnenzorns und kehrt dann zu Netherpirscher Oazul auf Tulumans Landeplatz im Nethersturm zurück.",
    ["T"] = "Die Nethermanten neutralisieren",
  },
  [10316] = {
    ["D"] = "Wer auch immer hinter den Veränderungen der Geister stehen mag, hat seine Ziele mit Bedacht ausgewählt, und darauf geachtet, dass er zuerst die mächtigsten Magier erwischt. Aber warum? Die Magie, die eingesetzt wird, um die Geister der getöteten Kirin Tor zu beeinflussen, muss sehr mächtig sein.$B$BWarum würde ein mächtiger Magier all diese Mühen auf sich nehmen? Ich bin versucht zu glauben, dass dies ein weiterer teuflischer Plan von Kael\'thas ist, um hier weiteres Unheil anzurichten. Wenn es hier in der Nähe Blutelfen gibt, verstecken sie sich bestimmt in den Häusern hinter der überdachten Brücke.",
    ["O"] = "Durchsucht die Häuser auf dem Kapellenhof von Kirin\'Var nach Hinweisen darauf, wer sich hinter der Beeinflussung der Geister verbirgt.",
    ["T"] = "Auf der Suche nach Beweisen",
  },
  [10317] = {
    ["D"] = "Die Blutelfen nennen die Mine unter der Manaschmiede Ara Trelleummine. Seit sie die Mine unter der Manaschmiede Ultris verloren haben, ist sie der einzige Ort, von dem sie den Nachschub zum Betrieb ihrer Schmieden bekommen.$B$BIch brauche nicht zu erwähnen, dass es ein großer Vorteil für uns wäre, wenn wir ihre Bergbauaktivitäten dort verhindern könnten. Vielleicht könnten wir sogar dort einfallen und die Mine für uns selbst nutzen?$B$BWie auch immer, ich möchte, dass Ihr Euch um Großknecht Sonnenend kümmert. Die Mine befindet sich am hinteren Ende der Insel links von uns.",
    ["O"] = "Vernichtet Großknecht Sonnenend in der Trelleummine, die sich unter der Manaschmiede Ara im Nethersturm befindet.",
    ["T"] = "Den Großknecht erledigen",
  },
  [10318] = {
    ["D"] = "Ich war früher hier der Großknecht. Aber jetzt, da ich degradiert wurde, geht mir auf, dass unsere ganze Pilgerreise von Quel\'Thalas in die Scherbenwelt eine einzige Lüge war!$B$BEine Lüge, die von Kael\'thas eingefädelt wurde, um seine Streitkräfte hier im Nethersturm zu stärken.$B$BIch habe versucht, meine Brüder davon zu überzeugen, dass wir fortgehen sollten, besonders wegen der Brennenden Legion, aber sie sind alle zu stur.$B$BKönnt Ihr mir einen Gefallen tun? Tötet Übermeister Grindgarr. Kehrt dann zu Eurem Astralen zurück und sagt ihm, dass ich kein Feind mehr bin.",
    ["O"] = "Tötet Übermeister Grindgarr. Kehrt dann zu Windhändler Tuluman auf Tulumans Landeplatz im Nethersturm.",
    ["T"] = "Den Übermeister erledigen",
  },
  [10319] = {
    ["D"] = "Ich glaube, es wird Zeit für eine Erklärung. Ihr habt einen der Lehrlinge des Erzmagiers getroffen, Ravandwyr. Es gab jedoch noch einen weiteren mit dem Namen Naberius.$B$BEr war ein sehr fleißiger Schüler, hegte aber großes Interesse an der Nekromantie. Als Kael\'thas mit seinen Streitmächten auftauchte, nutzte Naberius die Gelegenheit, um den Erzmagier zu verraten und die geheimen Verteidigungsmechanismen des Turms preiszugeben.$B$BJetzt, so scheint es, ist er zu einem Lich geworden. Wenn wir ihn aufhalten wollen, müssen wir uns sein Phylakterium besorgen. So wie ich ihn kenne, hat er es sicher irgendwo in seinem Gebiet sicher vergraben.",
    ["O"] = "Holt Naberius\' Phylakterium vom Kapellenhof in Kirin\'Var und bringt es zu Hüter Dierwert.",
    ["T"] = "Das Phylakterium entwenden",
  },
  [10320] = {
    ["D"] = "Erzmagier Vargoth hat die Gefahr in Naberius\' Interesse an der Nekromantie erkannt, aber ich bin mir sicher, dass er niemals geglaubt hätte, dass sein Lehrling den Kirin Tor so viel Schaden zufügen würde.$B$BIhr müsst ihn besiegen, $N, sonst wird er weiterhin die Geister unserer gefallenen Brüder versklaven, sie ihrer Ruhe berauben und das letzte bisschen, was noch von Kirin\'Var übrig ist, zerstören.$B$BOhne sein Phylakterium kann er mit den vereinten Kräften von zwei oder drei Kämpfern vernichtet werden, aber wer weiß, was seine Magie mehr Gegnern gegenüber anrichten könnte?",
    ["O"] = "Hüter Dierwert in Kirin\'Var möchte, dass Ihr Naberius auf dem Kapellenhof des Dorfes besiegt.",
    ["T"] = "Vernichtet Naberius!",
  },
  [10321] = {
    ["D"] = "Euer Erfolg in B\'naar hat und sehr große Hoffnung gemacht, $N.$B$BWir haben unsere Entdeckungen den Sehern mitgeteilt, und sie haben uns im Gegenzug mehr Informationen über die übrigen Manaschmieden gegeben.$B$BEs stehen immer noch drei weitere Manaschmieden, die von der Armee des Sonnenzorns kontrolliert werden.$B$BDie Manaschmiede Coruu findet Ihr im Osten. Vorarbeiterin Seylanna ist dort im Besitz des Zugangskristalls. Besorgt ihn und schaltet damit die Steuerungskonsole ab.$B$BWir zählen auf Euch, $N.",
    ["O"] = "Reist zur Manaschmiede Coruu, östlich von Area 52, und besorgt den Zugangskristall von Coruu von Vorarbeiterin Seylanna. Benutzt ihn, um die Manaschmiede abzuschalten, und kehrt dann zu Anachoret Karja zurück.$B$BDas Ausführen von Quests für die Aldor führt zu einer Verringerung Eures Rufs bei den Sehern.",
    ["T"] = "Manaschmiede Coruu abschalten",
  },
  [10322] = {
    ["D"] = "Wir haben Neuigkeiten von einer der Manaschmieden, die noch in Betrieb sind, erhalten. Die Armee des Sonnenzorns ist im Begriff, die Kontrolle über die Manaschmiede Duro zu verlieren.$B$BWegen ihrer erhöhten Energieproduktion werden ganze Schwärme von Manakreaturen von ihr angelockt - die Manaschmiede versinkt im Chaos.$B$BEine Explosion von Duro könnte eine Kettenreaktion auslösen, die die ganze Scherbenwelt in Gefahr bringen würde. Vorarbeiter Athanel ist im Besitz des Zugangskristalls, mit dem Ihr die Manaschmiede abschalten könnt.",
    ["O"] = "Anachoret Karja möchte, dass Ihr zur Manaschmiede Duro geht und den Zugangskristall von Vorarbeiter Athanel besorgt. Benutzt ihn, um die Steuerungskonsole von Duro abzuschalten.$B$BDas Ausführen von Quests für die Aldor führt zu einer Verringerung Eures Rufs bei den Sehern.",
    ["T"] = "Manaschmiede Duro abschalten",
  },
  [10323] = {
    ["D"] = "Nur noch eine Manaschmiede hält die Festung der Stürme in Betrieb. Nachdem wir die Manaschmiede Ara abgeschaltet haben, muss sich Kael mit seinen gespeicherten Energien zufrieden geben.$B$BReist zur Manaschmiede Ara im Nordwesten und gebt Kael den Rest, indem Ihr sie abschaltet.$B$BDer letzte Zugangskristall befindet sich im Besitz von Vorarbeiter Azarad. Seid jedoch vorsichtig; wir erwarten heftigen Widerstand in Kaels letzter funktionierender Schmiede. Ihr werdet wahrscheinlich die Unterstützung einiger Freunde brauchen.",
    ["O"] = "Reist zur Manaschmiede Ara und besorgt Euch einen Zugangskristall von Vorarbeiter Azarad. Benutzt ihn, um die Steuerungskonsole von Ara abzuschalten.$B$BDas Ausführen von Quests für die Aldor führt zu einer Verringerung Eures Rufs bei den Sehern.",
    ["T"] = "Manaschmiede Ara abschalten",
  },
  [10324] = {
    ["D"] = "Gut gemacht, $N! Gut gemacht!$B$BJetzt, da Ihr Euch die Hörner an den Mondweidenhirschen abgestoßen habt, ist es an der Zeit, dass Ihr Euch einer würdigeren Beute zuwendet. Lasst Euch von der Gelassenheit der Mondweidenböcke nicht täuschen, wenn sie sich provoziert fühlen, sind sie ein gefährlicher Gegner.$B$BIhr findet die Böcke nördlich von hier. Weidmannsheil.",
    ["O"] = "Bringt 6 Bälge eines Mondweidenbocks zu Acteon in der Azurwacht.",
    ["T"] = "Die große Mondweidenjagd",
  },
  [10325] = {
    ["D"] = "Wie Ihr vielleicht wisst, $N, sind die Aldor die Hüter des Lichts und die Diener der Naaru.$B$BUns direkt gegenüber stehen die Agenten der Brennenden Legion - Dämonen, die die Welten zerstören und die Unschuldigen verderben wollen.$B$BWenn Ihr Euch den Aldor gegenüber beweisen wollt, bringt mir die Insignien der niederrangigen Mitglieder der Legion und ihrer Anhänger des Schattenrats. Uns ist ein Versteck des Schattenrats in der Knochenwüste bekannt. Die Lager der Legion findet Ihr in Nagrand und dem Schergrat.",
    ["O"] = "Bringt 10 Male von Kil\'jaeden zu Adyen dem Lichtwächter in Shattrath.$B$BDas Abschließen von Quests für die Aldor führt zu einer Verringerung Eures Rufs bei den Sehern.",
    ["T"] = "Male von Kil\'jaeden",
  },
  [10326] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Mehr Male von Kil\'jaeden",
  },
  [10327] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Einzelne Male von Kil\'jaeden",
  },
  [10328] = {
    ["D"] = "Etwas an diesen Manaschmieden bereitet mir Kopfzerbrechen, $N. Die Naarutechnologie ist sehr fortschrittlich; Kael\'thas hätte Jahre brauchen müssen, um sie auch nur im Ansatz zu verstehen.$B$BDas Sammeln von Informationen zu diesem Thema ist sehr schwierig, da Kaels Armee sehr diszipliniert ist und jegliche schriftliche Kommunikation sofort vernichtet.$B$BWir haben gehört, dass die Manaschmiede Duro von Manakreaturen angegriffen wird. Vielleicht haben die Verteidiger ihre Anweisungen noch nicht zerstört. Besorgt sie Euch und bringt sie zu mir.",
    ["O"] = "Reist zur Manaschmiede Duro und beschafft die Truppenbefehle des Sonnenzorns und die Arkanbefehle des Sonnenzorns von den Einheiten des Sonnenzorns, die dort postiert sind. Kehrt zu Exarch Orelis zurück, wenn Ihr Eure Aufgabe erledigt habt.$B$BDas Abschließen von Quests für die Aldor führt zu einer Verringerung Eures Rufs bei den Sehern.",
    ["T"] = "Anweisungen des Sonnenzorns",
  },
  [10329] = {
    ["D"] = "Während Ihr fort wart, hatte ich eine freundliche Diskussion mit den Aldor. Die Manaschmieden bestehen aus der Naarutechnologie der Festung der Stürme. Offensichtlich gibt es bei dieser Technologie Mechanismen, die ein dauerhaftes Abschalten im Notfall ermöglichen.$B$BAlles, was wir brauchen, um diesen Vorgang einzuleiten, ist ein Zugangskristall zur Steuerungskonsole. Der praktischen Stammrolle, die Ihr uns gebracht habt, nach, befinden sich die Kristalle im Besitz eines Typen namens Vorarbeiter Theredis. Besorgt ihn und schaut, was Ihr tun könnt.",
    ["O"] = "Geht zurück zur Manaschmiede B\'naar und besorgt Euch den Zugangskristall von Vorarbeiter Theredis. Benutzt ihn bei der Steuerungskonsole von B\'naar um sie abzuschalten und meldet Euch danach bei Spionagemeister Thalodien zurück.$B$BDas Ausführen von Quests für die Seher führt zu einer Verringerung Eures Rufs bei den Aldor.",
    ["T"] = "Manaschmiede B\'naar abschalten",
  },
  [10330] = {
    ["D"] = "Thalodien hat uns von Eurem Erfolg beim Abschalten der Manaschmiede B\'naar erzählt. Er erwartet, dass Ihr Eure Leistung hier wiederholt und hat uns gebeten, ein Auge darauf zu haben, wer einen Kristall benutzt, um Zugang zu der Konsole im Inneren zu erhalten. Vorarbeiterin Seylanna hat den Zugangskristall, da sind wir uns sicher.$B$BGeht hinein und vollendet das Werk. Es besteht kein Grund zur Heimlichkeit mehr. Besorgt Euch den Kristall, schaltet die Konsole ab, tötet alle Techniker, die Euch daran hindern wollen, und macht Euch dann aus dem Staub.$B$BBeeilt Euch, Kaels Spürhunde kommen uns sicher bald auf die Schliche.",
    ["O"] = "Besorgt den Zugangskristall von Coruu von Vorarbeiterin Seylanna. Benutzt ihn, um die Steuerungskonsole der Manaschmiede abzuschalten und meldet Euch dann bei Caledis Morgenhell.$B$BDas Ausführen von Quests für die Seher führt zu einer Verringerung Eures Rufs bei den Aldor.",
    ["T"] = "Manaschmiede Coruu abschalten",
  },
  [10331] = {
    ["D"] = "Ihr habt hier nicht zufällig einen schweren Schmiedehammer herumliegen sehen, oder? Ich habe ihn hierher mitgenommen, um ein paar Hufeisen für das Pferd des alten Amorlin fertig zu stellen, aber ich kann ihn nicht finden.$B$BIch habe die ganze Scheune mindestens ein dutzend Mal abgesucht, aber ohne Erfolg. Vielleicht hat einer der anderen Lehrlinge ihn gefunden und ihn zurück zur Schmiede gebracht? Könntet Ihr für mich dorthin gehen und danach suchen? Sie befindet sich südöstlich des Turms auf dem Ratsplatz.",
    ["O"] = "Besorgt den Schmiedehammer von den Lehrlingen von Kirin\'Var und bringt ihn zurück zu Lehrling Andrethan in Kirin\'Var.",
    ["T"] = "Unverzichtbare Werkzeuge",
  },
  [10332] = {
    ["D"] = "Endlich ist mein Gesellenstück fertig! Ich kann es kaum abwarten, es Meister Rhonsus zu zeigen.$B$B<Der Lehrling schaut Euch an, als Ihr ihm erzählt, was passiert ist, als Ihr den Hammer zurückholen wolltet.>$B$BSie haben Euch angegriffen? Alle? Beim Licht, sie sind verrückt geworden!$B$BWas sollen wir tun, $N? Es schmerzt mich, das zu sagen, aber vielleicht müssen wir Meister Rhonsus umbringen. Dann wäre ich der einzige Schmied in Kirin\'Var, aber ich denke, dass meine Ausbildung ausreicht, um die Bedürfnisse des Dorfs zu befriedigen.",
    ["O"] = "Lehrling Andrethan in Kirin\'Var möchte, dass Ihr den Geist von Rhonsus, dem Meisterschmied des Dorfes, vernichtet.",
    ["T"] = "Meisterschmied Rhonsus",
  },
  [10333] = {
    ["D"] = "Sagt mal, $N, ich habe mich gefragt, ob Ihr mir noch einen Gefallen tun könntet.$B$BMeine Partnerin da draußen im Feld könnte sicher etwas Hilfe gebrauchen. Könnt Ihr ihr vielleicht helfen?$B$BIch bin sicher, dass Mama Mauschel dafür sehr dankbar wäre. Kümmert Euch nicht um ihre scharfe Zunge.$B$BSie bemüht sich um unsere Interessen im Osten. Ihr findet sie in der Biokuppel Mittelreich beim Mittelreichposten.",
    ["O"] = "Sprecht mit Mama Mauschel im Mittelreichposten in der Biokuppel Mittelreich im Nethersturm.",
    ["T"] = "Helft Mama Mauschel",
  },
  [10334] = {
    ["D"] = "Wo, oh, wo ist meine Bessy? Nach dem Angriff habe ich die Felder und den Kapellenhof nach Ihr abgesucht, aber ich konnte sie nicht finden!$B$BAlles, was ich von ihr noch habe, ist diese alte Kuhglocke. Normalerweise kommt sie gleich gerannt, wenn sie sie hört, aber vielleicht können diese alten Hände sie nicht stark genug läuten.$B$BIch leihe Euch Bessys Glocke, wenn Ihr hinaus auf die Felder geht und sie so fest Ihr nur könnt läutet. Vielleicht wartet Bessy einfach nur auf ein wenig mehr Kuhglocke.",
    ["O"] = "Find Bessy in the fields near Kirin\'Var Village\'s Chapel Hill.",
    ["T"] = "Braucht mehr Kuhglocke",
  },
  [10335] = {
    ["D"] = "Ich muss in die Ruinen von Farahlon gehen, um meine Ausgrabungen zu beginnen! Das Problem ist, dass dieser Ort von Dämonen heimgesucht wird.$B$BVielleicht könntet Ihr schon einmal ein wenig Vorarbeit für mich leisten?$B$BEs gibt drei Orte in den Ruinen im Nordosten, an dem Ihr für mich Vermessungsmarkierungen aufstellen sollt. Jede befindet sich in der Nähe eines Draeneibanners.$B$BKehrt zu mir zurück, wenn Ihr alle drei Markierungen erfolgreich aufgestellt habt.",
    ["O"] = "Platziert die drei Vermessungsmarkierungen und kehrt dann zu Zephyrion in der Sturmsäule im Nethersturm zurück.",
    ["T"] = "Die Ruinen beobachten",
  },
  [10336] = {
    ["D"] = "Vor ein paar Wochen hat die Brennende Legion in den Ruinen von Farahlon Einzug gehalten und damit all unsere Pläne, die Artefakte dort auszugraben, zunichtegemacht.$B$BDann habe ich heute erfahren, dass einer ihrer Schreckenslords, Culuthas, aus irgendeinem Grund alle anderen Mitglieder der Brennenden Legion dort getötet hat. Seitdem ruft er Dämonen, die ihm dienen.$B$B$C, ich möchte, dass Ihr zu den Ruinen im Nordosten geht und die Dämonen vernichtet!",
    ["O"] = "Tötet 10 Hunde von Culuthas und 5 Augen von Culuthas",
    ["T"] = "Diener von Culuthas",
  },
  [10337] = {
    ["D"] = "Als Ihr merkt, dass Bessy Euch immer noch anstarrt, deutet Ihr nach Westen, zu dem Ort, an dem Thadell sich befindet. Ihr erwischt Euch selbst dabei, wie Ihr der Kuh versprecht, sie auf ihrem Nachhauseweg zu beschützen. Als Ihr Euren Satz beendet habt, wird Euch klar, dass die Kuh gar nicht verstehen kann, was Ihr sagt.$B$BMit einem Schulterzucken bereitet Ihr Euch darauf vor, das Rindvieh zu Thadell zurückzubegleiten.",
    ["O"] = "Begleitet Bessy sicher zu ihrem Besitzer Thadell zurück.",
    ["T"] = "Almabtrieb",
  },
  [10338] = {
    ["D"] = "Die Manaschmiede Duro wird also angegriffen und die Streitkräfte des Sonnenzorns haben die Kontrolle verloren? Was für ein Dilemma! Wenn Duro fällt, fällt die Festung der Stürme. Der einzige Nachteil ist... wenn die Festung der Stürme zerstört wird, wird die Explosion wahrscheinlich die kläglichen Überreste dieses verlassenen Landes mit sich reißen.$B$BKael\'thas Tod ist gut. Jedermanns Tod ist... nicht so gut, glaube ich. Es schmerzt mich, Euch darum zu bitten, aber geht zur Manaschmiede Duro und schaltet sie ab, bevor sie in Flammen aufgeht. Vorarbeiter Athanel hat den Zugangskristall.",
    ["O"] = "Kehrt zur Manaschmiede Duro zurück und besorgt Euch den Zugangskristall von Vorarbeiter Athanel. Benutzt ihn, um die Steuerungskontrolle von Duro abzuschalten und meldet Euch danach bei Spionagemeister Thalodien.$B$BDas Ausführen von Quests für die Seher führt zu einer Verringerung Eures Rufs bei den Aldor.",
    ["T"] = "Manaschmiede Duro abschalten",
  },
  [10339] = {
    ["D"] = "Direkt südlich von hier findet Ihr den Stützpunkt des Astraleums. Das Astraleum bereitet sich dort auf irgendetwas vor... Vielleicht einen Angriff auf Ultris.$B$BWir können jedoch nichts dem Zufall überlassen, $C. Durchbrecht ihre Verteidigung und zerstört ihr Trainingslager.$B$BSobald Ihr ihre Soldaten und Hauptmann Zovax vernichtet habt, sucht den Transponder des Astraleums Zeta und benutzt ihn, um mit mir Kontakt aufzunehmen. Dann werdet Ihr Eure neuen Befehle erhalten.",
    ["O"] = "Kommandant Ameer möchte, dass Ihr 5 Auftragsmörder des Astraleums, 5 Stoßtruppsoldaten des Astraleums, 2 Forscher des Astraleums und Hauptmann Zovax tötet und dann den Transponder des Astraleums Zeta aktiviert.",
    ["T"] = "Das Astraleum",
  },
  [10340] = {
    ["D"] = "$N, Ihr habt gute Arbeit hier an der Front geleistet und großes Talent mit den gepanzerten Greifen bewiesen... Ich glaube, ich habe eine weitere Aufgabe für Euch.$B$BStartpunkt für Eure neue Mission ist der Trümmerposten, eine Angriffsbasis der Wildhämmer auf einer schwebenden Insel in der Nähe des Portals. Es ist etwas anstrengend dort, aber Ihr habt starke Nerven. Ihr packt das schon.$B$BSprecht mit Schwadronskommandant Dabir\'ee und lasst Euch von ihm zum Trümmerposten schicken. Meldet Euch dann bei Greifenmeister Runetog Wildhammer.",
    ["O"] = "Vorpostenkommandant Königstein möchte, dass Ihr mit Schwadronskommandant Dabir\'ee sprecht, auf einem Greifen zum Trümmerposten fliegt und Euch bei Runetog Wildhammer meldet.",
    ["T"] = "Trümmerposten",
  },
  [10341] = {
    ["D"] = "Das sind günstige Nachrichten, die Ihr bringt, $N. Ihr könnt nicht nur ungehindert in die Manaschmiede Duro reinspazieren, es eröffnet sich auch noch eine großartige Gelegenheit, Kaels Armee einen Schlag zu versetzen.$B$BNutzt den Angriff der Manakreaturen und vernichtet alle Nachzügler des Sonnenzorns, die Euch in die Arme laufen.$B$BIch habe keinen besonderen Spaß daran, mein eigenes Volk abzuschlachten, wie Tahlodien, aber diesen taktischen Vorteil dürfen wir nicht ungenutzt vorübergehen lassen.",
    ["O"] = "Tötet 8 Beschwörer des Sonnenzorns, 6 Jagdschützen des Sonnenzorns und 4 Zenturios des Sonnenzorns. Kehrt zu Magister Larynna in Area 52 zurück, wenn Ihr die Aufgabe erledigt habt.$B$BDas Abschließen von Quests für die Seher führt zu einer Verringerung Eures Rufs bei den Aldor.",
    ["T"] = "Tretet sie, solange sie am Boden liegen",
  },
  [10342] = {
    ["D"] = "Ich verdiene gut daran, den Trunkenbolden hier Getränke zu verkaufen. Aber ich will mehr!$B$BDer Raketenchef sagt mir immer, dass er den Raketentreibstoff, den er braucht, um die X-52 abheben zu lassen, nicht verbessern kann.$B$BWir können dieses kleine Problem für ihn lösen.$B$BZuerst muss ich das Öl aus dem Schiefer der hiesigen Schieferhautschinder pressen. Daraus mache ich einen konzentrierten, wenn auch leicht entzündlichen, Grundstoff für die Mischung, die mir vorschwebt.$B$BSucht die Schinder und ihren Schiefer! Sie sind überall bei den violetten Felsen.",
    ["O"] = "Sammelt 5 Stück Schieferhautschiefer und bringt sie zu Stiefels in Area 52 im Nethersturm.",
    ["T"] = "Schieferhautschiefer",
  },
  [10343] = {
    ["D"] = "Kael\'thas Angriff auf Kirin\'Var begann mit einem donnernden Geräusch und einem hellen Lichtblitz im Offiziersquartier im Nordwesten. Die Zerstörung war dort am größten. Nach dem Rückzug der Blutelfen geschah etwas Merkwürdiges.$B$BWesen aus reinem Mana begannen, von überall her in das Dorf zu strömen. Bis jetzt versuchen wir, sie so gut es geht im Zaum zu halten, aber ich hoffe, dass wir den Ansturm komplett abwehren können, wenn wir mehr über die Waffe in Erfahrung bringen, die Kael\'thas benutzt hat.",
    ["O"] = "Sucht die von Manakreaturen heimgesuchte Gegend in Kirin\'Var nach einem Manabombenfragment ab und bringt sie zu Zaubererleutnant Morran.",
    ["T"] = "Die unendliche Invasion",
  },
  [10344] = {
    ["D"] = "Schwadronskommandant Greifgar hat die Leitung über die Wildhämmer hier. Wenn Ihr zum ersten Mal im Trümmerposten seid, solltet Ihr Euch bei ihm melden.$B$BIhr findet ihn in den Überresten des Turms da drüben.",
    ["O"] = "Sprecht mit Schwadronskommandant Greifgar im Trümmerposten.",
    ["T"] = "Schwadronskommandant Greifgar",
  },
  [10345] = {
    ["D"] = "Seht Ihr diese Leichen, $R? Arconus ist für diese Massaker verantwortlich. Er hat sie ausgesaugt, sodass nur noch eine Fleischhülle übrig geblieben ist. Aber lasst Euch von den Körpern nicht täuschen! In diesen Leichen liegen Fleischbestien, die nur darauf warten, ahnungslose Dummköpfe, die sich zu nahe heranwagen, anzufallen.$B$BIch habe meine Befehle. Ich muss hierbleiben, bis meine Verstärkung eintrifft. Das bedeutet aber nicht, dass Ihr nicht meinen Anzünder nehmen und die siechenden Leichen, die überall in den Minen herumliegen, zerstören könnt. So oder so muss diese Aufgabe erledigt werden.",
    ["O"] = "Benutzt den Anzünder des Protektorats um 12 siechende Leichen zu zerstören. Bringt den Anzünder des Protektorats zu Agent Araxes außerhalb des Zugangsschachts Zeon zurück, wenn Ihr erfolgreich wart.",
    ["T"] = "Fleisch geworden",
  },
  [10346] = {
    ["D"] = "$N, Ihr habt Euch als fähiger Greifenreiter bewiesen. Wir könnten Euch für weitere Einsätze bei der Abyssischen Untiefe brauchen. Was sagt Ihr, $C? Wollt Ihr noch ein paar Flugstunden sammeln?",
    ["O"] = "Greifnerin Heulwind im Expeditionsposten auf der Höllenfeuerhalbinsel möchte, dass Ihr 20 Peons der Gan\'arg, 5 Vorarbeiter der Mo\'arg und 5 Teufelskanonen zerstört.",
    ["T"] = "Rückkehr zur Abyssischen Untiefe",
  },
  [10347] = {
    ["D"] = "$N, seid Ihr bereit für einen weiteren Flug zu Abyssischen Untiefe? Ich fürchte, dass uns die Dämonen der Legion von Norden her angreifen werden, wenn wir sie nicht weiter unter Druck setzen und zulassen, dass sie Ihre Festung verstärken.$B$BWas sagt Ihr, $N? Habt Ihr Lust, Euch erneut auf einen Windreiter zu schwingen und zur Abyssischen Untiefe zu fliegen?",
    ["O"] = "Schwadronskommandant Brack im Häschersturz auf der Höllenfeuerhalbinsel möchte, dass Ihr 20 Peons der Gan\'arg, 5 Vorarbeiter der Mo\'arg und 5 Teufelskanonen zerstört.",
    ["T"] = "Rückkehr zur Abyssischen Untiefe",
  },
  [10348] = {
    ["D"] = "Ich habe gehört, dass eine Gruppe von \'Druiden\' aus Azeroth sich für die Wirkung unserer Biokuppeltechnologie interessiert.$B$BDie Expedition des Cenarius ist sehr begierig darauf, Zugang zu den Pflanzen in den Kuppeln zu erlangen, besonders zu der einen, die sie Elfenbeinglocke nennen, und die für ihre heilenden Kräfte bekannt ist. Sie wächst in der Biokuppel Mittelreich in kleinen Büschen um größere Pflanzen herum.$B$BIch teile den Gewinn mit Euch, wenn Ihr mir ein paar der Kräuter für meine erste Lieferung sammelt.",
    ["O"] = "Sammelt 15 Elfenbeinglocken und kehrt dann zu Mehrdad im Mittelreichposten zurück.",
    ["T"] = "Neue Möglichkeiten",
  },
  [10349] = {
    ["D"] = "Ihr solltet dieses Kristallstück zur Erdbinderin bringen. Sie ist wie Ihr... immer muss sie einem Geheimnis auf die Spur kommen und darüber nachgrübeln.$B$BWenn Ihr mich fragt, denkt sie zu viel.$B$BGebt ihr den Kristall und ich bin sicher, dass sie Euch beim Versuch herauszufinden, was im Grat vor sich geht, in ganz schöne Schwierigkeiten bringen wird.",
    ["O"] = "Bringt den Purpurkristallsplitter zu Erdbinderin Galandria Nachtluft im Posten des Cenarius auf der Höllenfeuerhalbinsel.",
    ["T"] = "Die Erdbinderin",
  },
  [10350] = {
    ["D"] = "Ihr habt viele harte Lektionen in unserer Zeit zusammen von mir gelernt, doch nun ist es an der Zeit, dass ich Euch in weisere Hände übergebe.$B$BBehomat, der mich ausbildete, als ich noch grün wie Gras hinter den Ohren war, wird Euer neuer Lehrer sein. Ich habe ihm gesagt, dass ich Euch zu ihm schicken werde, er erwartet Euch also.$B$BIhr findet ihn in der Mitte der Terrasse der Krieger, am höchsten Punkt der Händlertreppe der Exodar.",
    ["O"] = "Sprecht mit Behomat bei der Händlertreppe in der Exodar.",
    ["T"] = "Behomat",
  },
  [10351] = {
    ["D"] = "Es müssen die bösen Strahlungen von dem riesigen roten Kristall sein, die die Bergriesen herbeirufen und sie wahnsinnig machen.$B$BEr ist zu groß, um ihn weg zu bringen, und unsere Fertigkeiten reichen nicht aus, um ihn zu zerstören, aber mir ist ein Weg eingefallen, wie wir seine Macht bezwingen können.$B$BIch habe einen Zirkel beim nordwestlichen Ende des Kristalls auf dem Grat beschworen. Bringt diesen Samen dorthin und setzt seine Macht ein, um das Land durch die natürlichen Kräfte des Regens, des Wachstums und der Insekten zu heilen.$B$BSeid vorsichtig, $N. Ich spüre, dass hier eine außenstehende Macht am Werk ist... und sie ist nicht natürlich!",
    ["O"] = "Benutzt den Samen der Wiederbelebung beim Zirkel des Erdbinders, um das Land um den Kristall herum zu heilen. Kehrt dann zu Erdbinderin Galandria Nachtluft im Posten des Cenarius auf der Höllenfeuerhalbinsel mit Euren Beobachtungen zurück.",
    ["T"] = "Natürliche Heilmittel",
  },
  [10352] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Spendet Wolle",
  },
  [10353] = {
    ["D"] = "Araxes und ich wurden vor zwei Tagen nach Ultris geschickt. Unser Auftrag: findet und vernichtet den Leerenfürsten Arconus. Wir haben ihn bis zu seiner Mine verfolgt und herausgefunden, dass er sich von den Energien der Kreaturen in dieser Gegend ernährt.$B$BEs brach ein Feuerwechsel aus, bei dem Araxes und ich getrennt wurden. Jetzt bin ich hier gefangen und schwer verletzt. Unser Auftrag ist dahin!$B$BWenn Ihr Arconus erledigen könntet, wäre die oberste Führung Euch sehr dankbar. Wenn Ihr den Auftrag annehmt, werde ich Euch eine Reflexion meiner selbst als Wächter mitschicken.",
    ["O"] = "Agent Sechs-Uhr beim Zugangsschacht Zeon im Nethersturm möchte, dass Ihr Arconus den Unersättlichen tötet und dann zu Kommandant Ameer im Wachposten des Protektorats im Nethersturm zurückkehrt.",
    ["T"] = "Arconus der Unersättliche",
  },
  [10354] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Spendet Seide",
  },
  [10355] = {
    ["D"] = "Wir müssen herausfinden, wie weit der Schaden reicht. Das Basidium, das Ihr mir gebracht habt, stammt von einem Sumpfriesen. Ihre größeren, zäheren Vettern, die Sumpflords, sollten der Degenerierung, die die Riesen befallen hat, gegenüber unempfindlicher sein.$B$BEs wäre auch schön zu wissen, inwiefern die Veränderungen die anderen Kreaturen des Todesmoors betroffen haben.$B$BKehrt zum Todesmoor zurück und schaut, ob Ihr Proben von den Sumpflords und Hydras in der Gegend sammeln könnt.",
    ["O"] = "Sammelt 5 Proben einer verdurstenden Hydra und 5 Proben eines siechenden Sumpflords und bringt sie zu Ruam in Telredor.",
    ["T"] = "Siechendes Fleisch",
  },
  [10356] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Spendet Magiestoff",
  },
  [10357] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Spendet Runenstoff",
  },
  [10358] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Noch mehr Runenstoff",
  },
  [10359] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Spendet Wolle",
  },
  [10360] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Spendet Seide",
  },
  [10361] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Spendet Magiestoff",
  },
  [10362] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Spendet Runenstoff",
  },
  [10363] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Noch mehr Runenstoff",
  },
  [10364] = {
    ["D"] = "Ihr seid in kurzer Zeit sehr weit gekommen, $N. Schneller, als ich erwartet oder gehofft hätte. Es ist an der Zeit, dass Ihr Eure Ausbildung unter der Anleitung meines Meisters, Caedmos, fortsetzt.$B$BIch habe ihm gesagt, dass er Euch in der Exodar erwarten soll. Ihr findet ihn im Sanktum der Anachoreten in der Nähe des Eingangs zur Halle des Lichts.",
    ["O"] = "Sprecht mit Caedmos in der Halle des Lichts der Exodar.",
    ["T"] = "Caedmos",
  },
  [10365] = {
    ["D"] = "Ihr habt Eure Arbeit besser erledigt, als wir dachten. Die Manaschmiede Ara ist die letzte von Kaels Schmieden, die noch in Betrieb ist. Ihr findet sie nordwestlich von hier, aber Ihr müsst wahrscheinlich um sie herumgehen, um dorthin zu gelangen. Wir möchten, dass Ihr sie abschaltet.$B$BDer letzte Zugangskristall befindet sich im Besitz von Vorarbeiter Azarad. Seid jedoch vorsichtig; wir erwarten heftigen Widerstand in Kaels letzter funktionierender Schmiede. Ihr werdet wahrscheinlich die Unterstützung einiger Freunde brauchen.$B$BHmm... Azarad. Was ein komischer Name für einen Sin\'dorei.",
    ["O"] = "Reist zur Manaschmiede Ara und besorgt Euch einen Zugangskristall von Vorarbeiter Azarad. Benutzt ihn, um die Steuerungskonsole von Ara abzuschalten.$B$BDas Ausführen von Quests für die Seher führt zu einer Verringerung Eures Rufs bei den Aldor.",
    ["T"] = "Manaschmiede Ara abschalten",
  },
  [10366] = {
    ["D"] = "Der Pfad des Lichts und der Reinheit ist steinig, $N. Sogar ich habe manchmal Probleme, ihm zu folgen.$B$BIhr solltet meine Meisterin, Jol, in der Exodar aufsuchen. Sie hat mich zu Beginn meiner Reise ausgebildet. Ich bin mir sicher, dass sie in Euch ihren neuen Lieblingsschüler finden wird.$B$BIhr findet Jol in der Halle des Lichts in der Exodar beim Sanktum der Verteidiger, einem Raum, der von einem pulsierenden Lichtschein umgeben ist.",
    ["O"] = "Sprecht mit Jol im Sanktum der Verteidiger in der Exodar.",
    ["T"] = "Jol",
  },
  [10367] = {
    ["D"] = "Wo immer ihr einen Stamm von versklavten Zerschlagenen seht, könnt Ihr auch einen Verräter finden. Ein verachtungswürdiges Wesen, das seine Brüder für das Versprechen von Macht verkauft hat.$B$BOben an der Treppe in der Mitte der Ruinen könnt Ihr eine Hütte finden. Das ist die Hütte des Verräters.$B$BEr ist im Augenblick nicht dort, Ihr könnt Euch also hineinschleichen, wenn Ihr Geduld habt. Innen findet Ihr einen Kasten mit einem Schlüssel darin. Dieser Schlüssel passt zu den Ketten, die die Ältesten des Lumpenpacks gefangen halten. Bringt ihn mir.$B$BSeid jedoch vorsichtig, Arzeth der Erbarmungslose treibt sich in der Nähe herum.",
    ["O"] = "Naladu in den Ruinen von Sha\'naar möchte, dass Ihr den Schlüssel von Sha\'naar aus einer Hütte in der Nähe besorgt.",
    ["T"] = "Ein Verräter unter uns",
  },
  [10368] = {
    ["D"] = "Der Geist des Lumpenpacks ist gebrochen, weil sich unsere Ältesten in Gefangenschaft befinden. Nehmt diesen Schlüssel und befreit sie.$B$BEs sind Morod der Windtreiber, Akoru der Feuerrufer und Aylaan der Wasserrufer. Sobald sie frei sind, werden die Geister zum Stamm des Lumpenpacks zurückkehren.",
    ["O"] = "Befreit Morod den Windtreiber, Akoru den Feuerrufer und Aylaan den Erderwecker aus den Ruinen von Sha\'naar. Kehrt zu Naladu zurück, wenn Ihr diese Aufgabe erledigt habt.",
    ["T"] = "Die Ältesten des Lumpenpacks",
  },
  [10369] = {
    ["D"] = "Ja, die Elementargeister sind zu uns zurückgekehrt! Wir sind nicht mehr so stark wie einst, aber auch geringe Magie kann in den richtigen Händen weit reichen.$B$BArzeth wurde von seinem dunklen Herrn große Macht verliehen. Nehmt diesen Stab. Er war einst ein großartiges Symbol unseres Volkes. Setzt ihn gegen Arzeth ein, und seine Macht wird für kurze Zeit verschwinden. Besiegt ihn, damit mein Volk endlich frei sein kann!",
    ["O"] = "Benutzt den Ältestenstab des Lumpenpacks gegen Arzeth den Erbarmungslosen und tötet ihn, nachdem er seine Macht verloren hat. Kehrt danach zu Naladu in den Ruinen von Sha\'naar zurück.",
    ["T"] = "Arzeths Untergang",
  },
  [10370] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Nazgrels Befehl",
  },
  [10371] = {
    ["D"] = "Ein Zwergenkrieger namens Yorus Gerstenbräu hat eine Herausforderung für alle tüchtigen Krieger der Allianz verkündet.$B$BEr wartet im Gasthaus von Seenhain, im sogenannten Rotkammgebirge in den Östlichen Königreichen. Es wird eine lange Reise, aber sie lohnt sich, wenn Ihr Euch selbst testen wollt, $N.",
    ["O"] = "Sprecht mit Yorus Gerstenbräu.",
    ["T"] = "Yorus Gerstenbräu",
  },
  [10372] = {
    ["D"] = "Eralan, eine Verbündete von mir in Tristessa, hat angefragt, ob ihr jemand bei einem eiligen Auftrag helfen könnte.$B$BIch weiß nicht, was sie vorhat, denn ich stelle keine Fragen, aber Ihr habt Euch mehr als fähig erwiesen. Ich möchte, dass Ihr nach Tristessa in den Geisterlanden reist, und mit Eralan sprecht.$B$BSie mag vielleicht ein wenig ruppig sein, aber man kann ihr vertrauen.",
    ["O"] = "Sprecht mit Eralan in Tristessa.",
    ["T"] = "Eine diskrete Anfrage",
  },
  [10373] = {
    ["D"] = "Hört her, hört her! Alle Helden werden ersucht, aufzumerken!$B$BDas Königreich Sturmwind ruft zu den Waffen! Alle einsatzfähigen Individuen sind hiermit angehalten, gegen die dunkle Bedrohung durch die Geißel in den nördlichen Ländern der Östlichen Königreiche zu den Waffen zu greifen! Es gibt Gerüchte über neue Bedrohungen, die sich aus den Ruinen der Pestländer erheben!$B$BHelden der Welt... Sucht Kommandant Ashlam Ehrenschlag auf, der seinen Stützpunkt an der Zugwindspitze errichtet hat, nördlich des Alteracgebirges!",
    ["O"] = "Sucht Kommandant Ashlam Ehrenschlag auf. Sein Stützpunkt befindet sich an der Zugwindspitze, nördlich des Alteracgebirges.",
    ["T"] = "Der Ruf zu den Waffen: Die Pestländer!",
  },
  [10374] = {
    ["D"] = "Helden der Welt, höret mich - die Horde ruft zum schnellen Aufmarsch!$B$BAlle einsatzfähigen Personen werden zu den Waffen gerufen, um gegen die Geißel zu ziehen! Es gibt Gerüchte über neue Bedrohungen, die sich aus den Ruinen der Östlichen Königreiche erheben! Sie lauern östlich von Unterstadt, in den nördlichen Ausläufern der Länder, die als Pestländer bekannt sind!$B$BHelden der Welt - sucht Hochexekutor Derrington auf. Er hat seinen Stützpunkt am Bollwerk östlich von Tirisfal aufgeschlagen!",
    ["O"] = "Sucht Hochexekutor Derrington auf. Sein Stützpunk befindet sich am Bollwerk, östlich von Tirisfal und Unterstadt.",
    ["T"] = "Der Ruf zu den Waffen: Die Pestländer!",
  },
  [10375] = {
    ["D"] = "Ihr habt Euch meinem Volk gegenüber als würdig erwiesen, $N. Jetzt ist es an der Zeit, uns zu beweisen, dass Ihr bereit seid, uns jederzeit beizustehen, auch gegen unserer ärgsten Feind.$B$BDie Oger der Scherbenwelt sind",
    ["O"] = "",
    ["T"] = "Obsidiankriegsperlen",
  },
  [10377] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Keine Angst",
  },
  [10379] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Berührung der Schwäche",
  },
  [10380] = {
    ["D"] = "Wir waren so blind, dass wir nicht gesehen haben, dass unsere beiden größten Feinde sich verbündet haben, $N.$B$BIch wäre nicht überrascht, wenn die Seher uns diese Information absichtlich vorenthalten hätten, aber wir müssen uns nun der Wirklichkeit stellen. Die Brennende Legion und Kael\'thas Sonnenzornarmee dienen unter dem gleichen Banner.$B$BDie Dämonen sind der einzige Grund, aus dem Kael\'thas je in der Lage war, die Technologie der Festung der Stürme nutzen zu können. Ich muss Euch auf Orelis\' Befehl hin bitten, sie zu zerstören.",
    ["O"] = "Kaylaan auf Tulumans Landeplatz möchte, dass Ihr 6 Warptüftler der Gan\'arg, 3 Töchter des Schicksals und 3 Warpmeister der Mo\'arg tötet.$B$BDas Abschließen von Quests für die Aldor führt zu einer Verringerung Eures Rufs bei den Sehern.",
    ["T"] = "Ein dunkler Pakt",
  },
  [10381] = {
    ["D"] = "Meine Mission hier ist zu Ende. Ich habe getan, was Orelis mir befohlen hat - nie habe ich ihn enttäuscht.$B$BNie wieder. Ich sehe ein großes Gemetzel voraus - einen Kreuzzug werden sie es nennen - das auf das neue Bündnis zwischen Kael\'thas und der Brennenden Legion folgen wird.$B$BZuerst werden sie unsere heiligste Stadt dem Feind übergeben und uns dann bitten, im Kampf gegen ihren Feind zu sterben. Geht zurück zu Orelis und sagt ihm, dass die Naaru von nun an ihre Kämpfe alleine austragen können.",
    ["O"] = "Meldet Euch bei Exarch Orelis in Area 52.$B$BDas Ausführen von Quests für die Aldor führt zu einer Verringerung Eures Rufs bei den Sehern.",
    ["T"] = "Nie mehr Aldor",
  },
  [10382] = {
    ["D"] = "Nun, $N, ich hoffe, Ihr seid für die nächste Phase unserer Operation bereit. Die Legion schläft nicht - und wir auch nicht! Nehmt einen Greifen und fliegt zum Ehrenposten, einem kleinen Lager in der Nähe der Front der Legion.$B$BSprecht mit Feldmarschall Brock. Er wird bei Eurer Ankunft Anweisungen für Euch bereithalten. Oh, und sagt diesem teiggesichtigen Schildschmuser einen Gruß von mir.",
    ["O"] = "Nehmt einen Greifen zum Ehrenposten und sprecht mit Feldmarschall Brock.",
    ["T"] = "Geht zur Front",
  },
  [10383] = {
    ["D"] = "Hallo. Ihr solltet mehr über diese Kaninchen herausfinden.",
    ["O"] = "Sprecht mit Jonathan LeCraft über die Kaninchen.",
    ["T"] = "Dies ist ein Brotkrumen!",
  },
  [10384] = {
    ["D"] = "Seht Euch um, Fleischling. Ganz in der Nähe des Transponders sollte sich eine Datenzelle des Astraleums befinden. Sucht sie, öffnet sie und entnehmt den Inhalt.$B$BBringt sie dann sofort zu diesem Transponder zurück!",
    ["O"] = "Das Bild von Kommandant Ameer beim Transponder des Astraleums Zeta im Stützpunkt des Astraleums möchte, dass Ihr die Datenzelle des Astraleums besorgt.",
    ["T"] = "Daten der Astralen",
  },
  [10385] = {
    ["D"] = "Sie benutzen Melder, um die Informationen im Stützpunkt zu verteilen.$B$BEs gibt nur einem Weg, wie Ihr die Melder sehen und ihre Informationen extrahieren könnt. Unglücklicherweise könntet Ihr dadurch einen irreparablen Hirnschaden erlangen.$B$BWenn Ihr immer noch interessiert seid, müsst Ihr einige der Anhänger des Astraleums hier erschlagen und ihre Essenz nehmen. Wenn Ihr eine Essenz habt, trinkt sie. Mit ihrer Hilfe werdet Ihr für kurze Zeit in der Lage sein, die Melder des Astraleums zu sehen. Die Zeit reicht aus, um einen Melder zu zerstören und seine Informationen zu extrahieren.",
    ["O"] = "Das Bild von Kommandant Ameer beim Transponder des Astraleums Zeta im Stützpunkt des Astraleums möchte, dass Ihr 15 Melderdaten des Astraleums besorgt.",
    ["T"] = "Hirnschadengefahr = hoch",
  },
  [10386] = {
    ["D"] = "Oberanführer Hun Stümmelfaust sucht immer nach Freiwilligen für seine selbstmörderischen Angriffe auf die Brennende Legion. Ihr verfügt definitiv über dem Mut für seine \"Missionen\".$B$BSprecht mit ihm, wenn Ihr Appetit auf noch mehr Gemetzel habt. Er feiert gerade da drüben bei dem gefallenen Teufelshäscher. Er und seine Wachen haben ihn vor ein paar Stunden vernichtet. Hätte er nicht so wild und geschickt gekämpft, wären wir jetzt alle tot.",
    ["O"] = "Sprecht mit Oberanführer Hun Stümmelfaust.",
    ["T"] = "Der Teufelshäschertöter",
  },
  [10387] = {
    ["D"] = "Oberanführer Hun Stümmelfaust sucht immer nach Freiwilligen für seine selbstmörderischen Angriffe auf die Brennende Legion. Ihr verfügt definitiv über dem Mut für seine \"Missionen\".$B$BSprecht mit ihm, wenn Ihr Appetit auf noch mehr Gemetzel habt. Er feiert gerade da drüben bei dem gefallenen Teufelshäscher. Er und seine Wachen haben ihn vor ein paar Stunden vernichtet. Hätte er nicht so wild und geschickt gekämpft, wären wir jetzt alle tot.",
    ["O"] = "Sprecht mit Oberanführer Hun Stümmelfaust.",
    ["T"] = "Der Teufelshäschertöter",
  },
  [10388] = {
    ["D"] = "Ich hoffe, Ihr hattet einen guten Flug, mein Freund. Während Ihr fort wart, ist ein Bote angekommen. Wie es aussieht, hat Nazgrel einen neuen Auftrag für Euch. Am besten geht Ihr nach Thrallmar zurück und sucht ihn auf. Ich habe das Gefühl, dass die Legion gerade erst warm geworden ist.",
    ["O"] = "Kehrt nach Thrallmar zurück und sprecht mit Nazgrel.",
    ["T"] = "Rückkehr nach Thrallmar",
  },
  [10389] = {
    ["D"] = "Grüße, $N. Ich habe den Schriftwechsel der Legion entziffert und ihre strategischen Pläne für die Halbinsel aufgedeckt. Offensichtlich sammelt sich in der Nähe der Teiche von Aggonar im Westen eine große Gruppe von teuflischen Terrorbestien. Das dürfen wir nicht ohne Kampf zulassen. Ihr müsst Euch dorthin begeben und ihre verfluchten Reihen ausdünnen.",
    ["O"] = "Magister Blutfalke möchte, dass Ihr 10 Terrorbestien bei den Teichen von Aggonar tötet.",
    ["T"] = "Das Leid und die Dunkelheit",
  },
  [10390] = {
    ["D"] = "$N, wir haben guten Grund zu vermuten, dass das Konstruktionslager, das sich am östlichsten befindet, noch nicht fertig ist. Die Gan\'argsklaven der Legion arbeiten rund um die Uhr, um die Einrichtung zum Laufen zu bringen. Das können wir nicht zulassen.$B$BBegebt Euch nach Osten zum Konstruktionslager: Mageddon und tötet 10 Gan\'arg. Bringt mir den Kopf ihres Meisters, Schmiedebestie Sägeklinge! So werden wir die Teufelshäscherproduktion der Legion zum Erliegen bringen!",
    ["O"] = "Nazgrel in Thrallmar möchte, dass Ihr das Konstruktionslager: Mageddon angreift, 10 Diener der Gan\'arg tötet und mit Schmiedebestie Sägeklinges Kopf zurückkehrt.",
    ["T"] = "Konstruktionslager: Mageddon",
  },
  [10391] = {
    ["D"] = "Es gibt eine weitere Produktionsstätte. Das Konstruktionslager: Zorn liegt direkt im Nordosten von Thrallmar. Die Dämonen haben eine Reihe von Teufelskanonen am Grat entlang aufgestellt - und sie zielen direkt auf uns! Ihr müsst in das Lager eindringen und diese Kanonen außer Gefecht setzen.$B$B$N, Ihr müsst sichergehen, dass Ihr alle zerstört - sonst kann die Legion nichts daran hindern, uns mit den Feuern der Hölle zu versengen!",
    ["O"] = "Zerstört 3 Teufelskanonen V1 im Konstruktionslager: Zorn und kehrt dann zu Nazgrel in Thrallmar zurück.",
    ["T"] = "Kanonen des Zorns",
  },
  [10392] = {
    ["D"] = "Ihr ehrt uns, $N. Aber nun müssen wir zuschlagen, bevor die Legion sich erholt! Nördlich von Thrallmar liegt der Invasionspunkt: Vernichter. Dort befindet sich ein Warpportal, durch das die Legion ihre Verstärkung bezieht.$B$BIhr müsst dieses Portal überlasten - und es zurück in den Abgrund schicken, aus dem es gekommen ist. Den Aktivierungsschlüssel trägt vermutlich der Kommandant des Punkts, Kriegshetzer Arix\'Amal, bei sich. Tötet ihn. Benutzt dann den Schlüssel bei dem Portal und tut, was getan werden muss. Möge der Segen des Kriegshäuptlings auf Euch ruhen.",
    ["O"] = "Tötet Arix\'Amal und nehmt Euch den Torschlüssel der Brennenden Legion. Benutzt den Schlüssel bei der Rune der Bosheit und kehrt danach zu Nazgrel in Thrallmar zurück.",
    ["T"] = "Die Schwelle zum Abgrund",
  },
  [10393] = {
    ["D"] = "Der Text auf dem Brief ist in dunklem, unentzifferbarem Gekrakel geschrieben. Je mehr Ihr sie anstarrt, desto mehr Symbole scheinen sich vor Euren Augen zu drehen und zu leuchten. Es gibt nur einen in Thrallmar, der so eine bösartige Schrift entziffern kann - der Blutelfenmagister Blutfalke.",
    ["O"] = "Bringt den Schriftwechsel der Legion zu Magister Blutfalke in Thrallmar.",
    ["T"] = "Boshafte Pläne",
  },
  [10394] = {
    ["D"] = "Passt genau auf, $N. Wir haben die Lager der Legion, die entlang des Grats vor uns liegen, überwacht. Wir haben guten Grund zur Annahme, dass die Dämonen in diesen so genannten \'Konstruktionslagern\' ihre verdammten Teufelshäscher herstellen.$B$BIhr könnt das am nächsten gelegene Ziel, das Konstruktionslager: Mageddon von hier aus im Westen sehen. Unsere Geheimagenten melden, dass es sich immer noch im Bau befindet. Wenn Ihr die Gan\'argdiener und ihren Meister, Schmiedebestie Sägeklinge, tötet, können wir der Teufelshäscherproduktion der Legion ein schnelles Ende machen!",
    ["O"] = "Tötet 10 Diener der Gan\'arg und Schmiedebestie Sägeklinge im Konstruktionslager: Mageddon und kehrt danach zu Feldmarschall Brock beim Ehrenposten zurück.",
    ["T"] = "Unterbrechung beim Konstruktionslager: Mageddon",
  },
  [10395] = {
    ["D"] = "Der Text auf dem Brief ist in dunklem, unentzifferbarem Gekrakel geschrieben. Je mehr Ihr sie anstarrt, desto mehr Symbole scheinen sich vor Euren Augen zu drehen und zu leuchten. Es gibt nur einen in der Ehrenfeste, der so eine bösartige Schrift entziffern kann - der Draeneimystiker Kryv.$B$BBringt den Schriftwechsel der Brennenden Legion zu Warpseher Kryv im Kriegsraum der Ehrenfeste.",
    ["O"] = "Bringt den Schriftwechsel zu Warpseher Kryv in der Haupthalle der Ehrenfeste.",
    ["T"] = "Der dunkle Schriftwechsel",
  },
  [10396] = {
    ["D"] = "Jetzt müssen wir uns auf unser zweites Ziel konzentrieren, das Konstruktionslager: Zorn. Es liegt direkt westlich von Mageddon. Das Problem ist, dass die Dämonen eine Reihe von Teufelskanonen direkt auf den Außenposten der Horde in Thrallmar gerichtet haben! Ich kann es zwar kaum fassen, aber wir müssen der Horde helfen, um unsere Linien beim Dunklen Portal aufrechtzuerhalten. Wir können es uns nicht erlauben, Thrallmar fallen zu lassen. Ihr müsst alle Kanonen zerstören, bevor die Legion unsere Verbündeten in die Luft jagt!",
    ["O"] = "Zerstört 3 Teufelskanonen V1 im Konstruktionslager: Zorn und kehrt dann zu Feldmarschall Brock beim Ehrenposten zurück.",
    ["T"] = "Der Feind meines Feindes...",
  },
  [10397] = {
    ["D"] = "Wir haben die letzte Basis der Legion auf dem Grat ausgemacht. Der Invasionspunkt: Vernichter liegt nordwestlich des Konstruktionslagers: Zorn. Dort befindet sich ein Warpportal, mit dessen Hilfe die Legion Verstärkung herbeiholt!$B$BIhr müsst an dieses Portal herankommen und verhindern, dass noch mehr Dämonen dadurch in unsere Welt kommen. Der Oberanführer des Punkts, Kriegshetzer Arix\'Amal, hat den Aktivierungsschlüssel für das Portal. Nehmt ihn ihm ab und schaltet dieses verdammte Tor ab!",
    ["O"] = "Tötet Arix\'Amal. Nehmt den Torschlüssel der Brennenden Legion und benutzt ihn bei der Rune der Bosheit.",
    ["T"] = "Invasionspunkt: Vernichter",
  },
  [10398] = {
    ["D"] = "Geht zu Ehrenfeste.",
    ["O"] = "Geht zu Ehrenfeste.",
    ["T"] = "Rückkehr zur Ehrenfeste",
  },
  [10399] = {
    ["D"] = "Es ist genau so, wie ich befürchtet habe. Der Dämon, den Ihr unlängst getötet habt, Arix\'Amal, stand im Dienst des heimlichen Legionslords Arazzius des Grausamen. Wie es scheint hat dieser Bösewicht eine neue Streitmacht von höllischen Terrorbestien in der Nähe der Teiche von Aggonar aufgestellt.$B$BIhr müsst Euch dorthin begeben und die Terrorbestien vernichten, bevor sie uns angreifen können. Aber seid vorsichtig, Freund. Die Teiche von Aggonar liegen in einem gemarterten, giftigen Tal. Ihr findet die verfluchten Teiche nordwestlich von hier, hinter der Höllenfeuerzitadelle.$B$BDas Licht sei mit Euch, $N.",
    ["O"] = "Tötet 10 Terrorbestien bei den Teichen von Aggonar und kehrt dann zu Warpseher Kryv in der Haupthalle der Ehrenfeste zurück.",
    ["T"] = "Das Herz der Dunkelheit",
  },
  [10400] = {
    ["D"] = "Der ehrwürdige Warpseher hat mir von Euren waghalsigen Abenteuern erzählt, $N. Er sprach auch von dem neuen Feind, den Ihr entdeckt habt. Dieser Arazzius der Grausame ist der mächtigste und bösartigste Feind, dem die Söhne Lothars sich je stellen mussten.$B$BWerdet Ihr uns zur Seite stehen, $N? Werdet Ihr diesem Legionslord gegenübertreten und ihn der Gerechtigkeit des Lichts übergeben? Wenn ja, dann begebt Euch zu den Teichen von Aggonar. Wenn Ihr ihn nicht findet, wird seine Bosheit sicher Euch finden.",
    ["O"] = "Tötet Arazzius den Grausamen bei den Teichen von Aggonar und kehrt danach zu Truppenkommandant Danath Trollbann in der Ehrenfeste zurück.",
    ["T"] = "Oberanführer",
  },
  [10401] = {
    ["D"] = "Wisst Ihr, was passiert wäre, wenn Ihr den Boten nicht abgefangen hättet, $N?$B$B<Nazgrel nickt.>$B$BDie gesamte Armee der Legion des Höllenfeuers hätte vor unserer Tür gestanden. Einen solchen Angriff hätten wie niemals überlebt.$B$BEs gibt nur eine Möglichkeit zu verhindern, dass diese Befehle jemals die Front erreichen. Ihr müsst zu den Teichen von Aggonar, westlich von hier, reisen und diesen Arazzius töten.$B$BWenn Ihr das tut, werde ich dafür sorgen, dass der Kriegshäuptling Euch eine Auszeichnung zukommen lässt.",
    ["O"] = "Nazgrel in Thrallmar auf der Höllenfeuerhalbinsel möchte, dass Ihr Arazzius den Grausamen tötet.",
    ["T"] = "Mission: Beendet alles, seid alles",
  },
  [10403] = {
    ["D"] = "Vielleicht ist noch nicht alle Hoffnung für das Lumpenpack verloren. Ich bin einer der vier Ältesten, die diesen Stamm leiteten, bevor wir von den Illidari versklavt wurden. Ich dachte, dass die anderen tot seien, aber dann habe ich erfahren, dass zumindest zwei weitere noch am leben sind - angekettet und machtlos, so wie ich.$B$BIch habe Naladu noch nicht finden können und befürchte das Schlimmste. Oben in den Ruinen gibt es noch mehr Hütten, vielleicht könntet Ihr dort nach ihm suchen. Es wäre ein großes Glück für unseren Stamm, wenn alle Ältesten am Leben wären.",
    ["O"] = "Sucht Naladu in den Ruinen von Sha\'naar.",
    ["T"] = "Naladu",
  },
  [10404] = {
    ["D"] = "Wir haben erfahren, dass die Brennende Legion Kael\'thas mit technologischem Wissen unterstützt hat, und so die Bedrohung, die von ihm ausgeht, noch um ein Vielfaches vergrößert hat.$B$BDa die Legion in der Gegend so zahlreich vertreten ist, können wir den größten Schaden nur durch einen gezielten Angriff auf die Konstruktionsbasen im Norden erreichen.$B$BGeht zur Konstruktionsbasis: Vergessenheit und zur Konstruktionsbasis: Gehenna und zerstört unsere Feinde. Möge das Licht Eure Klinge führen, $N.",
    ["O"] = "Exarch Orelis in Area 52 möchte, dass Ihr zur Konstruktionsbasis: Vergessenheit und zur Konstruktionsbasis: Gehenna geht und 12 Feuerfäuste der Gan\'arg, 6 Konstruktionsherren der Gan\'arg und 6 Zornschaffer tötet.$B$BDas Ausführen von Quests für die Aldor führt zu einer Verringerung Eures Rufs bei den Sehern.",
    ["T"] = "Gegen die Legion",
  },
  [10405] = {
    ["D"] = "Wir müssen die Maschinen zerstören, die diese abnormalen Dunkelpirscher hervorbringen. Ihren Protobeschleuniger anzugreifen ist sinnlos, da es sich um eine astrale Erfindung handelt. Wir müssen stattdessen die Rohre der Manaschmiede, die sie als Energieleitung verwenden, zerstören.$B$BIch habe fast alles, was ich brauche, um ein Gerät herzustellen, das die Energieleitung überlasten kann. Ich brauche nur noch die Bandagen eines Archon oder Oberanführers, der sich bald verwandeln wird, um das Gerät einzustellen.",
    ["O"] = "Das Bild von Kommandant Ameer bei dem Transponder des Astraleums Zeta im Stützpunkt des Astraleums möchte, dass Ihr eine vorbereitete Bandage des Astraleums besorgt.",
    ["T"] = "S-A-B-O-T-A-G-E",
  },
  [10406] = {
    ["D"] = "Ich schicke einen meiner Demolierer los, um das Störgerät zu platzieren. Sorgt nur dafür, dass ihm kein Leid geschieht, während er den Störer aufstellt.$B$BBeschützt ihn! Er wird sich nicht wehren, wenn er von Feinden angegriffen wird, da er nur ein holografisches Bild ist, aber auch das kann zerstört werden!$B$BKehrt zu mir zurück, wenn Ihr fertig seid.",
    ["O"] = "Das Bild von Kommandant Ameer bei dem Transponder des Astraleums Zeta im Stützpunkt des Astraleums möchte, dass Ihr den Demolierer des Protektorats zur Energieleitung der Manaschmiede Ultris begleitet und ihn beschützt, während er die Leitung sabotiert.",
    ["T"] = "Die Nachricht überbringen",
  },
  [10407] = {
    ["D"] = "Der berüchtigte Eredar, Socrethar, führt die Brennende Legion im Nethersturm an. Allein seinen Namen zu nennen lässt die meisten Draenei erzittern. Er erteilt seine Befehle von einer Insel im Nordwesten aus, die nur seine vertrauenswürdigsten Leutnants betreten können.$B$BUm dorthin zu gelangen, müsst Ihr einen Teleportationsstein besorgen, was nicht einfach wird. Die erste Hälfte des Steins befindet sich im Besitz von Konstruktionsmeister Morug in der Konstruktionsbasis: Vergessenheit. Die zweite Hälfte hat der Konstruktionsmeister Silroth in der Konstruktionsbasis: Gehenna.",
    ["O"] = "Anachoret Karja in Area 52 möchte, dass Ihr die erste Hälfte von Socrethars Stein von Konstruktionsmeister Morug in der Konstruktionsbasis: Vergessenheit und die zweite Hälfte des Steins von Konstruktionsmeister Silroth in der Konstruktionsbasis: Gehenna besorgt.$B$BDas Ausführen von Quests für die Aldor führt zu einer Verringerung Eures Rufs bei den Sehern.",
    ["T"] = "Socrethars Schatten",
  },
  [10408] = {
    ["D"] = "Ich habe einen weiteren Störer in meinem Besitz. Einer, der in der Lage ist, die drei Energieleitungen, die Salhadaar umgeben, zu zerstören. Wenn die Energieleitungen einmal vernichtet sind, werden sich Salhadaars schützende Kraftschilde senken. Für diese Tat wird er Euch mit einer Wildheit angreifen, die Ihr noch nie zuvor gesehen habt, $N.$B$BWenn Ihr bereit seid, die Welt von Salhadaar zu befreien, gebe ich Euch den Störer.",
    ["O"] = "Das Bild von Kommandant Ameer beim Transponder des Astraleums Zeta im Stützpunkt des Astraleums möchte, dass Ihr den Störer des Protektorats benutzt, um Salhadaar Kraftfelder zu senken und Nexuskönig Salhadaar zu vernichten.",
    ["T"] = "Nexuskönig Salhadaar",
  },
  [10409] = {
    ["D"] = "Wenn man mit mächtigen Eredar zu tun hat, sind große Armeen meist nutzlos. Ihre verderbte Macht über die sterblichen Seelen ist so stark, dass nur die mit einem starken Willen ihnen gegenübertreten können.$B$BBringt diesen Stein zum Invasionspunkt: Oberanführer zwischen der Konstruktionsbasis: Vergessenheit und der Konstruktionsbasis: Gehenna und nehmt Eure engsten und stärksten Freunde mit. Orelis und Karja erwarten Euch bei Socrethars Sitz. Ich werde Adyen mit ihnen losschicken.$B$BMöge das Licht sich heute behaupten, $N. Möge Socrethars Ende nahen.",
    ["O"] = "Hohepriesterin Ishanah möchte, dass Ihr zum Transporter der Legion am nordwestlichsten Ende des Nethersturms geht und Euch zu Socrethars Sitz teleportiert. Wenn Ihr dort seid, besiegt Socrethar.$B$BDas Ausführen von Quests für die Aldor führt zu einer Verringerung Eures Rufs bei den Sehern.",
    ["T"] = "Todesstoß für die Legion",
  },
  [10410] = {
    ["D"] = "Die Zeit ist gekommen, da Ihr Euch Socrethar stellen müsst, $N. Einem Eredar entgegen zu treten ist eine Aufgabe, die keiner von uns auf die leichte Schulter nimmt. Schon ein kleiner Fehler kann uns das Leben kosten, wenn wir unvorbereitet sind.$B$BBringt Socrethars Teleportationsstein zu Hohepriesterin Ishanah beim Schrein des Endlosen Lichts in Shattrath. Sie wird entscheiden, wie mit der Situation umzugehen ist.",
    ["O"] = "Bringt Socrethars Teleportationsstein zu Hohepriesterin Ishanah beim Schrein des Endlosen Lichts in Shattrath.$B$BDas Ausführen von Quests für die Aldor führt zu einer Verringerung Eures Rufs bei den Sehern.",
    ["T"] = "Ishanahs Hilfe",
  },
  [10411] = {
    ["D"] = "Um die zerstörte Manaschmiede herum findet Ihr die Überbleibsel von Dimensius\' Taten. Sickernder Schlick verschmutzt die Teiche um Ultris herum. Direkt unter diesen Teichen, wo sich nun der Stützpunkt des Astraleums befindet, findet Ihr noch mehr Müll in Form von Leerenabschaum.$B$BDer beste Weg, ihn zu entsorgen, ist, den Schleim mithilfe von Elektroschocks in Kugeln zu verwandeln. Benutzt dieses Elixier, um Eure Angriffe unter Strom zu setzen. Die Entladung, die dabei entsteht, löst den Schleim in seine Urform auf. Zerstört die Kugeln!",
    ["O"] = "Forscher Navuud im Wachposten des Protektorats im Nethersturm möchte, dass Ihr 30 Leerenabschaumkugeln und 30 Sickerschlickkugeln zerstört.",
    ["T"] = "Elektroschock!",
  },
  [10412] = {
    ["D"] = "Es ist kein Geheimnis, dass Kael\'thas Sonnenwanderer uns als große Bedrohung sieht. Nüchtern betrachtet gibt es nicht viele Sin\'dorei, die frisch aus Silbermond kommen und bereit sind, sich der Gesellschaft, die Kael\'thas unterhält, anzuschließen.$B$BEs ist auch kein Geheimnis, dass es sich hier nicht nur um Wortgefechte handelt. Die Seher und die vielen Armeen, die Kael\'thas dienen, haben und werden sich angreifen, sobald sie sich begegnen. Der einzige Weg, wie Ihr beweisen könnt, dass Ihr auf unserer Seite steht, ist, sich dem Kampf anzuschließen, $N.$B$BKaels niederrangige Anhänger tragen Siegel der Feuerschwingen bei sich, die ihren Rang anzeigen. Bringt sie mir.",
    ["O"] = "Magistrix Fyalenn in Shattrath möchte, dass Ihr ihr 10 Siegel der Feuerschwingen bringt.$B$BDas Ausführen von Quests für die Seher führt zu einer Verringerung Eures Rufs bei den Aldor.",
    ["T"] = "Siegel der Feuerschwingen",
  },
  [10413] = {
    ["D"] = "Ihr befüllt vorsichtig eine Phiole mit dem Schlamm des gefallenen Leerenschreckens.$B$BForscher Navuud im Wachposten des Protektorats wäre an einem solchen Fund sicher interessiert. Bringt ihn sofort zu ihm!",
    ["O"] = "Bringt die Phiole mit Leerenschreckensschlamm zu Forscher Navuud im Wachposten des Protektorakts im Nethersturm.",
    ["T"] = "Die Schrecken der Verschmutzung",
  },
  [10414] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Einzelne Siegel der Feuerschwingen",
  },
  [10415] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Mehr Siegel der Feuerschwingen",
  },
  [10416] = {
    ["D"] = "Arkane Folianten sind keine gewöhnlichen Bücher, die sich mit arkanen Themen befassen. Ihnen wohnt eine wesenhafte Macht inne - eine Macht, die ich in eine einzelne arkane Rune bündeln kann.$B$BSolltet Ihr auf Euren Reisen einen arkanen Folianten finden, werde ich Euch eine solche Rune geben. Nur die mächtigsten magiebegabten Wesen tragen die Bücher, die ich brauche, bei sich. In der Scherbenwelt sind das meistens Kaels Blutelfen, aber Ihr wärt überrascht, wo solche Bücher des Öfteren auftauchen.",
    ["O"] = "Voren\'thal der Seher in Shattrath möchte, dass Ihr einen arkanen Folianten besorgt.$B$BDas Ausführen von Quests für die Seher führt zu einer Verringerung Eures Rufs bei den Aldor.",
    ["T"] = "Bündelung der Macht",
  },
  [10417] = {
    ["D"] = "Wo auch immer das Konsortium Einzug hält, bringen wir unsere Biokuppeltechnologie mit. Diese Technologie erlaubt uns, in einer kontrollierten und lebenserhaltenden Atmosphäre zu arbeiten, da wir uns meist in feindlichen Gebieten befinden.$B$BDie bösartigen Bedingungen und Kreaturen des Nethersturms haben begonnen, die Ausrüstung, die unsere Biokuppeln aufrechterhält, zu zerstören, besonders den Generator im Osten. Ich habe einen Diagnoselauf dort begonnen, damit ich hoffentlich herausfinden kann, was gegen den Schaden getan werden kann. Könntet Ihr die Resultate dort abholen und zu mir bringen?",
    ["O"] = "Beschafft die Untersuchungsergebnisse und bringt sie zu Mehrdad im Mittelreichposten.",
    ["T"] = "Führt eine Diagnose durch!",
  },
  [10418] = {
    ["D"] = "Als ich das Gebiet nach Hinweisen auf den Schaden unserer Ausrüstung abgesucht habe, habe ich Teile der Schildgeneratoren auf dem Grund des kleinen Sees im Norden gesehen. Ich bin mir nicht sicher, warum die Krokilisken sich für unsere Ausrüstung interessieren, aber wir müssen dieses Problem beseitigen.$B$BDas Konsortium ist sich in dieser Sache einig. Wenn es zu einer Entscheidung zwischen der Ausrüstung und Krokilisken kommt, müssen die Tiere gehen. Ich werde Euch angemessen belohnen, wenn Ihr Euch darum kümmert.",
    ["O"] = "Mehrdad beim Mittelreichposten möchte, dass Ihr 8 Stachelschuppenkrokilisken tötet.",
    ["T"] = "Die Saboteure ausschalten",
  },
  [10419] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Arkane Folianten",
  },
  [10420] = {
    ["D"] = "Dieser Tempel ist eine der heiligsten Stätten der ganzen Scherbenwelt. Die Macht des Lichts ist hier so stark, dass es weltliche Gegenstände sogar von der schlimmsten teuflischen Verderbnis befreien kann.$B$BFalls Ihr bei Euren Kämpfen mit den dämonischen Feinden teuflische Waffen finden solltet, bringt sie zu mir. Dann werde ich Euch die reinigende Kraft des Lichts beweisen.",
    ["O"] = "Bringt eine Ladung teuflische Waffen zu Ishanah in Shattrath.$B$BDas Abschließen von Quests für die Aldor führt zu einer Verringerung Eures Rufs bei den Sehern.",
    ["T"] = "Ein reinigendes Licht",
  },
  [10421] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Teuflische Waffen",
  },
  [10422] = {
    ["D"] = "Hauptmann Tyralius hat eine Truppe von Soldaten vor 10 Tagen in Richtung des Stützpunkts des Astraleums geführt. Seitdem haben wir nichts von ihm oder seinen Leuten gehört.$B$BEs ist sehr wahrscheinlich, dass sie überfallen und getötet wurden, aber ich bekomme ein merkwürdiges Signal auf meinen Instrumenten.$B$BIch glaube, es ist ein Notsignal, aber diese Dinge haben sich schon öfter geirrt. Wenn Tyralius noch am Leben ist, halten sie ihn in einem Ihrer Gefängnisse fest. Ihr müsst das Gefängnis finden und es mit einem Schlüssel, den Ihr wahrscheinlich bei einem Wärter finden könnt, öffnen.",
    ["O"] = "Flesh Handler Viridius at the Protectorate Watch Post in Netherstorm has asked that you find and free Captain Tyralius.",
    ["T"] = "Hauptmann Tyralius",
  },
  [10423] = {
    ["D"] = "Meisteringenieur Ghabar möchte sicher die Ergebnisse der Diagnose erfahren. Sie zeigen uns wenigstens, dass wir die Funktionalität der Kuppelgeneratoren genau im Auge behalten müssen. Wenn die Felder immer instabiler werden, können wir ihre Auswirkungen auch immer schwerer kontrollieren. Das könnte ein Desaster für unsere Expedition und alles, was sich in den Kuppeln befindet, bedeuten.$B$BIhr findet Ghabar in der Sturmsäule. Folgt einfach der Straße nördlich dieser Biokuppel und überquert die Brücke. Sie führt Euch direkt zum Aufzug unten in der Säule.",
    ["O"] = "Speak with Ghabar at the Stormspire.",
    ["T"] = "Zur Sturmsäule",
  },
  [10424] = {
    ["D"] = "Wenn Mehrdads Ergebnisse richtig sind - und ich habe keinen Grund daran zu zweifeln - könnte der Rest der Kuppeln in Gefahr sein.$B$BIch glaube jedoch, dass Mehrdad nicht den richtigen Grund für die Defekte gefunden hat. Tiere können die Generatoren bisweilen schon beschädigen, aber niemals so schwer.$B$BUm sicher zu gehen, möchte ich, dass Ihr ein paar Diagnoseläufe bei dem Hauptkuppelgenerator der Sturmsäule am östlichen Rand in der Biokuppel Sutheron durchführt.",
    ["O"] = "Benutzt das Diagnosegerät in der Nähe des Generators der Biokuppel Sutheron und bringt die Diagnoseergebnisse zu Gabhar in der Sturmsäule zurück.",
    ["T"] = "Diagnose: kritisch",
  },
  [10425] = {
    ["D"] = "K\'aresh sei Dank! Er hätte mich in Stücke gerissen!$B$BIch bin ein Soldat des Protektorats, Fleischling. Meine Truppe wurde vor etwa 10 Tagen auf eine Aufklärungsmission geschickt. Als wir uns über die oberen Regionen dieser Insel bewegten, wurden wir von einer Gruppe des Astraleums überfallen. Zumindest denke ich, dass es das Astraleum war. Sie hatten die gleichen Energiefarben, aber ihre Bandagen sahen irgendwie leer aus.$B$BSie haben nur Tyralius verschont. Ich glaube, sie haben ihn in ein Gefängnis eingesperrt.$B$BIch muss zurück zum Wachposten des Protektorats! Könnt Ihr mich dorthin begleiten?",
    ["O"] = "Begleitet den gefangenen Vorposten des Protektorats zurück zum Wachposten des Protektorats im Nethersturm.",
    ["T"] = "Ausbruch aus dem Stützpunkt",
  },
  [10426] = {
    ["D"] = "Schaut Euch nur um, $C! Ist es nicht erstaunlich? Natürlich ist diese unglaubliche Fülle von Leben ein Nebenprodukt der Kuppeln der Astralen, aber stellt Euch nur vor, was diese Technologie in den Händen der Expedition bedeuten könnte.$B$BIch bereite mich darauf vor, mit den Astralen zu verhandeln, um Zugang zu ihrer Technologie zu bekommen, aber ich wüsste zuvor gerne genauer, welche Auswirkungen sie auf das pflanzliche Leben hat.$B$BIch habe ein Gerät gebaut, mit dem ich die Energien auf die Peitscher in dieser Gegend richten kann. Wärt Ihr so nett und würdet es für mich ausprobieren und mir die Ergebnisse mitteilen?",
    ["O"] = "Benutzt den Energiefeldmodulator bei den Farahlonpeitschern. Beobachtet die Ergebnisse und meldet Euch bei Aurine Mondflamm in der Sturmsäule zurück, wenn Ihr 10 Tests durchgeführt habt.",
    ["T"] = "Flora der Biokuppel",
  },
  [10427] = {
    ["D"] = "Eure Arbeit bei den Peitschern stellte sich als erstaunliche Information heraus, aber ich frage mich, ob das auch für die Tiere gilt.$B$BIch fürchte jedoch, dass es schwieriger wird, Informationen über die Talbuks zu sammeln, als über die Peitscher.$B$BMit diesem Markierungsgerät könnt Ihr einen geschwächten Talbuk schlafen legen und ihn mit einer Markierung versehen, mit deren Hilfe ich das Tier während seiner Wachstumsphase verfolgen kann. Der Mechanismus des Markierungsgeräts funktioniert erst, wenn der Talbuk schon sehr erschöpft ist.",
    ["O"] = "Markiert 12 Talbuks für Aurine Mondflamm in der Sturmsäule. Ihr könnt mit dem Talbukmarkierer sowohl Talbukböcke als auch Talbukkühe markieren.",
    ["T"] = "Fauna der Biokuppel",
  },
  [10428] = {
    ["D"] = "Von fiebrigen Träumen geplagt sprach unser verletzter Gast von einem Fischer, Cowlen, der auf der Silbermythosinsel im Südwesten vor der Küste der Azurmythosinsel, in Schwierigkeiten war.$B$BNach all den Geschehnissen hier hatte keiner die Gelegenheit, nachzuschauen, aber ich mache mir doch ein paar Sorgen. Vielleicht könnt Ihr etwas tun, $N?",
    ["O"] = "Sucht Cowlen bei den südöstlichen Docks der Silbermythosinsel vor der Küste der Azurmythosinsel.",
    ["T"] = "Der vermisste Fischer",
  },
  [10429] = {
    ["D"] = "Die Technologie der Kuppeln ist so mächtig! Sicher gibt es Grenzen, die wir nicht zu überschreiten wagen, $N.$B$BWesen, die zu schnell oder zu groß wachsen, können genauso schlimm sein wie eine unfruchtbare Landschaft.$B$BIch habe Gerüchte gehört, dass sich genau so eine Kreatur im Teich im Osten herumtreibt. Die Händler der Astralen wollen die übergroße Hydra aus Spaß erlegen, aber ich habe etwas anderes mit dem Riesen vor. Bringt mir das Herz dieser Kreatur, damit ich es untersuchen kann.",
    ["O"] = "Aurine Mondflamm in der Sturmsäule möchte, dass Ihr ihr ein titanisches Hydraherz bringt.",
    ["T"] = "Wenn die Natur zu weit geht",
  },
  [10430] = {
    ["D"] = "Ich habe an einem tragbaren Reparatursatz für den Generator gearbeitet, der in den kälteren Regionen, in denen das Konsortium Geschäfte macht, funktioniert. Zugegeben, er war nicht für den Nethersturm entwickelt worden, aber wir sollten trotzdem keine Probleme damit haben.$B$BIch habe bereits einen Test mit einem meiner besten Techniker bei der Biokuppel Fernfeld hinter dem Netherstein im Nordosten vereinbart. Bringt diesen Prototypen zu Tashar. Er wird wissen, was damit zu tun ist. Wenn er funktioniert können wir ihn benutzen, um die Schildgeneratoren bei den größeren Kuppeln zu verstärken.",
    ["O"] = "Bring the Repair Apparatus to Tashar at Eco-Dome Farfield.",
    ["T"] = "Den Prototyp testen",
  },
  [10431] = {
    ["D"] = "Die Befehle des Sonnenzorns bestätigen den Verdacht, dass Kael\'thas bei dem Bau und dem Betrieb der Manaschmieden Hilfe von außen bekommen hat. Wer dahinter steckt, ist immer noch ein Geheimnis, doch wir können es wahrscheinlich in der Manaschmiede Ara herausfinden.$B$BIch habe eine Gruppe meiner Leute losgeschickt, um die Gegend zu erkunden. Geht zu Tulumans Landeplatz nördlich von hier und sprecht mit Kaylaan. Hört Euch an, was er in der Manaschmiede Ara gefunden hat. Findet heraus, wer die Armee des Sonnenzorns unterstützt, und kümmert Euch um sie.",
    ["O"] = "Geht zu Tulumans Landeplatz, nördlich von Area 52, und sprecht mit Kaylaan.$B$BDas Ausführen von Quests für die Aldor führt zu einer Verringerung Eures Rufs bei den Sehern.",
    ["T"] = "Hilfe von außen",
  },
  [10432] = {
    ["D"] = "Wie Ihr sicher bemerkt habt, laufen in der Manaschmiede Ara überall Diener der Brennenden Legion herum. Kael\'thas ist einen Bund mit der Brennenden Legion eingegangen. Er muss gehört haben, dass Illidan dem Wahnsinn verfallen ist, und den Meister gewechselt haben.$B$BWenn die Seher dafür einen stichhaltigen Beweis hätten, wäre das ein deutlicher politischer Vorteil für sie. Ich weiß genau, dass die Streitkräfte der Legion bei der Manaschmiede hier Befehle haben, die Kaels Siegel tragen.$B$BBesorgt diese Befehle und bringt sie zu Thalodien.",
    ["O"] = "Besorgt Euch 8 Befehle von Kael\'thas von den Dämonen bei der Manaschmiede Ara und bringt sie zu Spionagemeister Thalodien in Area 52.$B$BDas Ausführen von Quests für die Seher führt zu einer Verringerung Eures Rufs bei den Aldor.",
    ["T"] = "Eindeutige Beweise",
  },
  [10433] = {
    ["D"] = "Die Astralen sind sehr klug und aufmerksam, daher ist es kein Zufall, dass ich hier als einfacher Fellhändler auftrete. Tatsächlich wurde ich von Area 52 hierher geschickt, um die Technologie, die die Astralen in ihren Biokuppeln einsetzen, zu bewerten.$B$BIch würde gerne Eure Hilfe in Anspruch nehmen, $C. Wir wurden darum gebeten, wo es nur geht andere Völker als Goblins als Spione einzusetzen.$B$BIch möchte, dass Ihr mir als Jäger verkleidet ein paar Reißzahnpelze bringt. Ihr findet die Luchse überall bei der Biokuppel Mittelreich.",
    ["O"] = "Bringt 10 Reißzahnpelze zu Shauly Pore im Mittelreichposten.",
    ["T"] = "Den Schein wahren",
  },
  [10434] = {
    ["D"] = "Ausgezeichnete Arbeit.$B$B<Shauly zwinkert Euch zu.>$B$BIch glaube, sie kaufen es uns ab. Wenn ich Euch jetzt zu meinen Verbündeten in der Sturmsäule schicke, werden sie nicht misstrauisch werden.$B$BHaltet nach Aktion Jaxon und Audi der Nadel Ausschau. Sie werden Euch alles sagen, was Ihr wissen müsst. Sagt ihnen, dass Shauly Euch geschickt hat.",
    ["O"] = "Speak with Audi the Needle at the Stormspire.",
    ["T"] = "Das dynamische Duo",
  },
  [10435] = {
    ["D"] = "<Audi sieht sich um und senkt die Stimme.>$B$BIch bin sicher, dass Shauly Euch ein wenig von unserer Arbeit hier erzählt hat. Als Jaxon und ich draußen im Feld auf der Jagd waren, haben wir Teile von den Generatoren der Biokuppeln der Astralen gesammelt und sie in den Nestern unserer Beute, den Raptoren bei der Biokuppel Fernfeld, versteckt.$B$BDas ist eine abgelegene Gegend nordöstlich der Sturmsäule, in der Nähe des Nethersteins. Ich nehme nicht an, dass Ihr dort auf viele Astrale treffen werdet, aber würdet Ihr uns helfen, die Kuppelgeneratorenteile zu sammeln?",
    ["O"] = "Audi die Nadel bei der Sturmsäule möchte, dass Ihr 10 Kuppelgeneratorsegmente besorgt.",
    ["T"] = "Die Waren zurückholen",
  },
  [10436] = {
    ["D"] = "Bevor wir den Test beginnen können, müssen wir sicherstellen, dass das Gebiet frei von allem ist, was uns dabei stören könnte. In unserem Fall sind das diese zu groß geratenen Echsen.$B$B<Tashar zeigt auf die Sichelzahnraptoren.>$B$BEs ist nicht voraussehbar, was passiert, wenn einer von ihnen auf die Idee kommt, genüsslich in den Generator zu beißen, während er gerade auflädt. Ich habe in all den Jahren als Techniker schon viele merkwürdige Dinge gesehen, darunter auch ein paar Mutationen, die einst ganz normale Tiere waren.",
    ["O"] = "Tashar bei der Biokuppel Fernfeld möchte, dass Ihr 12 Sichelzahnraptoren tötet.",
    ["T"] = "Freie Bahn!",
  },
  [10437] = {
    ["D"] = "Auf der Manaschmiede Ultris befindet sich der Riss im Nether, durch den Dimensius, der Leerenfürst, gekommen ist. Dieser Riss bildet eine Verbindung zwischen Dimensius und der Leere und macht ihn unverwundbar. Wenn es uns gelingt, diese Energieleitung, mit der Dimensius sich aus dieser Existenzebene zurückziehen kann, zu verschließen, können wir sie und Dimensius vernichten.$B$BDazu müsst Ihr zunächst Fragmente von Dimensius von den Leerkreaturen, die für ihn die Drecksarbeit erledigen, sammeln.",
    ["O"] = "Professor Dabiri im Wachposten des Protektorats im Nethersturm möchte, dass Ihr 8 Fragmente von Dimensius besorgt.",
    ["T"] = "Anleitung zur Zerstörung",
  },
  [10438] = {
    ["D"] = "Hier ist es, $N. Nehmt den Phasenstörer und steigt auf! Der Netherdrachen des Protektorats wird Euch nahe genug an Ultris heranfliegen, damit Ihr den Störer auf die Leerleitung werfen könnt.$B$BSendet so viele Störsignale wie möglich aus, um eine komplette Zerstörung zu gewährleisten!$B$BWenn Ihr bereit seid, sprecht mit dem Netherdrachen und er wird mit Euch in die Lüfte steigen.$B$BWenn Ihr das Ziel verfehlt, müsst Ihr mehr Bomben von mir besorgen, bevor Ihr weitermachen könnt.",
    ["O"] = "Professor Dabiri im Wachposten des Protektorats im Nethersturm möchte, dass Ihr den Phasenstörer bei der Leerleitung auf der Manaschmiede Ultris benutzt. Sendet so viele Störsignale wie möglich aus, um eine komplette Zerstörung zu gewährleisten. Die Entladungen des Phasenstörers werden automatisch das Ziel treffen, solange Ihr nahe genug seid, um ihn zu benutzen.$B$BKehrt zu Professor Dabiri zurück, wenn Ihr erfolgreich wart!",
    ["T"] = "Auf netherischen Schwingen",
  },
  [10439] = {
    ["D"] = "Eure mutigen Taten haben Ameer zum handeln bewegt! Er hat Hauptmann Saeed und die Rächer Aufstellung nehmen lassen!$B$BJetzt müssen wir nur noch Dimensius vernichten.$B$BWenn Ihr bereit seid, lasse ich Saeed rufen. Wenn er nicht gerade einen anderen Auftrag ausführt, wartet er östlich von hier auf Eure Befehle.$B$BLasst ihn wissen, wenn Ihr bereit seid zu kämpfen, damit er seine Truppen in Bewegung setzen kann.$B$BViel Glück, Fleischling! Wir jagen Dimensius schon seit tausend Jahren!",
    ["O"] = "Professor Dabiri im Wachposten des Protektorats im Nethersturm möchte, dass Ihr mit Hauptmann Saeed sprecht, seiner Armee zur Manaschmiede Ultris folgt und Dimensius den alles Verschlingenden tötet!",
    ["T"] = "Dimensius der alles Verschlingende",
  },
  [10440] = {
    ["D"] = "Der Test war ein großer Erfolg! Wir können das Gerät benutzen, um die Kuppeln zu reparieren.$B$BBerichtet in der Sturmsäule von den Testergebnissen und lasst Ghabar wissen, dass ich ein Muster in den Schäden an den Schildgeneratoren entdeckt habe. Wichtige Teile der Generatoreinheiten sind an verschiedenen Orten verschwunden. Das ist kein Zufall, $N, kein technischer Fehler. Wir werden bestohlen.",
    ["O"] = "Return to the Stormspire and speak with Ghabar.",
    ["T"] = "Erfolg!",
  },
  [10441] = {
    ["D"] = "<Audi senkt die Stimme wieder.>$B$BJetzt, da wir die Waren haben, werde ich die meisten davon nach Area 52 schicken. Wir haben hier mehr, als wir für einen Generator brauchen, was uns eine perfekte Geschäftsmöglichkeit eröffnet.$B$BIch habe gesehen, dass die Vertreterin der Expedition des Cenarius verhandeln will. Ich möchte, dass Ihr sie wissen lasst, dass wir ihr das, was sie sucht, zu einem günstigen Preis geben können. Es gibt kein Grund für sie, sich weiter mit diesen betrügerischen Astralen herumzuschlagen.",
    ["O"] = "Speak with Aurine Moonblaze at the bottom of the Stormspire.",
    ["T"] = "Die Waren unters Volk bringen.",
  },
  [10442] = {
    ["D"] = "$C, es gibt hier eine Tauren namens Thiah Rotmähne, die uns ständig um Hilfe anfleht. Sie gehört zur Expedition des Cenarius, die einen kleinen Posten im Westen, auf der südlichen Seite der Straße in Richtung Zangarmarschen, errichtet hat.$B$BWenn Ihr beim Dornnebelhügel angekommen seid, seid Ihr zu weit gegangen.$B$BKönntet Ihr vielleicht dort hingehen und nachschauen, was sie braucht? Ich möchte Waldläuferhauptmann Venn\'ren nicht mit solchen Kleinigkeiten belästigen.",
    ["O"] = "Falknerin Drenna Flusswind möchte, dass Ihr Thiah Rotmähne im Posten des Cenarius auf der Höllenfeuerhalbinsel Eure Dienste anbietet.",
    ["T"] = "Dem Posten des Cenarius helfen",
  },
  [10443] = {
    ["D"] = "Der Zirkel des Cenarius hat eine Expedition in die Scherbenwelt geschickt. So weit ich verstanden habe, sind sie hier, um zu untersuchen, was passiert ist, und wenn möglich die Schäden rückgängig zu machen.$B$BSie haben einen kleinen Posten im Westen, auf der südlichen Seite der Straße in Richtung Zangarmarschen, errichtet.$B$BKönnt Ihr vielleicht dort hingehen und schauen, wie Ihr ihnen helfen könnt? Sprecht mit Thiah Rotmähne. Sie hat uns um Hilfe gebeten.",
    ["O"] = "Amaan der Weise möchte, dass Ihr Thiah Rotmähne im Posten des Cenarius auf der Höllenfeuerhalbinsel Eure Dienste anbietet.",
    ["T"] = "Dem Posten des Cenarius helfen",
  },
  [10444] = {
    ["D"] = "Das erfordert sofortiges Handeln!$B$BWir haben jemanden da draußen, der sich noch näher am Posten der Feuerschwingen befindet. Ich möchte, dass Ihr mit ihm sprecht. Er weiß sicher besser, was wir als Nächstes tun sollten.$B$BMeldet Euch bei Leutnant Meridian bei Allerias Posten und setzt ihn von der Lage in Kenntnis. Ihr findet den Posten im Nordosten am südlichen Ufer des Flusses, direkt südlich vom Posten der Feuerschwingen.$B$BBeeilt Euch, $N!",
    ["O"] = "Jenai Sternwisper möchte, dass Ihr Euch bei Leutnant Meridian in Allerias Posten in den Wäldern von Terokkar meldet.",
    ["T"] = "Meldung bei Allerias Posten",
  },
  [10445] = {
    ["D"] = "Illidan entnahm sieben Phiolen aus dem Brunnen der Ewigkeit. Drei davon leerte er in den See auf dem Berg Hyjal, wodurch ein zweiter Brunnen der Ewigkeit entstand. Jahrelang glaubte man den Rest verloren.$B$BSeit der Öffnung des Dunklen Portals haben wir erfahren, dass er seinen Leutnants Kael\'thas und Vashj je eine Phiole gegeben hat. Holt zurück, was von den Phiolen noch übrig ist. Wir brauchen sie als Fokus, um ein Tor zu den Geschehnissen, seien sie aktuell oder vergangen, am Berg Hyjal zu öffnen. Das Ergebnis der Schlacht um den Berg Hyjal muss erhalten bleiben.",
    ["O"] = "Soridormi in den Höhlen der Zeit möchte, dass Ihr die Überreste von Vashjs Phiole im Echsenkessel und die Überreste von Kaels Phiole von Kael\'thas Sonnenwanderer in der Festung der Stürme besorgt.",
    ["T"] = "Die Phiolen der Ewigkeit",
  },
  [10446] = {
    ["D"] = "Sie bauen eine zweite Manabombe im Hof! Meine Agenten berichten, dass Sharth Voldoun, der Zuständige des Projekts im Posten der Feuerschwingen, den finalen Code der Bombe haben muss.$B$B$N, Ihr müsst dorthin zurückkehren und den letzten Code von ihm besorgen. Er hält sich auf der Spitze der höchsten Säule auf.$B$BBringt den finalen Code zu der Manabombe, an der sie im mittleren Hof arbeiten, und gebt ihn ein.$B$BNur dann können wir sicher sein, dass Allerias Feste gerettet ist.",
    ["O"] = "Leutnant Meridian möchte, dass Ihr den finalen Code eingebt, um die Manabombe auszulösen. Meldet euch dann bei Jenai Sternwisper in Allerias Feste in den Wäldern von Terokkar zurück.",
    ["T"] = "Der finale Code",
  },
  [10447] = {
    ["D"] = "Sie bauen eine zweite Manabombe im Hof! Meine Agenten berichten, dass Sharth Voldoun, der Zuständige des Projekts im Posten der Feuerschwingen, den finalen Code der Bombe haben muss.$B$B$N, Ihr müsst dorthin zurückkehren und den letzten Code von ihm besorgen. Er hält sich auf der Spitze der höchsten Säule auf.$B$BBringt den finalen Code zu der Manabombe, an der sie im mittleren Hof arbeiten, und gebt ihn ein.$B$BNur dann können wir sicher sein, dass die Steinbrecherfeste gerettet ist.",
    ["O"] = "Unteroffizier Chawni möchte, dass Ihr den finalen Code eingebt, um die Manabombe auszulösen. Meldet euch dann bei Tooki in der Steinbrecherfeste in den Wäldern von Terokkar zurück.",
    ["T"] = "Der finale Code",
  },
  [10448] = {
    ["D"] = "Das erfordert sofortiges Handeln!$B$BWir haben jemanden da draußen, der sich noch näher am Posten der Feuerschwingen befindet. Ich möchte, dass Ihr mit ihr sprecht. Sie weiß sicher besser, was wir als Nächstes tun sollten.$B$BMeldet Euch bei Unteroffizier Chawni im Steinbrecherlager und setzt sie von der Lage in Kenntnis.$B$BNehmt die Straße nach Norden aus der Feste heraus. Ab der Mitte von Tuurem müsst Ihr der Straße nach Osten folgen. Ihr findet das Lager am Ufer des Flusses, der direkt südwestlich des Postens der Feuerschwingen liegt.$B$BBeeilt Euch, $N!",
    ["O"] = "Tooki möchte, dass Ihr Euch bei Unteroffizier Chawni im Steinbrecherlager in den Wäldern von Terokkar meldet.",
    ["T"] = "Meldung beim Steinbrecherlager",
  },
  [10449] = {
    ["D"] = "Apethecary Zelana has been studying the beasts of Hellfire Peninsula in the field.  She wants a sample of the blood you gathered; with luck, she might discover what turned those Bonechewer bastards\' skins red!$B$BZelana is at Reaver\'s Fall, southeast of Thrallmar.  Follow the road east and when you see a destroyed, green-glowing hunk of metal, you\'re there.  She has a camp set up behind the wyverns.",
    ["O"] = "Vurtok Axtbrecher in Thrallmar möchte, dass Ihr die Blutprobe eines Knochenmalmers zu Apothekerin Zelana im Häschersturz bringt.",
    ["T"] = "Apothekerin Zelana",
  },
  [10450] = {
    ["D"] = "Knochenmalmer! Sie sollen alle sterben! Der Anblick ihrer roten Haut und ihrer Verkommenheit bringt mein Blut zum kochen!$B$BIch habe einen Auftrag für Euch, $N. Tötet die Orcs der Knochenmalmer und sammelt ihr bösartiges Blut. Es gibt eine Apothekerin, die vielleicht herausfinden kann, wie diese Orcs der Verderbnis der Dämonen anheim fallen konnten, eine Verderbnis, derer wir uns vor so kurzer Zeit erst entledigen konnten.$B$BDie Knochenmalmer streunen in der Nähe der zerstörten Kriegsmaschinerien entlang des Pfads der Ehre im Süden herum. Kehrt zu mir zurück, wenn Euer Auftrag erfüllt ist.",
    ["O"] = "Bringt 12 Phiolen mit Blut eines Knochenmalmers zu Vurtok Axtbrecher in Thrallmar.",
    ["T"] = "Blut der Knochenmalmer",
  },
  [10451] = {
    ["D"] = "Der Erdenmutter sei Dank! Ich habe mich vor ein paar Tagen von den anderen getrennt. Ich wollte der Quelle der Veränderung des Wassers, die wir spüren, auf den Grund gehen. Meine Suche führte mich zur Echsennarbe, wo ich herausgefunden habe, dass die Naga die Wasservorräte des Schattenmondtals verderben!$B$BUnglücklichweise hat man mich entdeckt und gefangen genommen. Nicht auszudenken, welch grausame Dinge sie mit mir vorhatten!$B$BWir müssen hier weg. Helft mir zu fliehen und sucht dann Torlok beim Altar der Verdammnis auf.",
    ["O"] = "Begleitet Erdheilerin Wilda aus der Zisterne der Echsennarbe hinaus und sucht dann Erdheiler Torlok beim Altar der Verdammnis am westlichen Rang der Hand von Guldan auf.",
    ["T"] = "Flucht aus der Zisterne der Echsennarbe",
  },
  [10454] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "FLAG - OFF THE RAILS",
  },
  [10455] = {
    ["D"] = "Wir haben den Lebenden Hain sehr schnell zum Wachsen gebracht, aber nun haben wir die Kontrolle über die Luchse verloren.$B$BEinst war ihre Population gleichmäßig, aber nun kommen die Hainpirscherluchse immer näher an Sylvanaar heran. In letzter Zeit gab es schon einige unerfreuliche Zwischenfälle.$B$BIch nehme an, dass die Arakkoa vom Lashhversteck dahinter stecken, aber zunächst sind die Luchse selbst unser Problem.$B$BIch wäre Euch sehr dankbar, wenn Ihr uns dabei helfen würdet, mit dieser Lage fertigzuwerden.",
    ["O"] = "Rina Moonspring wishes you to slay 12 Grovestalker Lynxes with all due haste and then return to her at Sylvanaar in the Blade\'s Edge Mountains.",
    ["T"] = "Die nahende Wildnis",
  },
  [10456] = {
    ["D"] = "Die abscheulichen Völker der Horde haben eine alte Siedlung im Osten eingenommen. Sie haben ein großes Rudel von Terrorwölfen mitgebracht, die sie gerne als Reittiere und Wachhunde einsetzen.$B$BDiese \"Wachhunde\" haben jedoch begonnen, hinter der Brücke, die uns voneinander trennt, Schaden anzurichten. Sie belästigen unsere Feendrachen, die für die Bestäubung unseres Waldes unersetzlich sind.$B$BNehmt den östlichen Ausgang, um die Brücke über der Blutschlägerklamm, die zum Zackengrat im Osten führt, zu überqueren, und bringt mir ihre Schwänze!",
    ["O"] = "Rina Moonspring has asked you to bring her 4 Thunderlord Dire Wolf Tails.  After doing so, return to her at Sylvanaar in the Blade\'s Edge Mountains.",
    ["T"] = "Marodierende Wölfe",
  },
  [10457] = {
    ["D"] = "Trotz der guten Arbeit, die Ihr geleistet habt, bin ich immer noch der Meinung, dass der Lebende Hain mehr Schutz braucht.$B$BBitte, nehmt diesen kleinen Zweig. Ich habe ihn mit der Macht der Natur verstärkt. Wenn er bei den Setzlingen, die ich im Hain gepflanzt habe, eingesetzt wird, ruft er einen Sturm herbei, der sie schnell zu großen Treantverteidigern heranwachsen lässt.$B$BDiese Treants sind für die Verteidigung des Waldes unverzichtbar, besonders, wenn man nicht für immer in Sylvanaar leben möchte.",
    ["O"] = "Use Rina\'s Bough on 5 of the Grove Seedlings to summon forth defenses for the Living Grove.  Then return to Rina Moonspring at Sylvanaar in the Blade\'s Edge Mountains.",
    ["T"] = "Die Eigenen schützen",
  },
  [10458] = {
    ["D"] = "Wir müssen versuchen, die Dinge zu ändern. Wir müssen mit den Elementen dieser Gegend kommunizieren und schauen, ob wir etwas tun können!$B$BIch gebe Euch das Totem der Geister, $R. Wenn Ihr einen wütenden Elementargeist in der Nähe des Totems umbringt, wird es die Seele des Elementars aufnehmen. Ihr müsst viele Seelen mithilfe des Totems einfangen, bevor Ihr zu mir zurückkehrt.$B$BSammelt zuerst die Seelen der Geister des Feuers und der Geister der Erde. Kehrt zu mir zurück, wenn das Totem voll ist.",
    ["O"] = "Erdheiler Torlok beim Altar der Verdammnis im Schattenmondtal möchte, dass Ihr das Totem der Geister benutzt, um 8 Erdenseelen und 8 Feuerseelen einzufangen.",
    ["T"] = "Wütende Geister des Feuers und der Erde",
  },
  [10459] = {
    ["D"] = "Eure Erfolge sind uns aufgefallen, $N. Was Ihr für uns getan habt geht weit über das, was wir von Verbündeten erwarten, hinaus.$B$BIch habe eine besondere Bitte an Euch, aber zunächst muss ich Eure Hingabe testen.",
    ["O"] = "Ysiel Windsänger bei der Zuflucht des Cenarius möchte, dass Euch einen respektvollen Ruf bei der Expedition des Cenarius verdient.",
    ["T"] = "Respektvoll bei der Expedition des Cenarius",
  },
