  [10467] = {
    ["D"] = "Vous avez prêté serment, $C. Il est maintenant de votre ressort d\'y faire honneur.$B$BServez la Balance des sables avec efficacité, et vous serez $Grécompensé:récompensée;.$B$BÉchouez, et la voie temporelle que nous connaissons sera défaite !",
    ["O"] = "Apportez votre Bague d\'éternité à Soridormi, aux Grottes du temps, après être $Gdevenu Honoré:devenue Honorée; auprès de la Balance des sables.",
    ["T"] = "Le vœu du défenseur",
  },
  [10468] = {
    ["D"] = "Vous avez tenu la promesse que vous nous aviez faite, $N. Et pour cela, vous gagnez la reconnaissance qui vous revient.$B$BContinuez à entrer en harmonie avec le déroulement des événements du passé qui entourent l\'histoire du mont Hyjal, et je pourrai continuer à puiser dans la puissance qu\'abrite votre bague. Elle atteindra bientôt le maximum de son potentiel.",
    ["O"] = "Apportez votre Bague d\'éternité à Soridormi aux Grottes du temps après être $Gdevenu Révéré:devenue Révérée; par la Balance des sables.",
    ["T"] = "La promesse du sage",
  },
  [10469] = {
    ["D"] = "Vous avez tenu la promesse que vous nous aviez faite, $N. Et pour cela, vous gagnez la reconnaissance qui vous revient.$B$BContinuez à entrer en harmonie avec le déroulement des événements du passé qui entourent l\'histoire du mont Hyjal, et je pourrai continuer à puiser dans la puissance qu\'abrite votre bague. Elle atteindra bientôt le maximum de son potentiel.",
