  [11874] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Promo Upper Deck - Monture fusée",
  },
  [11875] = {
    ["D"] = "The troops of the Shattered Sun Offensive will need every advantage they can get to beat back the allies of Kil\'Jaeden. My colleagues in Stormwind have created oils which will strengthen the blades and armor of our troops.$B$BThe key ingredient, nether residue, pervades Outland  -- if you know where to look. Any master herbalist, skinner, or miner who knows the signs can gather it while plying $g his:her; trade. Those herbs, ores, and beasts unique to Outland may all bear the residue.",
    ["O"] = "Apportez 8 Résidus de Néant à l\'Émissaire Mordin, à Shattrath.",
    ["T"] = "Prendre l\'avantage",
  },
  [11877] = {
    ["D"] = "Notre offensive sur Quel\'Danas progresse, mais nous avons découvert que les sbires de Kael\'thas préparaient une contre-attaque. Ils espèrent nous forcer à détourner notre attention du Puits de soleil en ordonnant aux elfes Solfurie du Raz-de-Néant d\'attaquer nos intérêts en Outreterre.$B$BNous ne devons pas les laisser faire, $N. Nous avons besoin de toutes les épées sur le front.$B$BRendez-vous au Raz-de-Néant et emparez-vous de leurs plans d\'attaque. Ils peuvent être sur n\'importe quel elfe de sang Solfurie. Bonne chance, $N.",
    ["O"] = "Le Seigneur Torvos, à Shattrath, veut que vous vous empariez des Plans d\'attaque solfurie.",
    ["T"] = "Les plans d\'attaque solfurie",
  },
  [11880] = {
    ["D"] = "Je suis sûr que cet inventeur gnome est fou à lier. Il n\'arrête pas de déblatérer sur des distorsions noirâtres qui auraient commencé à apparaître dans les Champs des esprits, autour d\'Oshu\'gun en Nagrand.$B$BD\'après lui, ces… heu… ces spectro-lunettes multiphases devraient permettre à leur porteur de voir ces distorsions et d\'en prendre des mesures. Il répète sans arrêt que ces données sont capitales pour notre campagne au Puits de soleil. Je pense qu\'il est juste dingue, mais j\'ai reçu l\'ordre d\'enquêter sur tout ce qui pourrait nous être utile. Ça ne vous dérangerait pas trop de faire quelques relevés ?",
    ["O"] = "Le Messager Haronem, à Shattrath, veut que vous utilisiez les Lunettes spectographiques multiphases pour prendre 6 Relevés multiphases.",
    ["T"] = "L\'examen multiphase",
  },
  [11882] = {
    ["D"] = "Ça vous intéresse d\'apprendre des compétences brûlantes ?$B$BJe ne sais pas si vous y arriverez un jour aussi bien que moi, mais vous parviendrez certainement à en tirer un truc ou deux si vous êtes $Gattentif:attentive;. Parlez à un maître cracheur de feu dans n\'importe quelle capitale de l\'Alliance. Il vous aidera.",
    ["O"] = "Parlez à un Maître cracheur de feu dans n\'importe quelle capitale de l\'Alliance.",
    ["T"] = "Jouer avec le feu",
  },
  [11883] = {
    ["D"] = "Vous voulez apprendre la danse de feu ?$B$BTrès bien. Parlez à un maître danseur de feu dans n\'importe quelle capitale de la Horde. Il vous aidera.",
    ["O"] = "Parlez à un Maître danseur de feu dans n\'importe quelle capitale de la Horde.",
    ["T"] = "La danse de feu",
  },
  [11885] = {
    ["D"] = "Ce livre contient une liste des descendants retenus prisonniers par les prêtres de la serre : Gezzarak la Chasseresse, Karrog le géant de cristal, l\'hérétique connu sous le nom d\'Akkarai le Hurle-sombre, et Vakkiz le Ragevent. Il faudra tous les abattre pour que Terokk puisse revenir en ce monde.$B$BVous aurez besoin de parchemins détenus par les arakkoa perdus dans le temps pour les faire apparaître aux cercles d\'invocation de Skettis. Apportez-moi un attribut de chacun d\'entre eux. Les êtres que vous allez affronter sont puissants : vous feriez bien d\'être $Gaccompagné:accompagnée;.",
    ["O"] = "Trouvez les Piles de crânes au milieu des cercles d\'invocation de Skettis. Invoquez et vainquez chacun des descendants des adversaires de Terokk en utilisant 10 Parchemins perdus dans le temps à la Pile de crâne. Retournez ensuite auprès de Hazzik au Raie\'odrome de Noirvent.",
    ["T"] = "Lignées antagonistes",
  },
  [11886] = {
    ["D"] = "Les distractions, c\'est bien, $C, mais certains d\'entre nous doivent veiller à des affaires plus graves.$B$BAu début de la fête, nous avons reçu des messages de la grève de Zoram, au sujet d\'un brutal accroissement de l\'activité du culte du Crépuscule. Comme cette fête tire son origine des pouvoirs mêmes que vénèrent ces déments, cela ne peut pas être une coïncidence. Rendez-vous au camp, dans les ruines juste au sud de l\'entrée des profondeurs de Brassenoire, et essayez d\'obtenir des informations auprès des sectateurs… De force, si nécessaire. Utilisez cette balise pour appeler un guide quand vous aurez quelque chose.",
    ["O"] = "Rendez-vous au nouveau Camp du Crépuscule, au sud de l\'entrée des Profondeurs de Brassenoire sur la grève de Zoram, dans Orneval, et récupérez la Correspondance du Crépuscule sur les sectateurs. Appelez ensuite le Guide du Cercle terrestre en utilisant la Balise totémique.",
    ["T"] = "Une activité inhabituelle",
  },
  [11891] = {
    ["D"] = "D\'après ceci, il y aurait un autre camp de sectateurs au nord-ouest de leur grand rassemblement sur la grève de Zoram, et ils discutent avec des… invités ?$B$BNous pourrions en apprendre beaucoup en écoutant ces échanges, $R. Prenez cet orbe, il vous permettra de vous faire passer pour l\'un des clampants de la plage. Écoutez la conversation des sectateurs avec leurs « invités » dans ce camp au nord-ouest, et revenez me voir quand vous en saurez assez.",
    ["O"] = "Servez-vous de l\'Orbe du clampant pour espionner le nouveau Camp du Crépuscule, à l\'ouest des Profondeurs de Brassenoire, sur la grève de Zoram, en Orneval. Ensuite, invoquez le Guide du Cercle terrestre à l\'aide de la Balise totémique.",
    ["T"] = "Un déguisement innocent",
  },
  [11915] = {
    ["D"] = "Ça vous intéresse d\'apprendre des compétences brûlantes ?$B$BJe ne sais pas si vous y arriverez un jour aussi bien que moi, mais vous parviendrez certainement à en tirer un truc ou deux si vous êtes $Gattentif:attentive;. Parlez à un maître avaleur de flammes dans n\'importe quelle capitale de la Horde. Il vous aidera.",
    ["O"] = "Parlez à un Maître avaleur de flammes dans n\'importe quelle capitale de la Horde.",
    ["T"] = "Jouer avec le feu",
  },
  [11917] = {
    ["D"] = "Nous devons agir pour préserver l\'équilibre ! Le temps joue contre nous.$B$BRetournez au camp de la grève de Zoram où se trouvent les grandes pierres de glace. Ils s\'en servent pour communiquer avec des élémentaires, comme en Silithus. Je déteste ça, mais les esprits comprendront – invoquez l\'un des lieutenants de cet Ahune et détruisez-le. C\'est une simple action de retardement, mais cela suffira peut-être…",
    ["O"] = "Tuez le Lieutenant Vaguegivre invoqué par la Pierre de glace située sur la grève de Zoram, en Orneval, puis retournez voir un Ancien du Cercle terrestre dans l\'une des capitales.",
    ["T"] = "Contre-attaquer",
  },
  [11921] = {
    ["D"] = "Salut, $Gjongleur:jongleuse; de feu ! J\'espère que vous vous amusez bien et que vous faites des étincelles ? Très bien ! Dites, est-ce que vous vous prenez pour $Gun expert:une experte; en lancer de torches ? Parce que j\'ai une épreuve pour vous…$B$BVous connaissez la règle : lancer des torches sur les braseros marqués. Mais cette fois, vous avez un peu plus de temps… et beaucoup plus de braseros à toucher.$B$B$GPrêt:Prête; ?",
    ["O"] = "Gagnez le jeu de lancer de torches, puis parlez au Maître cracheur de feu.",
    ["T"] = "Lancer plus de torches",
  },
  [11922] = {
    ["D"] = "Alors comme ça, vous voulez apprendre à jongler avec le feu ? Avez-vous ce qu\'il faut ? La dextérité ? Les réflexes ? Le courage ? Si oui, on va vous donner un peu d\'entraînement !$B$BPrenez ces torches et courez jusqu\'au feu de joie. Vous verrez des braseros surmontés de marques clignotantes. Lancez une torche sur un brasero marqué. Faites vite ! Faites-le avant que la marque disparaisse si vous voulez que ce lancer compte !$B$BEssayez de toucher assez de braseros avant d\'arriver au temps limite, puis revenez… pour votre prochaine leçon.",
    ["O"] = "Gagnez le jeu de lancer de torches, puis parlez au Maître avaleur de flammes.",
    ["T"] = "Lancer des torches",
  },
  [11923] = {
    ["D"] = "Maintenant que vous savez lancer une torche, voyons si vous saurez en attraper une !$B$BPrenez ce paquet de torches éteintes. Allez au feu de joie et lancez une torche en l\'air. Rattrapez-la avant qu\'elle ne touche le sol, puis relancez-la ! Si vous en attrapez quatre de suite, revenez me voir.$B$BFaites attention ! Elles sont dangereuses quand elles touchent le sol ! Tâchez de n\'en manquer aucune ! Sinon, vous devrez retourner au feu de joie et recommencer. Bonne chance !",
    ["O"] = "Attrapez 4 torches de suite, puis parlez au Maître avaleur de flammes.",
    ["T"] = "Attraper des torches",
  },
  [11924] = {
    ["D"] = "Attraper des torches est un bon moyen de rester $Gvigilant:vigilante;. Vous vous croyez capable de le faire pendant un moment ?$B$BVoyons ça…",
    ["O"] = "Attrapez 10 torches de suite, puis parlez au Maître cracheur de feu.",
    ["T"] = "Attraper plus de torches",
  },
  [11925] = {
    ["D"] = "Attraper des torches est un bon moyen de rester $Gvigilant:vigilante;. Vous vous croyez capable de le faire pendant un moment ?$B$BVoyons ça…",
    ["O"] = "Attrapez 10 torches de suite, puis parlez au Maître avaleur de flammes.",
    ["T"] = "Attraper plus de torches",
  },
  [11926] = {
    ["D"] = "Hello, fire juggler!  Are you having fun and making fiery mayhem?  That\'s what I like to hear!  Now, do you think you\'re an expert at torch tossing?  Then I have a test for you...$B$BYou know the rules: toss torches at the marked braziers.  This time, however, you have a little more time and a LOT more braizers to hit.$B$BReady?",
    ["O"] = "Gagnez le jeu de lancer de torches, puis parlez au Maître avaleur de flammes.",
    ["T"] = "Lancer plus de torches",
  },
  [11933] = {
    ["D"] = "Cette flamme, volée au cœur de l\'Exodar, vous emplit de chaleur. Cette puissance brute vous est étrangère, mais le conteur en saura peut-être plus…",
    ["O"] = "Rapportez la Flamme de l\'Exodar au Conteur de la fête.",
    ["T"] = "Voler la flamme de l\'Exodar",
  },
  [11935] = {
    ["D"] = "Cette flamme dérobée à Lune-d\'Argent vous remplit de chaleur. Cette puissance brute vous est étrangère, mais le maître des traditions en saura peut-être plus…",
    ["O"] = "Rapportez la Flamme de Lune-d\'Argent au Maître des traditions de la fête.",
    ["T"] = "Voler la flamme de Lune-d\'Argent",
  },
  [11937] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "MARQUEUR - Toutes les quêtes des torches sont terminées",
  },
  [11947] = {
    ["D"] = "Nous devons agir pour préserver l\'équilibre ! Le temps joue contre nous.$B$BIl y a un camp situé dans l\'ombre d\'Ethel Rethor, dans la partie nord-ouest de Désolace. Là-bas, ils utilisent les pierres de glace pour communiquer avec des élémentaires, comme en Silithus. Je déteste ça, mais les esprits comprendront – invoquez l\'un des lieutenants de cet Ahune et détruisez-le. C\'est une simple action de retardement, mais cela suffira peut-être…",
    ["O"] = "Tuez le Lieutenant Grêlon invoqué par la Pierre de glace située à Ethel rethor en Désolace, puis retournez voir un Ancien du Cercle terrestre dans l\'une des capitales.",
    ["T"] = "Contre-attaquer",
  },
  [11948] = {
    ["D"] = "Nous devons agir pour préserver l\'équilibre ! Le temps joue contre nous.$B$BNeptulon dispose depuis longtemps d\'une base sur une île à l\'ouest de Strangleronce. Là-bas, le culte du Crépuscule a installé des pierres de glace pour communiquer avec le royaume du Chasse-marée, de la même façon qu\'en Silithus. Je déteste ça, mais les esprits comprendront... Invoquez l\'un des lieutenants de cet Ahune et détruisez-le. Cette action se contentera de les retarder, mais cela suffira peut-être…",
    ["O"] = "Tuez le Lieutenant Noroît invoqué par la Pierre de glace située sur l\'île de l\'élémentaire d\'eau à l\'ouest de la Vallée de Strangleronce, puis retournez voir un Ancien du Cercle terrestre dans l\'une des capitales.",
    ["T"] = "Contre-attaquer",
  },
  [11952] = {
    ["D"] = "Nous devons agir pour préserver l\'équilibre ! Le temps joue contre nous.$B$BLa crête de Guet-du-feu dans la gorge des Vents brûlants est un bastion établi des activités du culte du Crépuscule. Ces derniers temps, ils utilisent des pierres de glace pour communiquer avec le royaume du Chasse-marée, de la même façon qu\'en Silithus. Je déteste ça, mais les esprits comprendront... Invoquez l\'un des lieutenants de cet Ahune et détruisez-le. Cette action se contentera de les retarder, mais cela suffira peut-être…",
    ["O"] = "Tuez le Lieutenant algide invoqué par la Pierre de glace située dans la caverne sous la Crête de Guet-du-feu, au nord-ouest de la Gorge des Vents brûlants, puis retournez voir un Ancien du Cercle terrestre dans l\'une des capitales.",
    ["T"] = "Contre-attaquer",
  },
  [11953] = {
    ["D"] = "Nous devons agir pour préserver l\'équilibre ! Le temps joue contre nous.$B$BSilithus est la base d\'opération du culte du crépuscule en Azeroth. Dans le nord, ils utilisent des pierres de glace pour communiquer avec le royaume du Chasse-marée. Je déteste ça, mais les esprits comprendront... Invoquez l\'un des lieutenants de cet Ahune et détruisez-le. Cette action se contentera de les retarder, mais cela suffira peut-être…",
    ["O"] = "Tuez le Lieutenant glaciaire invoqué par la Pierre de glace située dans le défilé du Crépuscule, au nord de Silithus, puis retournez voir un Ancien du Cercle terrestre dans l\'une des capitales.",
    ["T"] = "Contre-attaquer",
  },
  [11954] = {
    ["D"] = "Nous devons agir pour préserver l\'équilibre ! Le temps joue contre nous.$B$BPour assurer le succès d\'Ahune, le culte a établi une base à Draenor, tout près de la Porte des ténèbres. Ils utilisent des pierres de glace pour coordonner l\'arrivée du seigneur du Givre. Je déteste ça, mais les esprits comprendront... Invoquez l\'un des templiers protecteurs de cet Ahune et détruisez-le. Cette action se contentera de les retarder, mais cela suffira peut-être…",
    ["O"] = "Tuez le Templier glaciaire invoqué par la Pierre de glace située près de la Porte des ténèbres dans la Péninsule des flammes infernales, puis retournez voir un Ancien du Cercle terrestre dans l\'une des capitales.",
    ["T"] = "Contre-attaquer",
  },
  [11955] = {
    ["D"] = "Je pense que vous êtes de taille à affronter la dernière tâche qui nous attend.$B$BNous ne pouvons pas laisser le culte du Crépuscule et les nagas hérétiques de Neptulon arriver à leurs fins. Nous devons agir, dans l\'intérêt d\'Azeroth et de tout ce qui y vit. Notre voie est toute tracée. Vous devez descendre dans l\'enclos aux Esclaves, dans les profondeurs du réservoir de Glissecroc, et y rencontrer notre chamane. Allez, et faites vite !",
    ["O"] = "Voyagez jusqu\'à l\'Enclos aux esclaves, dans le Réservoir de Glissecroc, au Marécage de Zangar, et parlez à Numa Sœur-des-nuages.",
    ["T"] = "Ahune, le seigneur du Givre",
  },
  [11964] = {
    ["D"] = "Saviez-vous que nos feux de fête brûlent dans toutes les régions où est établie l\'Alliance ? Les gardiens des flammes les entretiennent dans des campements en bordure de chaque ville, pour s\'assurer que les flammes soient hautes et les festivités continues.$B$BChaque gardien des flammes est accompagné d\'un brûletin d\'été : un émissaire des esprits élémentaires du feu.$B$BSouhaiteriez-vous rendre hommage à un brûletin, $N ? Prenez cet encens estival et apportez-le à n\'importe quel brûletin d\'été. Apaisez le petit esprit du feu, peut-être vous récompensera-t-il.",
    ["O"] = "Le maître des traditions de la fête vous demande d\'apporter de l\'Encens estival à un Brûletin d\'été à l\'extérieur de n\'importe quelle ville de l\'Alliance.",
    ["T"] = "De l\'encens pour les brûletins de l\'été",
  },
  [11966] = {
    ["D"] = "Saviez-vous que nos feux de fête brûlent dans toutes les régions où est établie la Horde ? Les gardiens des flammes les entretiennent dans des campements en bordure de chaque ville, pour s\'assurer que les flammes soient hautes et les festivités continues.$B$BChaque gardien des flammes est accompagné d\'un brûletin de la fête : un émissaire des esprits élémentaires du feu.$B$BSouhaiteriez-vous rendre hommage à un brûletin, $N ? Prenez cet encens estival et apportez-le à n\'importe quel brûletin de la fête. Apaisez le petit esprit du feu, peut-être vous récompensera-t-il.",
    ["O"] = "Le conteur de la fête vous demande d\'apporter de l\'Encens estival à un Brûletin de la fête à l\'extérieur de n\'importe quelle ville de la Horde.",
    ["T"] = "De l\'encens pour les brûletins de la fête",
  },
  [11970] = {
    ["D"] = "La fête est une période de joie et de rires, pas de doute. Il est toutefois important de bien comprendre les origines de cette célébration. L\'éducation est une chose qui devrait être recherchée et appréciée.$B$BLes maîtres des traditions de la fête dans les capitales de l\'Alliance pourront vous guider. Allez et apprenez.",
    ["O"] = "Parlez au Maître des traditions de la fête dans n\'importe quelle capitale de l\'Alliance.",
    ["T"] = "Le maître des traditions estivales",
  },
  [11971] = {
    ["D"] = "La fête est une période de joie et de rires, pas de doute. Il est toutefois important de bien comprendre les origines de cette célébration. L\'éducation est une chose qui devrait être recherchée et appréciée.$B$BLes conteurs de la fête dans les capitales de la Horde pourront vous guider. Allez et apprenez.",
    ["O"] = "Parlez au Conteur de la fête dans n\'importe quelle capitale de la Horde.",
    ["T"] = "Le conteur des légendes estivales",
  },
  [11972] = {
    ["D"] = "Ces éclats semblent pulser de façon menaçante. Contiendraient-ils l\'ultime essence du seigneur du Givre ?",
    ["O"] = "Apportez les Éclats de glace à Luma Mère-des-cieux.",
    ["T"] = "Éclats d\'Ahune",
  },
  [11975] = {
    ["D"] = "Je crois que je vous l\'ai déjà demandé, mais quand vous aviez mon âge, est-ce que vous vouliez déjà devenir $Gun:une:c; $C en grandissant ?$B$BMoi, je ne sais pas ce que je ferai… Peut-être mage, ou démoniste, ou chevalier de sang… mais tout ça, je l\'ai déjà dit. Ou peut-être… une rock star ! Comme Sig Nicious, l\'elfe de sang des Elite Tauren Chieftains !$B$BOn m\'a dit que quand ils n\'étaient pas en tournée, ils vivaient à Lune-d\'Argent ! Ils sont souvent sur un balcon, sur la promenade des Anciens, et ils attendent que les gens s\'arrêtent.$B$BS\'il vous plaît, amenez-moi là-bas. S\'il vous plaîîît !",
    ["O"] = "Conduisez votre orpheline, Salandria, à la promenade des Anciens de Lune-d\'Argent pour voir les Elite Tauren Chieftains. Si elle n\'est pas présente lorsque vous serez sur place, n\'oubliez pas de l\'appeler.",
    ["T"] = "Et maintenant, quand je serai grande…",
  },
  [11976] = {
    ["D"] = "Ces éclats semblent pulser de façon menaçante. Contiendraient-ils l\'ultime essence du seigneur du Givre ?",
    ["O"] = "Apportez les Éclats de glace à Luma Mère-des-cieux.",
    ["T"] = "Éclats de glace",
  },
  [12012] = {
    ["D"] = "Cette information doit être portée à la connaissance de tout le Cercle terrestre. Je ne sais pas trop comment procéder, mais l\'ancien qui vous a confié cette tâche en premier lieu aura peut-être une idée. Retournez le voir, et voyez ce que vous pouvez faire. Nous ne devons pas rester les bras croisés !",
    ["O"] = "Travel to a capital city and return the Totemic Beacon to the Earthen Ring Elder.",
    ["T"] = "Informez l\'Ancien",
  },
  [12020] = {
    ["D"] = "Les Sombrefers ont pris une raclée ! Lorsqu\'ils se sont repliés sur le Sinistre écluseur, ils ont perdu un bout de leur transport. Mieux encore, vous avez protégé le camp… pour aujourd\'hui.$B$BAllez voir Boxey et racontez-lui votre triomphe face aux Sombrefers.",
    ["O"] = "Parlez à Boxey Tournécrou au campement de la fête des Brasseurs.",
    ["T"] = "Et une fois, quand j\'étais ivre…",
  },
  [12022] = {
    ["D"] = "Salut ! On dirait que vous savez jouer du coude. Mais est-ce que vous savez jouer de la chope ?$B$BLes Sombrefers sont revenus pour se venger et gâcher notre fête. Leur balancer des chopes à la tête est le meilleur moyen de les arrêter, mais voyons d\'abord si vous êtes capable d\'en lancer une sur la bonne vieille C.E.R.V.O.I.S.E.$B$BC\'est facile, prenez un taste-bière cadeau et buvez un coup. Mettez-vous bien en face de la C.E.R.V.O.I.S.E. Revenez me voir quand vous l\'aurez touchée cinq fois.",
    ["O"] = "Utilisez le Taste-bière cadeau pour toucher la C.E.R.V.O.I.S.E. 5 fois, puis revenez parler à Boxey Tournécrou.",
    ["T"] = "Buvez, éliminez !",
  },
  [12062] = {
    ["D"] = "Coren Navrebière est dans la pièce voisine, il prépare les Sombrefers à leur prochaine attaque contre la fête des Brasseurs. Cassez-lui la figure !$B$BAllez le voir et provoquez-le. Dites-lui que sa bière est tout juste bonne pour les cochons, et encore.$B$BBien entendu, il vous sautera dessus. La dernière personne qui a insulté sa bière… a été jetée aux sangliers.",
    ["O"] = "Parlez à Coren Navrebière.",
    ["T"] = "Insulter Coren Navrebière",
  },
  [12191] = {
    ["D"] = "Those pesky Dark Irons are back! We need to make sure that you can defend yourself if needed. More importantly, we need to know that you can defend our property!$B$BS.T.O.U.T. here is ready for action, are you? Drink from the Complimentary Brewfest Samplers then throw the mug at S.T.O.U.T.! Hit the target 5 times then talk to me.",
    ["O"] = "Utilisez le Taste-bière sans alcool pour toucher la C.E.R.V.O.I.S.E. 5 fois, puis revenez parler à Bizzle Soulevite.",
    ["T"] = "Buvez, éliminez !",
  },
  [12193] = {
    ["D"] = "La fête des Brasseurs, ce n\'est pas que des courses de béliers, c\'est aussi boire de bons coups ! Quand vous aurez un moment, apportez ce bon-cadeau à Belbi Coupecircuit. Elle est presque au fond du campement de la fête des Brasseurs. Si vous n\'arrivez pas à la voir, ne vous inquiétez pas, c\'est elle qui vous verra.$B$BDe toute façon, allez lui remettre ce bon-cadeau pour avoir votre bock de la fête des Brasseurs. Elle sera aussi très heureuse de gérer toutes les autres transactions de la fête des Brasseurs.",
    ["O"] = "Échangez le bon-cadeau à Belbi Coupecircuit dans le campement de la fête des Brasseurs.",
    ["T"] = "Dites, il n\'y aurait pas un souvenir, cette année ?",
  },
  [12194] = {
    ["D"] = "Not only are we supplying brew to everyone, we also have steins to give out! Beautiful, commemorative steins all obtained by the most legal of means! Here\'s a voucher for one.$B$BTalk to Blix near the back of the Brewfest camp. He\'s pretty strange, even more so now that he\'s always wearing those goggles. He\'ll handle the voucher for you, and any other Brewfest transaction.",
    ["O"] = "Échangez le Bon pour un bock de la fête des Brasseurs à Blix Réparouage à l\'arrière du campement de la fête des Brasseurs.",
    ["T"] = "Dites, il n\'y aurait pas un souvenir, cette année ?",
  },
  [12278] = {
    ["D"] = "L\'adhésion a certains avantages. En faisant partie du club de la « Bière du mois », vous serez $Gaverti:avertie; des endroits où trouver les meilleures bières et nos trouvailles les plus récentes.$B$BApportez votre bulletin d\'adhésion à Larkin Tonnebière à la taverne de Brûlepierre à Forgefer.",
    ["O"] = "Apporter le Bulletin d\'adhésion du club de la « Bière du mois » à Larkin Tonnebière à la Taverne de Brûlepierre, à Forgefer.",
    ["T"] = "Le club de la Bière du mois",
  },
  [12306] = {
    ["D"] = "L\'union fait la boisson, $N. Adhérez au club de la « Bière du mois » et vous et vos camarades aurez toujours accès à la meilleure des bières. Apportez votre bulletin d\'adhésion du club de la « Bière du mois » à Ray\'ma à Orgrimmar.",
    ["O"] = "Apporter le Bulletin d\'adhésion du club de la « Bière du mois » à Ray\'ma, à Orgrimmar.",
    ["T"] = "Le club de la Bière du mois",
  },
  [12318] = {
    ["D"] = "Nos espions ont mis au jour un complot diabolique, $N ! Les nains Sombrefer veulent détruire notre fête des Brasseurs bien-aimée !$B$BCoren Navrebière, à la tête d\'une famille de brasseurs renégats, a rassemblé les nains des Profondeurs de Rochenoire. Leurs infâmes taupes mécaniques nous attaquent sans arrêt.$B$BNotre espion se trouve au Sinistre écluseur, une taverne située dans les Profondeurs de Rochenoire. Il en saura plus sur le complot de Navrebière.$B$BTrouvez notre espion, sauvez la fête des Brasseurs !",
    ["O"] = "Parlez avec l\'Espion de la fête des Brasseurs, au Sinistre écluseur dans les Profondeurs de Rochenoire.",
    ["T"] = "Sauvez la fête des Brasseurs !",
  },
  [12491] = {
    ["D"] = "Même dans la mort, Coren Navrebière continue de s\'agripper à un baril. Ça doit être sa fameuse navre-bière !",
    ["O"] = "Donnez la Navre-bière de Navrebière à Ipfelkofer Baril-de-fer au camp de la fête des Brasseurs près de Forgefer.",
    ["T"] = "La navre-bière de Navrebière",
  },
  [12492] = {
    ["D"] = "Même dans la mort, Coren Navrebière continue de s\'agripper à un baril. Ça doit être sa fameuse navre-bière !",
    ["O"] = "Donnez la navre-bière de Navrebière à Perce Fauchebaril au camp de la fête des Brasseurs près d\'Orgrimmar.",
    ["T"] = "La navre-bière de Navrebière",
