  [8463] = {
    ["D"] = "최근에 버림받은 자들이 우리 비전 성소 중 한 곳에서 불안정한 마나 수정을 훔쳐갔습니다. 그 수정들을 잘못 다루면 도시를 둘러싼 벽을 뚫을 만큼 강력한 폭발이 일어날 수도 있습니다.$B$B사실 녀석들이 불안정한 비전 마력의 원천에 손을 댔다가 죽는다고 해도 별로 신경이 쓰이지는 않습니다만, 전체적인 영향을 고려하면 바람직한 일은 아니죠.$B$B그들이 훔쳐 간 불안정한 마나 수정 중 남아 있는 것을 가지고 오십시오. 원래 수정을 보관하던 상자 안에 그대로 있을 겁니다.",
    ["O"] = "매날개 광장에 있는 앨돈 선브랜드에게 불안정한 마나 수정 6개를 가져가야 합니다.",
    ["T"] = "불안정한 마나 수정",
  },
  [8466] = {
    ["O"] = "",
  },
  [8467] = {
    ["O"] = "",
  },
  [8468] = {
    ["D"] = "실버문에서 악명 높은 버림받은 자들의 우두머리인 욕망의 탤리스를 현상 수배하는 바입니다. 그는 실버문의 선량한 시민들을 상대로 연쇄 범죄를 저질러 왔으며, 도시 경비병 두 명을 살해한 죄목이 있습니다.$B$B그를 처치하고 경비대장 칸렌에게 보고하는 이에게는 현상금이 있을 것입니다.",
    ["O"] = "영원노래 숲의 매날개 광장에 있는 경비대장 칸렌에게 탤리스를 처치한 증거를 가져가야 합니다.",
    ["T"] = "현상 수배: 욕망의 탤리스",
  },
  [8469] = {
    ["O"] = "",
  },
  [8472] = {
    ["D"] = "우리 선조가 도시를 지키기 위해 만든 비전 순찰자들이 위험한 상태로 방치되어 있습니다. 그들은 주인인 우리를 알아보지도 못하고 눈에 띄는 즉시 공격하고 있습니다.$B$B순찰자들은 만든 지 수년이 지났지만 이들에게 동력을 공급하는 비전 핵은 아직도 쓸모가 많은 소중한 물건입니다. 비전 핵을 모아서 가져오시면 유용하게 재활용하겠습니다. 북서쪽에 실버문의 폐허 지역으로 가면 비전 순찰자를 발견하실 수 있습니다.",
    ["O"] = "실버문의 폐허를 배회하는 비전 순찰자들에게서 비전 핵 6개를 수집하여, 영원노래 숲의 매날개 광장에 있는 마법학자 자로니스에게 가져가야 합니다.",
    ["T"] = "치명적인 고장",
  },
  [8473] = {
    ["D"] = "요즘은 우리 모두에게 어려운 시기입니다. 스컬지의 진군을 저지하기 위해 유령의 땅 가장자리의 숲을 전부 태워버리기로 한 것은 정말 어려운 결정이었지요.$B$B수년간 우리의 친구였던 숲지기들은 현재 불타버린 숲을 따라 새로 조경을 조성하려 합니다.$B$B$C님, 이런 부탁을 하는 것은 정말 괴로운 일이지만, 우리는 옛 동맹이 추진 중인 일을 중단하라고 설득하는 데 실패했어요. 우리에게 남은 마지막 방법으로 그대가 저들을 막아주십시오. 바로 힘으로 말이죠!",
    ["O"] = "불타버린 숲에서 시들어버린 숲지기 10마리를 처치한 후, 영원노래 숲에 불타버린 숲의 북서쪽 탑 안에 있는 라리아나 리버윈드에게 보고해야 합니다.",
    ["T"] = "괴로운 임무",
  },
  [8474] = {
    ["D"] = "목걸이에는 \'라리\'라는 짧은 이름이 새겨져 있습니다. 이 글자들로 목걸이의 제작자를 찾을 수 있겠습니다.",
    ["O"] = "늙은 흰나무껍질의 목걸이를 만든 이를 찾아야 합니다. 이 물건을 알아보는 이라면 그 안에 담긴 의미를 바로 이해할 수 있을 것입니다.",
    ["T"] = "늙은 흰나무껍질의 목걸이",
  },
  [8475] = {
    ["D"] = "영원노래 숲을 가로지르는 검은 토양을 보고 있노라면, 스컬지를 이끌고 소중한 우리 도시를 침략한 아서스를 절대 잊을 수가 없답니다.$B$B이 땅은 손상되어 보기도 흉할 뿐만 아니라, 근래에는 유령의 땅부터 실버문까지 스컬지들을 끌어들이고 있습니다.$B$B가서 내 부하들을 도와 죽음의 흉터를 떠도는 넋이 나간 언데드들을 처치해 주십시오.",
    ["O"] = "역병해골 약탈자 8마리를 처치하고, 영원노래 숲의 죽음의 흉터 밖에 있는 순찰자 자엘라에게 보고해야 합니다.",
    ["T"] = "죽음의 흉터",
  },
  [8476] = {
    ["D"] = "우리 왕국은 인간이나 오크의 왕국처럼 넓지 않답니다. 아제로스에서 우리에게 허락된 땅은 지금 보고 있는 곳이 거의 전부입니다.$B$B아웃랜드에 다다를 때까지는, 무슨 일이 있어도 지금 우리가 살고 있는 이 작은 땅을 지켜야 합니다.$B$B여기서 남동쪽으로 이웃해 있는 미개한 아마니일족 트롤들은 이 땅의 소유권에 대해 우리와 다른 생각을 품고 있는 것 같습니다. 매일같이 반복되는 놈들의 침략을 더는 참을 수 없습니다. 녀석들의 근거지인 제브와타와 토르와타로 가서 따끔한 맛을 보여주십시오.",
    ["O"] = "영원노래 숲의 원정순찰대 산장에 있는 부관 돈러너가 아마니 광전사와 아마니 도끼투척병을 각각 5마리씩 해치워 달라고 했습니다.",
    ["T"] = "아마니의 침략",
  },
  [8477] = {
    ["D"] = "아마니와 신도레이가 서로 미워하는 데는 긴 사연이 있답니다. 하지만 장인의 솜씨를 평가할 때 이런 점은 문제가 되지 않지요.$B$B우연히 아마니의 무기 몇 점을 볼 기회가 있었는데 그 솜씨에 감탄하지 않을 수 없었습니다. 창제작자 오템베가 사용하는 마법의 망치는 아주 가볍고 균형이 잘 잡혀 있어서 신속하고도 효율적으로 무기를 제작할 수 있다는 소문이 있답니다.$B$B그 망치를 가져오시면 그걸로 그대에게 좋은 무기를 만들어 드리겠습니다.",
    ["O"] = "원정순찰대 산장의 남동쪽에 토르와타로 가서 창제작자 오템베를 찾은 후, 영원노래 숲의 원정순찰대 산장에 있는 아라텔 선포지에게 오템베의 망치를 가져가야 합니다.",
    ["T"] = "창제작자의 망치",
  },
  [8478] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "BETA Choose Your Weapon",
  },
  [8479] = {
    ["D"] = "좀 더 가까이 오게. 벤자쉬는 자네를 해치지 않을 테니 겁낼 필요 없어.$B$B자네 종족과 마찬가지로 우리 검은창 부족과 아마니는 철천지원수 사이라고 할 수 있지. 놈들의 우두머리인 줄마로쉬는 아주 사악한 트롤이라네. 놈은 내게 독약을 먹인 다음 죽을 때까지 이 감옥에 가둬놓으려 했다고.$B$B죽음은 두렵지 않네. 나는 죽음을 목격했고 그녀를 향해 웃어주었지.$B$B이봐, 난 아직 편히 잠들 수 없어. 줄마로쉬가 죽기 전까지는. 제브와타의 호수를 건너면 녀석을 찾을 수 있을 거야. 자네가 추악한 그놈을 처치했다는 증거를 가져올 때까지 나는 이 독을 견디고 있겠네. 그때야 나도 편히 잠들 수 있을 것이야.",
    ["O"] = "토르와타에 포로로 잡혀 있는 트롤 벤자쉬가 족장 줄마로쉬를 처치하고 그 증거를 가져다 달라고 했습니다. 족장 줄마로쉬는 엘렌다르 호수 건너, 제브와타에서 찾을 수 있습니다.",
    ["T"] = "줄마로쉬",
  },
  [8480] = {
    ["D"] = "버림받은 자들이 날이 갈수록 과감해지고 있소. 그들의 조직은 해돋이 부두를 공격할 수 있을 만큼 정비가 되었소.$B$B그 자체로도 어이가 없지만, 놈들로부터 공격을 받은 우리 편은 무기까지 전부 버리고 후퇴하고 말았소. 우리 무기를 되찾고 그 무뢰한들에게 누가 이 땅의 지배자인지 똑똑히 보여주시오.",
    ["O"] = "영원노래 숲에 있는 벨렌드리스 화이트몬에게 신도레이 무기 8개를 가져가야 합니다.",
    ["T"] = "잃어버린 무기 수집",
  },
  [8482] = {
    ["D"] = "이 문서에는 영원노래 숲에 있는 전략적으로 중요한 건물들의 위치가 자세히 기록된 지도가 들어있습니다. 비전 성소의 약도 역시 자세하게 그려져 있습니다. 또한 알아볼 수 없는 언어로 기록된 설명도 잔뜩 있습니다.$B$B다르나서스에서 온 첩자가 이 문서를 지니고 있었으니 앨돈 선브랜드 경비대장에게 보고하는 것이 좋겠습니다.",
    ["O"] = "영원노래 숲의 매날개 광장에 있는 앨돈 선브랜드에게 첩보 문서를 가져가야 합니다.",
    ["T"] = "첩보 문서",
  },
  [8483] = {
    ["D"] = "잘 들으십시오. 비록 첩자의 정체를 밝혀내기는 했지만 공개적으로 처치할 수 없는 미묘한 상황입니다. 놈이 달아날 가능성도 매우 크기 때문에 사로잡지 못할 수도 있습니다.$B$B북부 성소에 있는 조사단 앤빌워드를 찾아서 세상의 쓴맛을 보여 주십시오. 우리 도시에 첩자가 침투했다는 사실이 외부에 알려지는 일은 원치 않으니 조용히 처리해 주시기 바랍니다.$B$B그런 다음 놈이 죽었다는 증거를 가져오시기 바랍니다. 이번 일로 아이언포지에서도 신도레이의 일에 간섭하면 어떻게 되는지 분명히 깨닫게 될 겁니다.",
    ["O"] = "조사단 앤빌워드를 제거한 다음 영원노래 숲의 매날개 광장에 있는 앨돈 선브랜드에게 그 증거를 가져가야 합니다.",
    ["T"] = "드워프 첩자",
  },
  [8486] = {
    ["D"] = "멍청이 케이다니스가 이쪽으로 누군가를 보낼 때가 됐구나 싶었죠.$B$B서부 성소의 부하를 늘리면 위험하다고 경고했건만! 지금 동력 전환기 중 한 대가 파괴되어 서부 성소에서 비전 망령들이 쏟아져 나오고 있답니다!$B$B수리공을 보내기 전에 먼저 그대가 가서 그 망령들을 해치워 주십시오. 서두르시기 바랍니다!",
    ["O"] = "맥지기 벨라니아가 마나 망령과 마나 추적자를 각각 5마리씩 처치해 달라고 했습니다.",
    ["T"] = "불안정한 성소",
  },
  [8487] = {
    ["D"] = "아서스가 스컬지 군대를 몰고 실버문으로 진군하기 전만 해도 이 비전 성소는 온전히 작동했습니다.$B$B하지만 이제는 죽음의 흉터의 다른 지역과 마찬가지로 아무짝에도 쓸모가 없지요. 대지는 오염되어 언데드들이 몰려들고 있으며, 지맥의 정기도 완전히 파손되었습니다.$B$B저는 오염된 땅을 원상태로 복구할 수 있을지 알고 싶습니다. 이 지역의 토양 견본을 모아다 주시면 제 연구에 많은 보탬이 될 것입니다. 시간만 충분하면 원상태로 되돌릴 수 있을 거예요.",
    ["O"] = "영원노래 숲의 동부 성소에 있는 수습생 미르베다가 오염된 토양 견본 8개를 모아 달라고 했습니다.",
    ["T"] = "오염된 땅",
  },
  [8488] = {
    ["D"] = "뭔가 잘못됐어요. 스컬지의 존재가 느껴집니다. 어서 싸울 준비를 하세요!",
    ["O"] = "Protect Apprentice Mirveda from the Scourge ambush.",
    ["T"] = "예상 밖의 결과",
  },
  [8489] = {
    ["D"] = "This object appears to be one of the arcane converters Ley-Keeper Velania mentioned.  It hasn\'t been damaged by the fire, perhaps she could find a use for it.",
    ["O"] = "Bring the Intact Arcane Converter to Ley-Keeper Velania.",
    ["T"] = "온전한 변환기",
  },
  [8490] = {
    ["D"] = "고대 마법석은 한때 영원노래 숲 경계를 둘러싸고 동력을 공급했습니다. 하지만, 수많은 마법석이 아서스의 공격에 파괴되거나 도난당했죠.$B$B온전한 모습을 유지하고 있는 세 개의 마법석 중에 힘을 유지하고 있는 것은 하나뿐입니다. 그런데 어쩌면 동쪽에 있는 마법석은 복구할 수 있을지도 모릅니다.$B$B이 수정을 가져가서 동부 마법석에 놓으십시오. 아마 몇 분 정도 걸릴 것입니다. 조심하십시오. 마법석은 주변의 스컬지를 빨아들일 것입니다. 스컬지가 마법석을 파괴하지 못하도록 하십시오!",
    ["O"] = "동부 마법석에 마력 깃든 수정을 놓은 후 스컬지의 공격으로부터 1분 동안 보호해야 합니다. 영원노래 숲에 있는 마법석 파수꾼 데리안에게 마력 깃든 수정을 가지고 돌아가서 보상을 받으십시오.",
    ["T"] = "방어선에 동력 공급",
  },
  [8491] = {
    ["D"] = "이 지역에 서식하는 갈기발 스라소니는 품질이 매우 우수한 모피로 유명하오. 쓰레기 같은 나이트 엘프 놈들이 갈기발 스라소니를 밀렵하기 위해 이 지역을 서성대는 걸 본 적도 있으니까.$B$B아마니 트롤과 충돌이 자주 발생하다 보니 물자도 좀 부족하고, 우리 순찰자들은 모피를 수집할 시간도 별로 없소. 우리를 도와 갈기발 스라소니 모피를 좀 모아주면 손수 제작한 방어구를 보상으로 드리겠소. 이곳에서 북쪽에 있는 숲으로 가면 갈기발 스라소니가 쉽게 눈에 띌 것이오.",
    ["O"] = "영원노래 숲의 산들바람 마을에 있는 벨란 브라이트오크가 갈기발 스라소니 모피 6개를 가져다 달라고 했습니다.",
    ["T"] = "모피 수집",
  },
  [8510] = {
    ["O"] = "Bring 20 arthas\' tears to Sergeant Major Germaine at the airfield in Dun Morogh.",
