  [5762] = {
    ["O"] = "Take Kravel\'s Crate to Hemet Nesingwary Jr. in Stranglethorn.",
    ["T"] = "Хеминг Эрнестуэй младший",
  },
  [6082] = {
    ["D"] = "Последнее животное, которое тебе придется приручить, – это панцирный скорпид. Тебе придется частенько с ними сражаться. Но ты, будучи $Gохотником:охотницей;, $Gдолжен:должна; уметь повелевать и такими существами.$B$BКогда ты вернешься, я обучу тебя всему, что потребуется, чтобы приручить животное по твоему выбору. Выбранный тобой зверь будет набираться опыта и становиться сильнее. А еще я покажу тебе, как призывать его и отпускать на волю, когда в нем не будет надобности.",
  },
  [6131] = {
    ["D"] = "Фурболги Древобрюхов – единственное фурболгское племя, не затронутое порчей. Однако нынче многие расы без разбору убивают всех фурболгов подряд. И потому фурболгов Древобрюхов осталось так мало.$B$BХочешь заслужить доверие? Изгони из Оскверненного леса пораженных порчей фурболгов Мертвого Леса, и в один прекрасный день мы признаем тебя $Gсоюзником:союзницей;. Ты найдешь первое племя Мертвого Леса – воинов, землепроходцев и садовников – к западу от меня.",
    ["O"] = "Гразл желает, чтобы вы заслужили доверие. Вам нужно убить 5 воинов 5 землепроходцев и 5 садовников из племени Мертвого Леса.",
    ["T"] = "Союзник Древобрюхов",
  },
  [6221] = {
    ["D"] = "Прямо на юго-западе отсюда расположен лагерь фурболгов Мертвого Леса. В лагере царствует порча, и я хочу положить этому конец прежде, чем эти фурболги стали настоящей угрозой.$B$BПлемя Мервтого Леса злобное, но это все действие скверны, поразившей их разум. Они не враждебны от природы, но тут уж ничего не поделать. Мне невыносимо тяжело сражаться против собратьев.$B$BПрошу тебя, $N, делай, что должно, но не рассказывай мне подробностей – у меня и без того сердце разрывается.",
    ["O"] = "Нафиэн просит вас убить 5 дозорного из племени Мертвого Леса, 5 мстителей и 5 шаманов из племени Мертвого Леса.",
    ["T"] = "Северные фурболги Мертвого Леса",
  },
  [6241] = {
    ["D"] = "Клан Древобрюхов готов постоять за себя, $N. В то время как мы позволяем свободно ходить среди нас тем, кому мы доверяем, мы стараемся по возможности избегать общения с другими племенами фурболгов. $B$BПлемя Зимней Спячки в последнее время становится все более враждебным к нам. Они, похоже, впали в состояние ярости, всецело поглощенные собственным страхом и ненавистью ко всему, чего они не понимают. $B$BЕсли бы вы могли сократить их число, это бы нам здорово помогло, $N.",
    ["O"] = "Убейте 6 тотемистов, 6 дозорных и 6 землепроходцев из племени Зимней Спячки по просьбе Сальфы.",
    ["T"] = "Боевые действия в деревне Зимней Спячки",
  },
  [6843] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Штуковина",
  },
  [7487] = {
    ["D"] = "Земля испещрена трещинами и разломами – прямо у нас под ногами, |3-6($R). В двух шагах от того места, где ты сейчас стоишь, есть разлом, ведущий в недра в Черной горы, в самую вотчину Повелителя огня.$B$B$GУдивлен:Удивлена;? Прискорбно... Смертные не в силах понять то, что они не могут увидеть, пощупать, попробовать на зуб...$B$BУверяю тебя, ты стоишь прямо рядом с порталом и вполне можешь им воспользоваться.$B$BСмотрю, у тебя глаза заблестели. Послушай, приобрести сродство с недрами не так сложно. Отправляйся в Глубины Черной горы и принеси мне осколок из Огненных Недр. Я сделаю так, что ты сможешь видеть портал.",
    ["O"] = "Отправляйтесь через портал, ведущий к Огненным Недрам в Глубинах Черной горы и добудьте осколок из Огненных Недр. Принесите его Лотосу Хранителю Портала в Черной горе.",
    ["T"] = "Сродство с недрами",
  },
  [7521] = {
    ["D"] = "Я рассказал тебе все, что знаю, $N. Именно тебе предстоит отыскать элементий и уничтожить воплощение Повелителя огня.$B$BЕсли ты справишься, с тобой пребудет благословение Громораана.",
    ["O"] = "Чтобы освободить Громораана Искателя Ветра из тюрьмы, отнесите правый и левый наручник Ветроносца, 10 слитков элементия и сущность Повелителя Огня верховному лорду Демитриану.",
    ["T"] = "Громораан Искатель Ветра",
  },
  [7522] = {
    ["D"] = "Изучи Сосуд Возрождения, смертный. Из него возродится Искатель Ветра!",
    ["O"] = "Изучите Сосуд Возрождения и вернитесь к верховному лорду Демитриану.",
    ["T"] = "Сосуд Возрождения",
  },
  [7668] = {
    ["D"] = "Рыцарь смерти Темный Терзатель, владычествует над Главным cклепом в Некроситете. Он собирается подчинить себе весь мир духов! Его необходимо остановить! Тут-то тебе и понадобится кристальный предсказатель.$B$BПриведи его в действие в Главном склепе, чтобы вызвать духов-приспешников Темного Терзателя. Когда ты одолеешь этих духов, явится сам рыцарь смерти. Принеси мне его голову, $N! Только его гибель способна исправить урон, нанесенный миру духов!",
    ["O"] = "Используйте кристальный предсказатель в Главном склепе в Некроситете. Сразитесь с духами. Когда появится Темный Терзатель, убейте его.$B$BПринесите голову Темного Терзателя Сагорну Гривастому Страннику в Аллею Мудрости Оргриммара.",
